def test_dict(self):

    from allennlp.common.registrable import Registrable

    class A(Registrable):
        pass

    @A.register("b")
    class B(A):
        def __init__(self, size: int) -> None:
            self.size = size

    class C(Registrable):
        pass

    @C.register("d")
    class D(C):
        def __init__(self, items: Dict[str, A]) -> None:
            self.items = items

    params = Params(
        {
            "type": "d",
            "items": {"first": {"type": "b", "size": 1}, "second": {"type": "b", "size": 2}},
        }
    )
    d = C.from_params(params)

    assert isinstance(d.items, dict)
    assert len(d.items) == 2
    assert all(isinstance(key, str) for key in d.items.keys())
    assert all(isinstance(value, B) for value in d.items.values())
    assert d.items["first"].size == 1
    assert d.items["second"].size == 2

def test_mapping(self):
    from allennlp.common.registrable import Registrable

    class A(Registrable):
        pass

    @A.register("b")
    class B(A):
        def __init__(self, size: int) -> None:
            self.size = size

    class C(Registrable):
        pass

    @C.register("d")
    class D(C):
        def __init__(self, items: Mapping[str, A]) -> None:
            self.items = items

    params = Params(
        {
            "type": "d",
            "items": {"first": {"type": "b", "size": 1}, "second": {"type": "b", "size": 2}},
        }
    )
    d = C.from_params(params)

    assert isinstance(d.items, Mapping)
    assert len(d.items) == 2
    assert all(isinstance(key, str) for key in d.items.keys())
    assert all(isinstance(value, B) for value in d.items.values())
    assert d.items["first"].size == 1
    assert d.items["second"].size == 2

def test_simple_gradient_basic_text(self): inputs = {"sentence": "It was the ending that I hated"} archive = load_archive( self.FIXTURES_ROOT / "basic_classifier" / "serialization" / "model.tar.gz" ) predictor = Predictor.from_archive(archive, "text_classifier")
interpreter = SimpleGradient(predictor) interpretation = interpreter.saliency_interpret_from_json(inputs) assert interpretation is not None assert "instance_1" in interpretation assert "grad_input_1" in interpretation["instance_1"] grad_input_1 = interpretation["instance_1"]["grad_input_1"] assert len(grad_input_1) == 7 # 7 words in input
# two interpretations should be identical for gradient repeat_interpretation = interpreter.saliency_interpret_from_json(inputs) repeat_grad_input_1 = repeat_interpretation["instance_1"]["grad_input_1"] for grad, repeat_grad in zip(grad_input_1, repeat_grad_input_1): assert grad == approx(repeat_grad)

