def test_decode(self):
    config, inputs_dict = self.model_tester.prepare_config_and_inputs_for_common()

    for model_class in self.all_model_classes:
        with self.subTest(model_class.__name__):
            model = model_class(config)
            encoder_outputs = model.encode(inputs_dict["input_ids"], inputs_dict["attention_mask"])

            prepared_inputs_dict = {
                "decoder_input_ids": inputs_dict["decoder_input_ids"],
                "decoder_attention_mask": inputs_dict["decoder_attention_mask"],
                "encoder_outputs": encoder_outputs,
            }

            @jax.jit
            def decode_jitted(decoder_input_ids, decoder_attention_mask, encoder_outputs):
                return model.decode(
                    decoder_input_ids=decoder_input_ids,
                    decoder_attention_mask=decoder_attention_mask,
                    encoder_outputs=encoder_outputs,
                )

            with self.subTest("JIT Enabled"):
                jitted_outputs = decode_jitted(**prepared_inputs_dict).to_tuple()

            with self.subTest("JIT Disabled"):
                with jax.disable_jit():
                    outputs = decode_jitted(**prepared_inputs_dict).to_tuple()

            self.assertEqual(len(outputs), len(jitted_outputs))
            for jitted_output, output in zip(jitted_outputs, outputs):
                self.assertEqual(jitted_output.shape, output.shape)

def test_decode(self):
    config, inputs_dict = self.model_tester.prepare_config_and_inputs_for_common()

    for model_class in self.all_model_classes:
        with self.subTest(model_class.__name__):
            model = model_class(config)
            encoder_outputs = model.encode(inputs_dict["input_ids"], inputs_dict["attention_mask"])

            prepared_inputs_dict = {
                "decoder_input_ids": inputs_dict["decoder_input_ids"],
                "decoder_attention_mask": inputs_dict["decoder_attention_mask"],
                "encoder_outputs": encoder_outputs,
            }

            @jax.jit
            def decode_jitted(decoder_input_ids, decoder_attention_mask, encoder_outputs):
                return model.decode(
                    decoder_input_ids=decoder_input_ids,
                    decoder_attention_mask=decoder_attention_mask,
                    encoder_outputs=encoder_outputs,
                )

            with self.subTest("JIT Enabled"):
                jitted_outputs = decode_jitted(**prepared_inputs_dict).to_tuple()

            with self.subTest("JIT Disabled"):
                with jax.disable_jit():
                    outputs = decode_jitted(**prepared_inputs_dict).to_tuple()

            self.assertEqual(len(outputs), len(jitted_outputs))
            for jitted_output, output in zip(jitted_outputs, outputs):
                self.assertEqual(jitted_output.shape, output.shape)

def prepare_config_and_inputs(self): input_ids = ids_tensor([self.batch_size, self.decoder_seq_length], self.vocab_size)
attention_mask = None if self.use_attention_mask: attention_mask = ids_tensor([self.batch_size, self.decoder_seq_length], vocab_size=2)
lm_labels = None if self.use_labels: lm_labels = ids_tensor([self.batch_size, self.decoder_seq_length], self.vocab_size)
config = PegasusConfig( vocab_size=self.vocab_size, d_model=self.d_model, decoder_layers=self.decoder_layers, decoder_ffn_dim=self.decoder_ffn_dim, encoder_attention_heads=self.encoder_attention_heads, decoder_attention_heads=self.decoder_attention_heads, eos_token_id=self.eos_token_id, bos_token_id=self.bos_token_id, use_cache=self.use_cache, pad_token_id=self.pad_token_id, decoder_start_token_id=self.decoder_start_token_id, max_position_embeddings=self.max_position_embeddings, is_encoder_decoder=self.is_encoder_decoder, )
return ( config, input_ids, attention_mask, lm_labels, )

def prepare_config_and_inputs(self): input_ids = ids_tensor([self.batch_size, self.decoder_seq_length], self.vocab_size)
attention_mask = None if self.use_attention_mask: attention_mask = ids_tensor([self.batch_size, self.decoder_seq_length], vocab_size=2)
lm_labels = None if self.use_labels: lm_labels = ids_tensor([self.batch_size, self.decoder_seq_length], self.vocab_size)
config = BlenderbotSmallConfig( vocab_size=self.vocab_size, d_model=self.d_model, decoder_layers=self.decoder_layers, decoder_ffn_dim=self.decoder_ffn_dim, encoder_attention_heads=self.encoder_attention_heads, decoder_attention_heads=self.decoder_attention_heads, eos_token_id=self.eos_token_id, bos_token_id=self.bos_token_id, use_cache=self.use_cache, pad_token_id=self.pad_token_id, decoder_start_token_id=self.decoder_start_token_id, max_position_embeddings=self.max_position_embeddings, is_encoder_decoder=self.is_encoder_decoder, )
return ( config, input_ids, attention_mask, lm_labels, )

def prepare_config_and_inputs_for_common(self):
    config_and_inputs = self.prepare_config_and_inputs()
    (
        config,
        input_ids,
        attention_mask,
        lm_labels,
    ) = config_and_inputs

    inputs_dict = {
        "input_ids": input_ids,
        "attention_mask": attention_mask,
    }
    return config, inputs_dict

