def test_tokenizer(self): feature_extractor = self.get_feature_extractor() tokenizer = self.get_tokenizer()
processor = Speech2TextProcessor(tokenizer=tokenizer, feature_extractor=feature_extractor)
input_str = "This is a test string"
with processor.as_target_processor(): encoded_processor = processor(input_str)
encoded_tok = tokenizer(input_str)
for key in encoded_tok.keys(): self.assertListEqual(encoded_tok[key], encoded_processor[key])

def test_tokenizer(self): feature_extractor = self.get_feature_extractor() tokenizer = self.get_tokenizer()
processor = Wav2Vec2Processor(tokenizer=tokenizer, feature_extractor=feature_extractor)
input_str = "This is a test string"
with processor.as_target_processor(): encoded_processor = processor(input_str)
encoded_tok = tokenizer(input_str)
for key in encoded_tok.keys(): self.assertListEqual(encoded_tok[key], encoded_processor[key])

