def create_and_check_mpnet_for_multiple_choice(
    self, config, input_ids, input_mask, sequence_labels, token_labels, choice_labels
):
    config.num_choices = self.num_choices
    model = TFMPNetForMultipleChoice(config)
    multiple_choice_inputs_ids = tf.tile(tf.expand_dims(input_ids, 1), (1, self.num_choices, 1))
    multiple_choice_input_mask = tf.tile(tf.expand_dims(input_mask, 1), (1, self.num_choices, 1))
    inputs = {
        "input_ids": multiple_choice_inputs_ids,
        "attention_mask": multiple_choice_input_mask,
    }
    result = model(inputs)
    self.parent.assertEqual(result.logits.shape, (self.batch_size, self.num_choices))

def create_and_check_distilbert_for_multiple_choice(
    self, config, input_ids, input_mask, sequence_labels, token_labels, choice_labels
):
    config.num_choices = self.num_choices
    model = TFDistilBertForMultipleChoice(config)
    multiple_choice_inputs_ids = tf.tile(tf.expand_dims(input_ids, 1), (1, self.num_choices, 1))
    multiple_choice_input_mask = tf.tile(tf.expand_dims(input_mask, 1), (1, self.num_choices, 1))
    inputs = {
        "input_ids": multiple_choice_inputs_ids,
        "attention_mask": multiple_choice_input_mask,
    }
    result = model(inputs)
    self.parent.assertEqual(result.logits.shape, (self.batch_size, self.num_choices))

def config_and_inputs(self): question_encoder_tester = TFDPRModelTester(self) dpr_config_and_inputs = question_encoder_tester.prepare_config_and_inputs() generator_tester = TFBartModelTester(self) bart_config_and_inputs = generator_tester.prepare_config_and_inputs_for_common()
(question_encoder_config, input_ids, _, input_mask, _, _, _) = dpr_config_and_inputs (generator_config, bart_inputs_dict) = bart_config_and_inputs decoder_input_ids, decoder_attention_mask = bart_inputs_dict["input_ids"], bart_inputs_dict["attention_mask"]
config = RagConfig.from_question_encoder_generator_configs( question_encoder_config, generator_config, n_docs=self.n_docs, retrieval_vector_size=self.retrieval_vector_size, max_combined_length=self.max_combined_length, )
return { "config": config, "input_ids": input_ids, "attention_mask": input_mask, "decoder_input_ids": decoder_input_ids, "decoder_attention_mask": decoder_attention_mask, }

def config_and_inputs(self): question_encoder_tester = DPRModelTester(self) dpr_config_and_inputs = question_encoder_tester.prepare_config_and_inputs() generator_tester = BartModelTester(self) bart_config_and_inputs = generator_tester.prepare_config_and_inputs_for_common()
(question_encoder_config, input_ids, _, input_mask, _, _, _) = dpr_config_and_inputs (generator_config, bart_inputs_dict) = bart_config_and_inputs decoder_input_ids, decoder_attention_mask = bart_inputs_dict["input_ids"], bart_inputs_dict["attention_mask"]
config = RagConfig.from_question_encoder_generator_configs( question_encoder_config, generator_config, n_docs=self.n_docs, retrieval_vector_size=self.retrieval_vector_size, max_combined_length=self.max_combined_length, )
return { "config": config, "input_ids": input_ids, "attention_mask": input_mask, "decoder_input_ids": decoder_input_ids, "decoder_attention_mask": decoder_attention_mask, }

def test_model_from_pretrained(self):
    for model_name in DPR_CONTEXT_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = DPRContextEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in DPR_CONTEXT_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = DPRContextEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in DPR_QUESTION_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = DPRQuestionEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in DPR_READER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = DPRReader.from_pretrained(model_name)
        self.assertIsNotNone(model)

def test_model_from_pretrained(self):
    for model_name in TF_DPR_CONTEXT_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = TFDPRContextEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in TF_DPR_CONTEXT_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = TFDPRContextEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in TF_DPR_QUESTION_ENCODER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = TFDPRQuestionEncoder.from_pretrained(model_name)
        self.assertIsNotNone(model)

    for model_name in TF_DPR_READER_PRETRAINED_MODEL_ARCHIVE_LIST[:1]:
        model = TFDPRReader.from_pretrained(model_name)
        self.assertIsNotNone(model)

def test_run_qa(self): stream_handler = logging.StreamHandler(sys.stdout) logger.addHandler(stream_handler)
tmp_dir = self.get_auto_remove_tmp_dir() testargs = f""" run_qa.py --model_name_or_path bert-base-uncased --version_2_with_negative --train_file tests/fixtures/tests_samples/SQUAD/sample.json --validation_file tests/fixtures/tests_samples/SQUAD/sample.json --output_dir {tmp_dir} --overwrite_output_dir --num_train_epochs=3 --warmup_steps=2 --do_train --do_eval --logging_steps 2 --eval_steps 2 --learning_rate=2e-4 --per_device_train_batch_size=2 --per_device_eval_batch_size=1 """.split()
with patch.object(sys, "argv", testargs): run_qa.main() result = get_results(tmp_dir) self.assertGreaterEqual(result["eval_f1"], 30) self.assertGreaterEqual(result["eval_exact"], 30)

