def get_permanent_hostname(self):
    if not os.path.isfile(self.FILE):
        return ''

    try:
        for line in get_file_lines(self.FILE):
            line = line.strip()
            if line.startswith('hostname='):
                return line[10:].strip('"')
    except Exception as e:
        self.module.fail_json(
            msg="failed to read hostname: %s" % to_native(e),
            exception=traceback.format_exc())

def get_permanent_hostname(self):
    if not os.path.isfile(self.FILE):
        return ''

    try:
        for line in get_file_lines(self.FILE):
            line = line.strip()
            if line.startswith('hostname='):
                return line[10:].strip('"')
    except Exception as e:
        self.module.fail_json(
            msg="failed to read hostname: %s" % to_native(e),
            exception=traceback.format_exc())

def check_owner_exists(module, owner):
    try:
        uid = int(owner)
        try:
            getpwuid(uid).pw_name
        except KeyError:
            module.warn('failed to look up user with uid %s. Create user up to this point in real play' % uid)
    except ValueError:
        try:
            getpwnam(owner).pw_uid
        except KeyError:
            module.warn('failed to look up user %s. Create user up to this point in real play' % owner)

def check_group_exists(module, group):
    try:
        gid = int(group)
        try:
            getgrgid(gid).gr_name
        except KeyError:
            module.warn('failed to look up group with gid %s. Create group up to this point in real play' % gid)
    except ValueError:
        try:
            getgrnam(group).gr_gid
        except KeyError:
            module.warn('failed to look up group %s. Create group up to this point in real play' % group)

def write_changes(module, contents, path):
tmpfd, tmpfile = tempfile.mkstemp(dir=module.tmpdir) f = os.fdopen(tmpfd, 'wb') f.write(contents) f.close()
validate = module.params.get('validate', None) valid = not validate if validate: if "%s" not in validate: module.fail_json(msg="validate must contain %%s: %s" % (validate)) (rc, out, err) = module.run_command(validate % tmpfile) valid = rc == 0 if rc != 0: module.fail_json(msg='failed to validate: ' 'rc:%s error:%s' % (rc, err)) if valid: module.atomic_move(tmpfile, path, unsafe_writes=module.params['unsafe_writes'])

def write_changes(module, contents, path):
tmpfd, tmpfile = tempfile.mkstemp(dir=module.tmpdir) f = os.fdopen(tmpfd, 'wb') f.write(contents) f.close()
