env_vars = dict( VULTR_API_KEY=parser.get('default', 'key'), )
display.sensitive.add(env_vars['VULTR_API_KEY'])
ansible_vars = dict( vultr_resource_prefix=self.resource_prefix, )
return CloudEnvironmentConfig( env_vars=env_vars, ansible_vars=ansible_vars, )

def get_environment_config(self): # type: () -> CloudEnvironmentConfig """Return environment configuration for use in the test environment after delegation.""" parser = configparser.ConfigParser() parser.read(self.config_path)
env_vars = dict( CLOUDSCALE_API_TOKEN=parser.get('default', 'cloudscale_api_token'), )
display.sensitive.add(env_vars['CLOUDSCALE_API_TOKEN'])
ansible_vars = dict( cloudscale_resource_prefix=self.resource_prefix, )
ansible_vars.update(dict((key.lower(), value) for key, value in env_vars.items()))
return CloudEnvironmentConfig( env_vars=env_vars, ansible_vars=ansible_vars, )

