def PlottingSingleFrame( clip, Dataframe, bodyparts2plot, tmpfolder, index, dotsize, pcutoff, alphavalue, colors, strwidth=4, savelabeled=True, ): """ Label frame and save under imagename / this is already cropped (for clip) """ from skimage import io
imagename1 = os.path.join(tmpfolder, "img" + str(index).zfill(strwidth) + ".png") imagename2 = os.path.join( tmpfolder, "img" + str(index).zfill(strwidth) + "labeled.png" )
if not os.path.isfile( os.path.join(tmpfolder, "img" + str(index).zfill(strwidth) + ".png") ): plt.axis("off") image = img_as_ubyte(clip.get_frame(index * 1.0 / clip.fps)) io.imsave(imagename1, image)
if savelabeled: if np.ndim(image) > 2: h, w, nc = np.shape(image) else: h, w = np.shape(image)
bpts = Dataframe.columns.get_level_values("bodyparts") all_bpts = bpts.values[::3] df_x, df_y, df_likelihood = Dataframe.values.reshape( (Dataframe.shape[0], -1, 3) ).T bplist = bpts.unique().to_list() if Dataframe.columns.nlevels == 3: map2bp = list(range(len(all_bpts))) else: map2bp = [bplist.index(bp) for bp in all_bpts] keep = np.flatnonzero(np.isin(all_bpts, bodyparts2plot))
plt.figure(frameon=False, figsize=(w * 1.0 / 100, h * 1.0 / 100)) plt.subplots_adjust(left=0, bottom=0, right=1, top=1, wspace=0, hspace=0) plt.imshow(image) for i, ind in enumerate(keep): if df_likelihood[ind, index] > pcutoff: plt.scatter( df_x[ind, index], df_y[ind, index], s=dotsize ** 2, color=colors(map2bp[i]), alpha=alphavalue, ) plt.xlim(0, w) plt.ylim(0, h) plt.axis("off") plt.subplots_adjust(left=0, bottom=0, right=1, top=1, wspace=0, hspace=0) plt.gca().invert_yaxis() plt.savefig(imagename2) plt.close("all")

def GetPoseF(cfg, dlc_cfg, sess, inputs, outputs, cap, nframes, batchsize):
    """ Batchwise prediction of pose """
    PredictedData = np.zeros(
        (nframes, dlc_cfg["num_outputs"] * 3 * len(dlc_cfg["all_joints_names"]))
    )
    batch_ind = 0  # keeps track of which image within a batch should be written to
    batch_num = 0  # keeps track of which batch you are at
    ny, nx = int(cap.get(4)), int(cap.get(3))
    if cfg["cropping"]:
        ny, nx = checkcropping(cfg, cap)

    frames = np.empty(
        (batchsize, ny, nx, 3), dtype="ubyte"
    )  # this keeps all frames in a batch
    pbar = tqdm(total=nframes)
    counter = 0
    step = max(10, int(nframes / 100))
    inds = []
    while cap.isOpened():
        if counter % step == 0:
            pbar.update(step)
        ret, frame = cap.read()
        if ret:
            frame = cv2.cvtColor(frame, cv2.COLOR_BGR2RGB)
            if cfg["cropping"]:
                frames[batch_ind] = img_as_ubyte(
                    frame[cfg["y1"] : cfg["y2"], cfg["x1"] : cfg["x2"]]
                )
            else:
                frames[batch_ind] = img_as_ubyte(frame)
            inds.append(counter)
            if batch_ind == batchsize - 1:
                pose = predict.getposeNP(frames, dlc_cfg, sess, inputs, outputs)
                PredictedData[inds] = pose
                batch_ind = 0
                inds.clear()
                batch_num += 1
            else:
                batch_ind += 1
        elif counter >= nframes:
            if batch_ind > 0:
                pose = predict.getposeNP(
                    frames, dlc_cfg, sess, inputs, outputs
                )  # process the whole batch (some frames might be from previous batch!)
                PredictedData[inds[:batch_ind]] = pose[:batch_ind]
            break
        counter += 1

    pbar.close()
    return PredictedData, nframes

def GetPoseS(cfg, dlc_cfg, sess, inputs, outputs, cap, nframes):
    """ Non batch wise pose estimation for video cap."""
    if cfg["cropping"]:
        ny, nx = checkcropping(cfg, cap)

    PredictedData = np.zeros(
        (nframes, dlc_cfg["num_outputs"] * 3 * len(dlc_cfg["all_joints_names"]))
    )
    pbar = tqdm(total=nframes)
    counter = 0
    step = max(10, int(nframes / 100))
    while cap.isOpened():
        if counter % step == 0:
            pbar.update(step)

        ret, frame = cap.read()
        if ret:
            frame = cv2.cvtColor(frame, cv2.COLOR_BGR2RGB)
            if cfg["cropping"]:
                frame = img_as_ubyte(
                    frame[cfg["y1"] : cfg["y2"], cfg["x1"] : cfg["x2"]]
                )
            else:
                frame = img_as_ubyte(frame)
            pose = predict.getpose(frame, dlc_cfg, sess, inputs, outputs)
            PredictedData[
                counter, :
            ] = (
                pose.flatten()
            )  # NOTE: thereby cfg['all_joints_names'] should be same order as bodyparts!
        elif counter >= nframes:
            break
        counter += 1

    pbar.close()
    return PredictedData, nframes

def GetPoseS_GTF(cfg, dlc_cfg, sess, inputs, outputs, cap, nframes):
    """ Non batch wise pose estimation for video cap."""
    if cfg["cropping"]:
        ny, nx = checkcropping(cfg, cap)

    pose_tensor = predict.extract_GPUprediction(
        outputs, dlc_cfg
    )  # extract_output_tensor(outputs, dlc_cfg)
    PredictedData = np.zeros((nframes, 3 * len(dlc_cfg["all_joints_names"])))
    pbar = tqdm(total=nframes)
    counter = 0
    step = max(10, int(nframes / 100))
    while cap.isOpened():
        if counter % step == 0:
            pbar.update(step)

        ret, frame = cap.read()
        if ret:
            frame = cv2.cvtColor(frame, cv2.COLOR_BGR2RGB)
            if cfg["cropping"]:
                frame = img_as_ubyte(
                    frame[cfg["y1"] : cfg["y2"], cfg["x1"] : cfg["x2"]]
                )
            else:
                frame = img_as_ubyte(frame)

            pose = sess.run(
                pose_tensor,
                feed_dict={inputs: np.expand_dims(frame, axis=0).astype(float)},
            )
            pose[:, [0, 1, 2]] = pose[:, [1, 0, 2]]
            # pose = predict.getpose(frame, dlc_cfg, sess, inputs, outputs)
            PredictedData[
                counter, :
            ] = (
                pose.flatten()
            )  # NOTE: thereby cfg['all_joints_names'] should be same order as bodyparts!
        elif counter >= nframes:
            break
        counter += 1

    pbar.close()
    return PredictedData, nframes

def call(self, inputs, use_batch_norm=False, drop_out=False, drop_connect_rate=None): """Implementation of call(). Args: inputs: the inputs tensor. training: boolean, whether the model is constructed for training. drop_connect_rate: float, between 0 to 1, drop connect rate. Returns: A output tensor. """ tf.compat.v1.logging.info('Block input: %s shape: %s' % (inputs.name, inputs.shape)) if self._block_args.expand_ratio != 1: x = self._relu_fn(self._bn0(self._expand_conv(inputs), training=use_batch_norm)) else: x = inputs tf.compat.v1.logging.info('Expand: %s shape: %s' % (x.name, x.shape))
x = self._relu_fn(self._bn1(self._depthwise_conv(x), training=use_batch_norm)) tf.compat.v1.logging.info('DWConv: %s shape: %s' % (x.name, x.shape))
if self._has_se: with tf.compat.v1.variable_scope('se'): x = self._call_se(x)
self.endpoints = {'expansion_output': x}
x = self._bn2(self._project_conv(x), training=use_batch_norm) if self._block_args.id_skip: if all( s == 1 for s in self._block_args.strides ) and self._block_args.input_filters == self._block_args.output_filters: # only apply drop_connect if skip presents. if drop_connect_rate: x = utils.drop_connect(x, drop_out, drop_connect_rate) x = tf.add(x, inputs) tf.compat.v1.logging.info('Project: %s shape: %s' % (x.name, x.shape)) return x

def call(self, inputs, use_batch_norm=False, drop_out=False, drop_connect_rate=None): """Implementation of call(). Args: inputs: the inputs tensor. training: boolean, whether the model is constructed for training. drop_connect_rate: float, between 0 to 1, drop connect rate. Returns: A output tensor. """ tf.compat.v1.logging.info('Block input: %s shape: %s' % (inputs.name, inputs.shape)) if self._block_args.expand_ratio != 1: x = self._relu_fn(self._bn0(self._expand_conv(inputs), training=use_batch_norm)) else: x = inputs tf.compat.v1.logging.info('Expand: %s shape: %s' % (x.name, x.shape))
self.endpoints = {'expansion_output': x}
x = self._bn1(self._project_conv(x), training=use_batch_norm) if self._block_args.id_skip: if all( s == 1 for s in self._block_args.strides ) and self._block_args.input_filters == self._block_args.output_filters: # only apply drop_connect if skip presents. if drop_connect_rate: x = utils.drop_connect(x, drop_out, drop_connect_rate) x = tf.add(x, inputs) tf.compat.v1.logging.info('Project: %s shape: %s' % (x.name, x.shape)) return x

def edit_inf_config(self, event):
    # Read the infer config file
    cfg = auxiliaryfunctions.read_config(self.config)
    trainingsetindex = self.trainingset.GetValue()
    trainFraction = cfg["TrainingFraction"][trainingsetindex]
    self.inf_cfg_path = os.path.join(
        cfg["project_path"],
        auxiliaryfunctions.GetModelFolder(
            trainFraction, self.shuffles.GetValue(), cfg
        ),
        "test",
        "inference_cfg.yaml",
    )
    # let the user open the file with default text editor. Also make it mac compatible
    if sys.platform == "darwin":
        self.file_open_bool = subprocess.call(["open", self.inf_cfg_path])
        self.file_open_bool = True
    else:
        self.file_open_bool = webbrowser.open(self.inf_cfg_path)
    if self.file_open_bool:
        self.inf_cfg = auxiliaryfunctions.read_config(self.inf_cfg_path)
    else:
        raise FileNotFoundError("File not found!")

