def addCheckBoxSlider(self, bodyparts, fileIndex, markersize): """ Adds checkbox and a slider """ self.choiceBox = wx.BoxSizer(wx.VERTICAL)
self.slider = wx.Slider( self, -1, markersize, 1, markersize * 3, size=(250, -1), style=wx.SL_HORIZONTAL | wx.SL_AUTOTICKS | wx.SL_LABELS, ) self.slider.Enable(False) self.checkBox = wx.CheckBox(self, id=wx.ID_ANY, label="Adjust marker size.") self.choiceBox.Add(self.slider, 0, wx.ALL, 5) self.choiceBox.Add(self.checkBox, 0, wx.ALL, 5) names = ["Color individuals", "Color bodyparts"] self.visualization_radiobox = wx.RadioBox( self, label="Select the visualization scheme", majorDimension=1, style=wx.RA_SPECIFY_COLS, choices=names, ) self.choiceBox.Add(self.visualization_radiobox, 0, wx.EXPAND | wx.ALL, 10)
self.SetSizerAndFit(self.choiceBox) self.Layout() return (self.choiceBox, self.slider, self.checkBox, self.visualization_radiobox)

