def basic_static_model(): ni = Input((None, 24, 24, 3)) nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv1")(ni) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool1')(nn)
nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv2")(nn) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool2')(nn)
nn = Flatten(name='flatten')(nn) nn = Dense(100, act=None, name="dense1")(nn) nn = Dense(10, act=None, name="dense2")(nn) M = Model(inputs=ni, outputs=nn, name='basic_static') return M

def basic_static_model(): ni = Input((None, 24, 24, 3)) nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv1")(ni) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool1')(nn)
nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv2")(nn) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool2')(nn)
nn = Flatten(name='flatten')(nn) nn = Dense(100, act=None, name="dense1")(nn) M = Model(inputs=ni, outputs=nn) return M

def basic_static_model(include_top=True): ni = Input((None, 24, 24, 3)) nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv1")(ni) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool1')(nn)
nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv2")(nn) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool2')(nn)
nn = Flatten(name='flatten')(nn) nn = Dense(100, act=None, name="dense1")(nn) if include_top is True: nn = Dense(10, act=None, name="dense2")(nn) M = Model(inputs=ni, outputs=nn) return M

def basic_static_model(): ni = Input((None, 24, 24, 3)) nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv1")(ni) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool1')(nn)
nn = Conv2d(16, (5, 5), (1, 1), padding='SAME', act=tf.nn.relu, name="conv2")(nn) nn = MaxPool2d((3, 3), (2, 2), padding='SAME', name='pool2')(nn)
nn = Flatten(name='flatten')(nn) nn = Dense(100, act=None, name="dense1")(nn) nn = Dense(10, act=None, name="dense2")(nn) M = Model(inputs=ni, outputs=nn) return M

def test_npz_dict(self): modify_val = np.zeros_like(self.dynamic_model.all_weights[-2].numpy()) ori_val = self.dynamic_model.all_weights[-2].numpy() tl.files.save_npz_dict(self.dynamic_model.all_weights, "./model_basic.npz")
self.dynamic_model.all_weights[-2].assign(modify_val) tl.files.load_and_assign_npz_dict("./model_basic.npz", self.dynamic_model) self.assertLess(np.max(np.abs(ori_val - self.dynamic_model.all_weights[-2].numpy())), 1e-7)
ori_weights = self.dynamic_model._all_weights self.dynamic_model._all_weights = self.static_model._all_weights[1:] self.dynamic_model.all_weights[-2].assign(modify_val) tl.files.load_and_assign_npz_dict("./model_basic.npz", self.dynamic_model, skip=True) self.assertLess(np.max(np.abs(ori_val - self.dynamic_model.all_weights[-2].numpy())), 1e-7) self.dynamic_model._all_weights = ori_weights

def test_monitor(self):
with self.assertNotRaises(Exception):
hd.HyperDashHandler.set_apikey(self.apikey)
@hd.monitor("TRAVIS 1 - dogs vs. cats") def train_dogs_vs_cats(exp=None):
# Record the value of hyperparameter gamma for this experiment lr = exp.param("learning rate", 0.005) tl.logging.debug("Learning Rate: %f" % lr)
for epoch, accuracy in enumerate([10, 30, 50, 70, 80, 90, 95, 100]): tl.logging.debug("Epoch %d - Accuracy %d%%" % (epoch + 1, accuracy))
# Record a numerical performance metric exp.metric(name="accuracy", value=accuracy)
time.sleep(0.1)
train_dogs_vs_cats()

def test_monitor_variant(self):

    with self.assertNotRaises(Exception):

        @hd.monitor("TRAVIS 2 - dogs vs. cats", api_key=self.apikey)
        def train_dogs_vs_cats(exp=None):

            # Record the value of hyperparameter gamma for this experiment
            lr = exp.param("learning rate", 0.005)
            tl.logging.debug("Learning Rate: %f" % lr)

            for epoch, accuracy in enumerate([10, 30, 50, 70, 80, 90, 95, 100]):
                tl.logging.debug("Epoch %d - Accuracy %d%%" % (epoch + 1, accuracy))

                # Record a numerical performance metric
                exp.metric(name="accuracy", value=accuracy)

                time.sleep(0.1)

        train_dogs_vs_cats()

def test_Experiment_variant(self):
with self.assertNotRaises(Exception):
def train_dogs_vs_cats():
# Create an experiment with a model name, then autostart exp = hd.Experiment("TRAVIS 4 - dogs vs. cats", api_key=self.apikey)
# Record the value of hyperparameter gamma for this experiment lr = exp.param("learning rate", 0.005) tl.logging.debug("Learning Rate: %f" % lr)
for epoch, accuracy in enumerate([10, 30, 50, 70, 80, 90, 95, 100]): tl.logging.debug("Epoch %d - Accuracy %d%%" % (epoch + 1, accuracy))
# Record a numerical performance metric exp.metric(name="accuracy", value=accuracy)
time.sleep(0.1)
# Cleanup and mark that the experiment successfully completed exp.end()
train_dogs_vs_cats()

def test_lrelu(self):
    for i in range(-5, 15):

        if i > 0:
            good_output = i
        else:
            good_output = self.alpha * i

        computed_output = tl.act.leaky_relu(float(i), alpha=self.alpha)

        self.assertAlmostEqual(computed_output.numpy(), good_output, places=5)

    net = tl.layers.Input([10, 2])
    net = tl.layers.Dense(n_units=100, act=lambda x: tl.act.lrelu(x, 0.2), name='dense')(net)
    print(net)

def test_lrelu6(self):
    for i in range(-5, 15):

        if i < 0:
            good_output = self.alpha * i
        else:
            good_output = min(6, i)

        computed_output = tl.act.leaky_relu6(float(i), alpha=self.alpha)

        self.assertAlmostEqual(computed_output.numpy(), good_output, places=5)

    net = tl.layers.Input([10, 2])
    net = tl.layers.Dense(n_units=100, act=lambda x: tl.act.leaky_relu6(x, 0.2), name='dense')(net)
    print(net)

def test_concat(self):
class CustomModel(tl.models.Model):
def __init__(self): super(CustomModel, self).__init__() self.dense1 = tl.layers.Dense(in_channels=20, n_units=10, act=tf.nn.relu, name='relu1_1') self.dense2 = tl.layers.Dense(in_channels=20, n_units=10, act=tf.nn.relu, name='relu2_1') self.concat = tl.layers.Concat(concat_dim=1, name='concat_layer')
def forward(self, inputs): d1 = self.dense1(inputs) d2 = self.dense2(inputs) outputs = self.concat([d1, d2]) return outputs
model = CustomModel() model.train() inputs = tf.convert_to_tensor(np.random.random([4, 20]).astype(np.float32)) outputs = model(inputs) print(model)
self.assertEqual(outputs.get_shape().as_list(), [4, 20])

def test_elementwise(self):
class CustomModel(tl.models.Model):
def __init__(self): super(CustomModel, self).__init__() self.dense1 = tl.layers.Dense(in_channels=20, n_units=10, act=tf.nn.relu, name='relu1_1') self.dense2 = tl.layers.Dense(in_channels=20, n_units=10, act=tf.nn.relu, name='relu2_1') self.element = tl.layers.Elementwise(combine_fn=tf.minimum, name='minimum', act=tf.identity)
def forward(self, inputs): d1 = self.dense1(inputs) d2 = self.dense2(inputs) outputs = self.element([d1, d2]) return outputs, d1, d2
model = CustomModel() model.train() inputs = tf.convert_to_tensor(np.random.random([4, 20]).astype(np.float32)) outputs, d1, d2 = model(inputs) print(model)
min = tf.minimum(d1, d2) self.assertEqual(outputs.get_shape().as_list(), [4, 10]) self.assertTrue(np.array_equal(min.numpy(), outputs.numpy()))

def test_basic_simplernn(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), return_last_output=True, return_seq_2d=False, return_last_state=True ) rnn, rnn_state = rnnlayer(inputs) outputs = tl.layers.Dense(n_units=1)(rnn) rnn_model = tl.models.Model(inputs=inputs, outputs=[outputs, rnn_state[0]]) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train() assert rnnlayer.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y, final_state = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_lstmrnn(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer = tl.layers.RNN( cell=tf.keras.layers.LSTMCell(units=self.hidden_size, dropout=0.1), return_last_output=True, return_seq_2d=False, return_last_state=True ) rnn, rnn_state = rnnlayer(inputs) outputs = tl.layers.Dense(n_units=1)(rnn) rnn_model = tl.models.Model(inputs=inputs, outputs=[outputs, rnn_state[0], rnn_state[1]]) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train()
for epoch in range(50): with tf.GradientTape() as tape: pred_y, final_h, final_c = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

