def test_basic_simplernn_dynamic(self):
class CustomisedModel(tl.models.Model):
def __init__(self): super(CustomisedModel, self).__init__() self.rnnlayer = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=8, dropout=0.1), in_channels=4, return_last_output=False, return_seq_2d=False, return_last_state=False ) self.dense = tl.layers.Dense(in_channels=8, n_units=1)
def forward(self, x): z = self.rnnlayer(x) z = self.dense(z[:, -1, :]) return z
rnn_model = CustomisedModel() print(rnn_model) optimizer = tf.optimizers.Adam(learning_rate=0.01) rnn_model.train()
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_birnn_grucell(self):
class CustomisedModel(tl.models.Model):
def __init__(self): super(CustomisedModel, self).__init__() self.rnnlayer = tl.layers.BiRNN( fw_cell=tf.keras.layers.GRUCell(units=8, dropout=0.1), bw_cell=tf.keras.layers.GRUCell(units=8, dropout=0.1), in_channels=4, return_seq_2d=False, return_last_state=False ) self.dense = tl.layers.Dense(in_channels=16, n_units=1) self.reshape = tl.layers.Reshape([-1, 6])
def forward(self, x): z = self.rnnlayer(x, return_seq_2d=True) z = self.dense(z) z = self.reshape(z) return z
rnn_model = CustomisedModel() print(rnn_model) optimizer = tf.optimizers.Adam(learning_rate=0.01) rnn_model.train()
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_simplernn_dynamic_3(self):
class CustomisedModel(tl.models.Model):
def __init__(self): super(CustomisedModel, self).__init__() self.rnnlayer1 = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=8, dropout=0.1), in_channels=4, return_last_output=True, return_last_state=True ) self.rnnlayer2 = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=8, dropout=0.1), in_channels=4, return_last_output=True, return_last_state=False ) self.dense = tl.layers.Dense(in_channels=8, n_units=1)
def forward(self, x): _, state = self.rnnlayer1(x[:, :2, :]) z = self.rnnlayer2(x[:, 2:, :], initial_state=state) z = self.dense(z) return z
rnn_model = CustomisedModel() print(rnn_model) optimizer = tf.optimizers.Adam(learning_rate=0.01) rnn_model.train() assert rnn_model.rnnlayer1.is_train assert rnn_model.rnnlayer2.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_simplernn_dynamic_2(self):
class CustomisedModel(tl.models.Model):
def __init__(self): super(CustomisedModel, self).__init__() self.rnnlayer = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=8, dropout=0.1), in_channels=4, return_last_output=False, return_seq_2d=False, return_last_state=False ) self.dense = tl.layers.Dense(in_channels=8, n_units=1)
def forward(self, x): z = self.rnnlayer(x, return_seq_2d=True) z = self.dense(z[-2:, :]) return z
rnn_model = CustomisedModel() print(rnn_model) optimizer = tf.optimizers.Adam(learning_rate=0.01) rnn_model.train() assert rnn_model.rnnlayer.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_birnn_simplernncell(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer = tl.layers.BiRNN( fw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), bw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size + 1, dropout=0.1), return_seq_2d=True, return_last_state=True ) rnn, rnn_fw_state, rnn_bw_state = rnnlayer(inputs) dense = tl.layers.Dense(n_units=1)(rnn) outputs = tl.layers.Reshape([-1, self.num_steps])(dense) rnn_model = tl.models.Model(inputs=inputs, outputs=[outputs, rnn, rnn_fw_state[0], rnn_bw_state[0]]) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train() assert rnnlayer.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y, r, rfw, rbw = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y2)
self.assertEqual( r.get_shape().as_list(), [self.batch_size * self.num_steps, self.hidden_size + self.hidden_size + 1] ) gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_basic_birnn_lstmcell(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer = tl.layers.BiRNN( fw_cell=tf.keras.layers.LSTMCell(units=self.hidden_size, dropout=0.1), bw_cell=tf.keras.layers.LSTMCell(units=self.hidden_size + 1, dropout=0.1), return_seq_2d=False, return_last_state=True ) rnn, rnn_fw_state, rnn_bw_state = rnnlayer(inputs) din = tl.layers.Reshape([-1, self.hidden_size + self.hidden_size + 1])(rnn) dense = tl.layers.Dense(n_units=1)(din) outputs = tl.layers.Reshape([-1, self.num_steps])(dense) rnn_model = tl.models.Model(inputs=inputs, outputs=[outputs, rnn, rnn_fw_state[0], rnn_bw_state[0]]) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train() assert rnnlayer.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y, r, rfw, rbw = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y2)
self.assertEqual( r.get_shape().as_list(), [self.batch_size, self.num_steps, self.hidden_size + self.hidden_size + 1] ) gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_stack_simplernn(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer1 = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), return_last_output=False, return_seq_2d=False, return_last_state=False ) rnn1 = rnnlayer1(inputs) rnnlayer2 = tl.layers.RNN( cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), return_last_output=True, return_seq_2d=False, return_last_state=False ) rnn2 = rnnlayer2(rnn1) outputs = tl.layers.Dense(n_units=1)(rnn2) rnn_model = tl.models.Model(inputs=inputs, outputs=outputs) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train() assert rnnlayer1.is_train assert rnnlayer2.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_stack_birnn_simplernncell(self):
inputs = tl.layers.Input([self.batch_size, self.num_steps, self.embedding_size]) rnnlayer = tl.layers.BiRNN( fw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), bw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size + 1, dropout=0.1), return_seq_2d=False, return_last_state=False ) rnn = rnnlayer(inputs) rnnlayer2 = tl.layers.BiRNN( fw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size, dropout=0.1), bw_cell=tf.keras.layers.SimpleRNNCell(units=self.hidden_size + 1, dropout=0.1), return_seq_2d=True, return_last_state=False ) rnn2 = rnnlayer2(rnn) dense = tl.layers.Dense(n_units=1)(rnn2) outputs = tl.layers.Reshape([-1, self.num_steps])(dense) rnn_model = tl.models.Model(inputs=inputs, outputs=outputs) print(rnn_model)
optimizer = tf.optimizers.Adam(learning_rate=0.01)
rnn_model.train() assert rnnlayer.is_train assert rnnlayer2.is_train
for epoch in range(50): with tf.GradientTape() as tape: pred_y = rnn_model(self.data_x) loss = tl.cost.mean_squared_error(pred_y, self.data_y2)
gradients = tape.gradient(loss, rnn_model.trainable_weights) optimizer.apply_gradients(zip(gradients, rnn_model.trainable_weights))
if (epoch + 1) % 10 == 0: print("epoch %d, loss %f" % (epoch, loss))

def test_prelu_1(self):
    inputs = tl.layers.Input([10, 5])
    prelulayer = tl.layers.PRelu(channel_shared=True)
    prelu = prelulayer(inputs)
    model = tl.models.Model(inputs=inputs, outputs=prelu)
    out = model(self.data, is_train=True)

    print(prelulayer)

    gt = np.zeros(shape=self.data.shape)
    for i in range(len(gt)):
        for j in range(len(gt[i])):
            if self.data[i][j] >= 0:
                gt[i][j] = self.data[i][j]
            else:
                gt[i][j] = prelulayer.alpha_var_constrained.numpy() * self.data[i][j]

    self.assertTrue(np.array_equal(out.numpy(), gt))

def test_prelu6_2(self):
    inputs = tl.layers.Input([10, 5])
    prelulayer = tl.layers.PRelu6(in_channels=5)
    prelu = prelulayer(inputs)
    model = tl.models.Model(inputs=inputs, outputs=prelu)
    out = model(self.data, is_train=True)

    print(prelulayer)

    gt = np.zeros(shape=self.data.shape)
    for i in range(len(gt)):
        for j in range(len(gt[i])):
            if self.data[i][j] >= 0 and self.data[i][j] <= 6:
                gt[i][j] = self.data[i][j]
            elif self.data[i][j] > 6:
                gt[i][j] = 6
            else:
                gt[i][j] = prelulayer.alpha_var_constrained.numpy()[j] * self.data[i][j]

    self.assertTrue(np.array_equal(out.numpy(), gt))

def test_ptrelu6_2(self):
    inputs = tl.layers.Input([10, 5])
    prelulayer = tl.layers.PTRelu6(in_channels=5)
    prelu = prelulayer(inputs)
    model = tl.models.Model(inputs=inputs, outputs=prelu)
    out = model(self.data, is_train=True)

    print(prelulayer)

    gt = np.zeros(shape=self.data.shape)
    for i in range(len(gt)):
        for j in range(len(gt[i])):
            if self.data[i][j] >= 0 and self.data[i][j] <= 6:
                gt[i][j] = self.data[i][j]
            elif self.data[i][j] > 6:
                gt[i][j] = 6 + prelulayer.alpha_high_constrained.numpy()[j] * (self.data[i][j] - 6)
            else:
                gt[i][j] = prelulayer.alpha_low_constrained.numpy()[j] * self.data[i][j]

    self.assertTrue(np.allclose(out.numpy(), gt))

def test_ptrelu6_1(self):
    inputs = tl.layers.Input([10, 5])
    prelulayer = tl.layers.PTRelu6(channel_shared=True)
    prelu = prelulayer(inputs)
    model = tl.models.Model(inputs=inputs, outputs=prelu)
    out = model(self.data, is_train=True)

    print(prelulayer)

    gt = np.zeros(shape=self.data.shape)
    for i in range(len(gt)):
        for j in range(len(gt[i])):
            if self.data[i][j] >= 0 and self.data[i][j] <= 6:
                gt[i][j] = self.data[i][j]
            elif self.data[i][j] > 6:
                gt[i][j] = 6 + prelulayer.alpha_high_constrained.numpy() * (self.data[i][j] - 6)
            else:
                gt[i][j] = prelulayer.alpha_low_constrained.numpy() * self.data[i][j]

    self.assertTrue(np.array_equal(out.numpy(), gt))

def test_prelu_2(self):
