if self.in_channels: self.build(None) self._built = True
logging.info( "QuanConv2d %s: n_filter: %d filter_size: %s strides: %s pad: %s act: %s" % ( self.name, n_filter, str(filter_size), str(strides), padding, self.act.__name__ if self.act is not None else 'No Activation' ) )
if self.use_gemm: raise Exception("TODO. The current version use tf.matmul for inferencing.")
