def __init__( self, n_filter=32, filter_size=(3, 3), strides=(1, 1), act=None, padding='SAME', use_gemm=False, data_format="channels_last", dilation_rate=(1, 1), W_init=tl.initializers.truncated_normal(stddev=0.02), b_init=tl.initializers.constant(value=0.0), in_channels=None, name=None # 'binary_cnn2d', ): super().__init__(name, act=act) self.n_filter = n_filter self.filter_size = filter_size self.strides = self._strides = strides self.padding = padding self.use_gemm = use_gemm self.data_format = data_format self._dilation_rate = self.dilation_rate = dilation_rate self.W_init = W_init self.b_init = b_init self.in_channels = in_channels
if self.in_channels: self.build(None) self._built = True
logging.info( "BinaryConv2d %s: n_filter: %d filter_size: %s strides: %s pad: %s act: %s" % ( self.name, n_filter, str(filter_size), str(strides), padding, self.act.__name__ if self.act is not None else 'No Activation' ) )
if use_gemm: raise Exception("TODO. The current version use tf.matmul for inferencing.")
if len(self.strides) != 2: raise ValueError("len(strides) should be 2.")

def __repr__(self):
    actstr = self.act.__name__ if self.act is not None else 'No Activation'
    s = (
        '{classname}(in_channels={in_channels}, out_channels={n_filter}, kernel_size={filter_size}'
        ', strides={strides}, padding={padding}'
    )
    if self.dilation_rate != (1, ) * len(self.dilation_rate):
        s += ', dilation={dilation_rate}'
    if self.b_init is None:
        s += ', bias=False'
    s += (', ' + actstr)
    if self.name is not None:
        s += ', name=\'{name}\''
    s += ')'
    return s.format(classname=self.__class__.__name__, **self.__dict__)

