def build(self, inputs_shape):
    if self.data_format == 'channels_last':
        self.data_format = 'NHWC'
        if self.in_channels is None:
            self.in_channels = inputs_shape[-1]
        self._strides = [1, self._strides[0], self._strides[1], 1]
        self._dilation_rate = [1, self._dilation_rate[0], self._dilation_rate[1], 1]
    elif self.data_format == 'channels_first':
        self.data_format = 'NCHW'
        if self.in_channels is None:
            self.in_channels = inputs_shape[1]
        self._strides = [1, 1, self._strides[0], self._strides[1]]
        self._dilation_rate = [1, 1, self._dilation_rate[0], self._dilation_rate[1]]
    else:
        raise Exception("data_format should be either channels_last or channels_first")

    self.filter_shape = (self.filter_size[0], self.filter_size[1], self.in_channels, self.n_filter)

    self.W = self._get_weights("filters", shape=self.filter_shape, init=self.W_init)

    if self.b_init:
        self.b = self._get_weights("biases", shape=(self.n_filter, ), init=self.b_init)

def forward(self, inputs):

    alpha = compute_alpha(self.W)

    W_ = ternary_operation(self.W)
    W_ = tf.multiply(alpha, W_)

    outputs = tf.nn.conv2d(
        input=inputs, filters=W_, strides=self._strides, padding=self.padding, data_format=self.data_format,
        dilations=self._dilation_rate, name=self.name
    )

    if self.b_init:
        outputs = tf.nn.bias_add(outputs, self.b, data_format=self.data_format, name='bias_add')
    if self.act:
        outputs = self.act(outputs)

    return outputs

def forward(self, inputs):

    inputs = quantize_active(cabs(inputs), self.bitA)  # Do not remove

    W_ = quantize_weight(self.W, self.bitW)

    outputs = tf.nn.conv2d(
        input=inputs, filters=W_, strides=self._strides, padding=self.padding, data_format=self.data_format,
        dilations=self._dilation_rate, name=self.name
    )

    if self.b_init:
        outputs = tf.nn.bias_add(outputs, self.b, data_format=self.data_format, name='bias_add')
    if self.act:
        outputs = self.act(outputs)

    return outputs

def forward(self, inputs):

    _W = quantize(self.W)

    outputs = tf.nn.conv2d(
        input=inputs, filters=_W, strides=self._strides, padding=self.padding, data_format=self.data_format,
        dilations=self._dilation_rate, name=self.name
    )

    if self.b_init:
        outputs = tf.nn.bias_add(outputs, self.b, data_format=self.data_format, name='bias_add')
    if self.act:
        outputs = self.act(outputs)

    return outputs

def forward(self, inputs):

    inputs = quantize_active_overflow(inputs, self.bitA)  # Do not remove

    W_ = quantize_weight_overflow(self.W, self.bitW)

    outputs = tf.nn.conv2d(
        input=inputs, filters=W_, strides=self.strides, padding=self.padding, data_format=self.data_format,
        dilations=self._dilation_rate, name=self.name
    )

    if self.b_init:
        outputs = tf.nn.bias_add(outputs, self.b, data_format=self.data_format, name='bias_add')
    if self.act:
        outputs = self.act(outputs)

    return outputs

def forward(self, inputs):
outputs = tf.nn.conv1d( input=inputs, filters=self.W, stride=self.stride, padding=self.padding, dilations=[ self.dilation_rate, ], data_format=self.data_format, name=self.name, )
if self.b_init: outputs = tf.nn.bias_add(outputs, self.b, data_format=self.data_format, name='bias_add') if self.act: outputs = self.act(outputs) return outputs

def __init__( self, scale=2, act=None, in_channels=None, name=None # 'subpixel_conv1d' ): super().__init__(name, act=act) self.scale = scale self.in_channels = in_channels self.out_channels = int(self.in_channels / self.scale)
if self.in_channels is not None: self.build(None) self._built = True
logging.info( "SubpixelConv1d %s: scale: %d act: %s" % (self.name, scale, self.act.__name__ if self.act is not None else 'No Activation') )

def __init__( self, keep=0.5, n_units=100, act=None, W_init=tl.initializers.truncated_normal(stddev=0.1), b_init=tl.initializers.constant(value=0.0), in_channels=None, name=None, # 'dropconnect', ): super().__init__(name, act=act)
if isinstance(keep, numbers.Real) and not (keep > 0 and keep <= 1): raise ValueError("keep must be a scalar tensor or a float in the " "range (0, 1], got %g" % keep)
self.keep = keep self.n_units = n_units self.W_init = W_init self.b_init = b_init self.in_channels = in_channels
if self.in_channels is not None: self.build((None, self.in_channels)) self._built = True
logging.info( "DropconnectDense %s: %d %s" % (self.name, n_units, self.act.__name__ if self.act is not None else 'No Activation') )

def _get_param_shape(self, inputs_shape):
    if self.data_format == 'channels_last':
        axis = len(inputs_shape) - 1
    elif self.data_format == 'channels_first':
        axis = 1
    else:
        raise ValueError('data_format should be either %s or %s' % ('channels_last', 'channels_first'))

    channels = inputs_shape[axis]
    params_shape = [1] * len(inputs_shape)
    params_shape[axis] = channels

    axes = [i for i in range(len(inputs_shape)) if i != axis]
    return params_shape, axes

def _get_param_shape(self, inputs_shape):
    if self.data_format == 'channels_last':
        axis = len(inputs_shape) - 1
    elif self.data_format == 'channels_first':
        axis = 1
    else:
        raise ValueError('data_format should be either %s or %s' % ('channels_last', 'channels_first'))

    channels = inputs_shape[axis]
    params_shape = [1] * len(inputs_shape)
    params_shape[axis] = channels

    axes = [i for i in range(len(inputs_shape)) if i != 0 and i != axis]
    return params_shape, axes

def _get_param_shape(self, inputs_shape):
    if self.data_format == 'channels_last':
        axis = 2
    elif self.data_format == 'channels_first':
        axis = 1
    else:
        raise ValueError('data_format should be either %s or %s' % ('channels_last', 'channels_first'))

    if self.num_features is None:
        channels = inputs_shape[axis]
    else:
        channels = self.num_features
    params_shape = [1] * 3
    params_shape[axis] = channels

    axes = [i for i in range(3) if i != axis]
    return params_shape, axes

