def __init__( self, padding, name=None, # 'zeropad1d', ): super().__init__(name) self.padding = padding logging.info("ZeroPad1d %s: padding: %s" % (self.name, str(padding)))
if not isinstance(self.padding, (int, tuple, dict)): raise AssertionError()
self.build() self._built = True

def __init__( self, padding, name=None, # 'zeropad2d', ): super().__init__(name)
self.padding = padding logging.info("ZeroPad2d %s: padding: %s" % (self.name, str(self.padding)))
if not isinstance(self.padding, (int, tuple)): raise AssertionError("Padding should be of type `int` or `tuple`")
self.build() self._built = True

def __init__(
        self,
        fn,
        fn_weights=None,
        fn_args=None,
        name=None,
):

    super(Lambda, self).__init__(name=name)
    self.fn = fn
    self._trainable_weights = fn_weights if fn_weights is not None else []
    self.fn_args = fn_args if fn_args is not None else {}

    try:
        fn_name = repr(self.fn)
    except:
        fn_name = 'name not available'
    logging.info("Lambda  %s: func: %s, len_weights: %s" % (self.name, fn_name, len(self._trainable_weights)))

    self.build()
    self._built = True

def __init__(
        self,
        fn,
        fn_weights=None,
        fn_args=None,
        name=None,  #'elementwiselambda',
):

    super(ElementwiseLambda, self).__init__(name=name)
    self.fn = fn
    self._trainable_weights = fn_weights if fn_weights is not None else []
    self.fn_args = fn_args if fn_args is not None else {}

    try:
        fn_name = repr(self.fn)
    except:
        fn_name = 'name not available'
    logging.info(
        "ElementwiseLambda  %s: func: %s, len_weights: %s" % (self.name, fn_name, len(self._trainable_weights))
    )

    self.build()
    self._built = True

def __repr__(self):
    s = '{classname}('
    s += 'fn={fn_name},'
    s += 'len_weights={len_weights},'
    s += 'name=\'{name}\''
    s += ')'
    try:
        fn_name = repr(self.fn)
    except:
        fn_name = 'name not available'
    return s.format(
        classname=self.__class__.__name__, fn_name=fn_name, len_weights=len(self._trainable_weights),
        **self.__dict__
    )

def __repr__(self):
    s = '{classname}('
    s += 'fn={fn_name},'
    s += 'len_weights={len_weights},'
    s += 'name=\'{name}\''
    s += ')'
    try:
        fn_name = repr(self.fn)
    except:
        fn_name = 'name not available'
    return s.format(
        classname=self.__class__.__name__, fn_name=fn_name, len_weights=len(self._trainable_weights),
        **self.__dict__
    )
