def _traced_call_int(self, *args, **kwargs):
    _log_stderr(name, fnc, args, kwargs, False)
    r = None
    try:
        ret = fnc(self, *args, **kwargs)
    except Exception as ex:
        r = ex
    _log_stderr(name, fnc, args, kwargs, True)
    if r:
        raise r
    return ret

def _traced_call_int(*args, **kwargs):
    _log_stderr(name, fnc, args, kwargs, False)
    r = None
    try:
        ret = fnc(*args, **kwargs)
    except Exception as ex:
        r = ex
    _log_stderr(name, fnc, args, kwargs, True)
    if r:
        raise r
    return ret

def validate(self, value):
    """Validate value."""
    if self.exclusive:
        if value <= self.minimum_value:
            tpl = "'{value}' is lower or equal than minimum ('{min}')."
            raise ValidationError(
                tpl.format(value=value, min=self.minimum_value))
    else:
        if value < self.minimum_value:
            raise ValidationError(
                "'{value}' is lower than minimum ('{min}').".format(
                    value=value, min=self.minimum_value))

def validate(self, value):
    """Validate value."""
    if self.exclusive:
        if value >= self.maximum_value:
            tpl = "'{val}' is bigger or equal than maximum ('{max}')."
            raise ValidationError(
                tpl.format(val=value, max=self.maximum_value))
    else:
        if value > self.maximum_value:
            raise ValidationError(
                "'{value}' is bigger than maximum ('{max}').".format(
                    value=value, max=self.maximum_value))

