def report_histogram(self, title, series, histogram, iter, labels=None, xlabels=None, xtitle=None, ytitle=None, comment=None, mode='group', layout_config=None): """ Report an histogram bar plot :param title: Title (AKA metric) :type title: str :param series: Series (AKA variant) :type series: str :param histogram: The histogram data. A row for each dataset(bar in a bar group). A column for each bucket. :type histogram: numpy array :param iter: Iteration number :type iter: int :param labels: The labels for each bar group. :type labels: list of strings. :param xlabels: The labels of the x axis. :type xlabels: List of strings. :param str xtitle: optional x-axis title :param str ytitle: optional y-axis title :param comment: comment underneath the title :type comment: str :param mode: multiple histograms mode. valid options are: stack / group / relative. Default is 'group'. :type mode: str :param layout_config: optional dictionary for layout configuration, passed directly to plotly :type layout_config: dict or None """ assert mode in ('stack', 'group', 'relative')
plotly_dict = create_2d_histogram_plot( np_row_wise=histogram, title=title, xtitle=xtitle, ytitle=ytitle, labels=labels, series=series, xlabels=xlabels, comment=comment, mode=mode, layout_config=layout_config, )
return self.report_plot( title=self._normalize_name(title), series=self._normalize_name(series), plot=plotly_dict, iter=iter, nan_as_null=False, )

def report_2d_scatter(self, title, series, data, iter, mode='lines', xtitle=None, ytitle=None, labels=None, comment=None, layout_config=None): """ Report a 2d scatter graph (with lines)
:param title: Title (AKA metric) :type title: str :param series: Series (AKA variant) :type series: str :param data: A scattered data: pairs of x,y as rows in a numpy array :type data: ndarray :param iter: Iteration number :type iter: int :param mode: (type str) 'lines'/'markers'/'lines+markers' :param xtitle: optional x-axis title :param ytitle: optional y-axis title :param labels: label (text) per point in the scatter (in the same order) :param comment: comment underneath the title :type comment: str :param layout_config: optional dictionary for layout configuration, passed directly to plotly :type layout_config: dict or None """ plotly_dict = create_2d_scatter_series( np_row_wise=data, title=title, series_name=series, mode=mode, xtitle=xtitle, ytitle=ytitle, labels=labels, comment=comment, layout_config=layout_config, )
return self.report_plot( title=self._normalize_name(title), series=self._normalize_name(series), plot=plotly_dict, iter=iter, nan_as_null=False, )

def report_value_surface(self, title, series, data, iter, xlabels=None, ylabels=None, xtitle=None, ytitle=None, ztitle=None, camera=None, comment=None, layout_config=None): """ Report a 3d surface (same data as heat-map matrix, only presented differently)
:param title: Title (AKA metric) :type title: str :param series: Series (AKA variant) :type series: str :param data: A heat-map matrix (example: confusion matrix) :type data: ndarray :param iter: Iteration number :type iter: int :param xlabels: optional label per column of the matrix :param ylabels: optional label per row of the matrix :param xtitle: optional x-axis title :param ytitle: optional y-axis title :param ztitle: optional z-axis title :param camera: X,Y,Z camera position. def: (1,1,1) :param comment: comment underneath the title :param layout_config: optional dictionary for layout configuration, passed directly to plotly :type layout_config: dict or None """
plotly_dict = create_3d_surface( np_value_matrix=data, title=title + '/' + series, xlabels=xlabels, ylabels=ylabels, series=series, xtitle=xtitle, ytitle=ytitle, ztitle=ztitle, camera=camera, comment=comment, layout_config=layout_config, )
return self.report_plot( title=self._normalize_name(title), series=self._normalize_name(series), plot=plotly_dict, iter=iter, nan_as_null=False, )

@staticmethod
def parse_known_args(self, args=None, namespace=None):
    if PatchArgumentParser._recursion_guard:
        return {} if not PatchArgumentParser._original_parse_args else \
            PatchArgumentParser._original_parse_known_args(self, args=args, namespace=namespace)

    PatchArgumentParser._recursion_guard = True
    try:
        result = PatchArgumentParser._patched_parse_args(
            PatchArgumentParser._original_parse_known_args, self, args=args, namespace=namespace)
    finally:
        PatchArgumentParser._recursion_guard = False

def _save(original_fn, obj, f, *args, **kwargs):
    ret = original_fn(obj, f, *args, **kwargs)

    # if there is no main task or this is a nested call
    if not PatchMegEngineModelIO.__main_task:
        return ret

    # noinspection PyBroadException
    try:
        if isinstance(f, six.string_types):
            filename = f
        elif hasattr(f, 'as_posix'):
            filename = f.as_posix()
        elif hasattr(f, 'name'):
            # noinspection PyBroadException
            try:
                f.flush()
            except Exception:
                pass

            if not isinstance(f.name, six.string_types):
                # Probably a BufferedRandom object that has no meaningful name (still no harm flushing)  # noqa
                return ret

            filename = f.name
        else:
            filename = None
    except Exception:
        filename = None

    # give the model a descriptive name based on the file name
    # noinspection PyBroadException
    try:
        model_name = Path(filename).stem if filename is not None else None
    except Exception:
        model_name = None

    WeightsFileHandler.create_output_model(
        obj, filename, Framework.megengine,
        PatchMegEngineModelIO.__main_task,
        singlefile=True, model_name=model_name,
    )

    return ret

@staticmethod
def _save(original_fn, obj, f, *args, **kwargs):
    ret = original_fn(obj, f, *args, **kwargs)

    # if there is no main task or this is a nested call
    if not PatchPyTorchModelIO.__main_task:
        return ret

    # pytorch-lightning check if rank is zero
    if hasattr(obj, 'is_global_zero'):
        if not obj.is_global_zero:
            return ret
    elif hasattr(obj, 'trainer') and hasattr(obj.trainer, 'is_global_zero'):
        if not obj.trainer.is_global_zero:
            return ret

    # noinspection PyBroadException
    try:
        if isinstance(f, six.string_types):
            filename = f
        elif hasattr(f, 'as_posix'):
            filename = f.as_posix()
        elif hasattr(f, 'name'):
            # noinspection PyBroadException
            try:
                f.flush()
            except Exception:
                pass

            if not isinstance(f.name, six.string_types):
                # Probably a BufferedRandom object that has no meaningful name (still no harm flushing)
                return ret

            filename = f.name
        else:
            filename = None
    except Exception:
        filename = None

    # give the model a descriptive name based on the file name
    # noinspection PyBroadException
    try:
        model_name = Path(filename).stem if filename is not None else None
    except Exception:
        model_name = None

    WeightsFileHandler.create_output_model(
        obj, filename, Framework.pytorch, PatchPyTorchModelIO.__main_task, singlefile=True, model_name=model_name)

def _load(original_fn, f, *args, **kwargs):
    # if there is no main task or this is a nested call
    if not PatchMegEngineModelIO.__main_task:
        return original_fn(f, *args, **kwargs)

    # noinspection PyBroadException
    try:
        if isinstance(f, six.string_types):
            filename = f
        elif hasattr(f, 'as_posix'):
            filename = f.as_posix()
        elif hasattr(f, 'name'):
            filename = f.name
        else:
            filename = None
    except Exception:
        filename = None

    # register input model
    empty = _Empty()
    # try to load model before registering, in case we fail
    model = original_fn(f, *args, **kwargs)
    WeightsFileHandler.restore_weights_file(
        empty, filename, Framework.megengine,
        PatchMegEngineModelIO.__main_task
    )

    if empty.trains_in_model:
        # noinspection PyBroadException
        try:
            model.trains_in_model = empty.trains_in_model
        except Exception:
            pass

    return model

def _load_from_obj(original_fn, obj, f, *args, **kwargs):
    # if there is no main task or this is a nested call
    if not PatchMegEngineModelIO.__main_task:
        return original_fn(obj, f, *args, **kwargs)

    # noinspection PyBroadException
    try:
        if isinstance(f, six.string_types):
            filename = f
        elif hasattr(f, 'as_posix'):
            filename = f.as_posix()
        elif hasattr(f, 'name'):
            filename = f.name
        else:
            filename = None
    except Exception:
        filename = None

    # register input model
    empty = _Empty()
    # try to load model before registering, in case we fail
    model = original_fn(obj, f, *args, **kwargs)
    WeightsFileHandler.restore_weights_file(
        empty, filename, Framework.megengine,
        PatchMegEngineModelIO.__main_task,
    )

    if empty.trains_in_model:
        # noinspection PyBroadException
        try:
            model.trains_in_model = empty.trains_in_model
        except Exception:
            pass

    return model

