    if isinstance(f, six.string_types):
        filename = f
    elif hasattr(f, 'name'):
        filename = f.name
    elif len(args) == 1 and isinstance(args[0], six.string_types):
        filename = args[0]
    else:
        filename = None

    if not PatchXGBoostModelIO.__main_task:
        return original_fn(f, *args, **kwargs)

    # register input model
    empty = _Empty()
    # Hack: disabled
