        WeightsFileHandler.restore_weights_file(empty, filename, Framework.xgboost,
                                                PatchXGBoostModelIO.__main_task)

    if empty.trains_in_model:
        # noinspection PyBroadException
        try:
            model.trains_in_model = empty.trains_in_model
        except Exception:
            pass
    return model

