def _numpypickler(original_fn, obj, f, *args, **kwargs):
    ret = original_fn(obj, f, *args, **kwargs)
    if not PatchedJoblib._current_task:
        return ret

    fname = f if isinstance(f, six.string_types) else None
    fileobj = ret if isinstance(f, six.string_types) else f

    if fileobj and hasattr(fileobj, 'close'):
        def callback(*_):
            PatchedJoblib._register_dump(obj, fname or fileobj)

        if isinstance(fname, six.string_types) or hasattr(fileobj, 'name'):
            buffer_writer_close_cb(fileobj, callback)
    else:
        PatchedJoblib._register_dump(obj, f)

    return ret

def _write_fileobject(obj, original_fn, f, *args, **kwargs):
    ret = original_fn(f, *args, **kwargs)
    if not PatchedJoblib._current_task:
        return ret

    fname = f if isinstance(f, six.string_types) else None
    fileobj = ret if isinstance(f, six.string_types) else f

    if fileobj and hasattr(fileobj, 'close'):
        def callback(*_):
            PatchedJoblib._register_dump(obj, fname or fileobj)

        if isinstance(fname, six.string_types) or hasattr(fileobj, 'name'):
            buffer_writer_close_cb(fileobj, callback)
    else:
        PatchedJoblib._register_dump(obj, f)
    return ret

def hocon_quote_key(a_obj):
    """ Recursively quote key with '.' to \"key\" """
    if isinstance(a_obj, list):
        return [hocon_quote_key(a) for a in a_obj]
    elif isinstance(a_obj, tuple):
        return tuple(hocon_quote_key(a) for a in a_obj)
    elif not isinstance(a_obj, dict):
        return a_obj

    # preserve dict type
    a_dict = a_obj
    new_dict = type(a_dict)()
    for k, v in a_dict.items():
        if isinstance(k, str) and '.' in k:
            new_dict['"{}"'.format(k)] = hocon_quote_key(v)
        else:
            new_dict[k] = hocon_quote_key(v)
    return new_dict

def hocon_unquote_key(a_obj):
    """ Recursively unquote \"key\" with '.' to key """

    if isinstance(a_obj, list):
        return [hocon_unquote_key(a) for a in a_obj]
    elif isinstance(a_obj, tuple):
        return tuple(hocon_unquote_key(a) for a in a_obj)
    elif not isinstance(a_obj, dict):
        return a_obj

    a_dict = a_obj

    # ConfigTree to dict
    if hasattr(a_dict, 'as_plain_ordered_dict'):
        a_dict = a_dict.as_plain_ordered_dict()

    # preserve dict type
    new_dict = type(a_dict)()
    for k, v in a_dict.items():
        if isinstance(k, str) and k[0] == '"' and k[-1] == '"' and '.' in k:
            new_dict[k[1:-1]] = hocon_unquote_key(v)
        else:
            new_dict[k] = hocon_unquote_key(v)
    return new_dict

def nvmlDeviceGetSupportedMemoryClocks(handle): # first call to get the size c_count = c_uint(0) fn = _nvmlGetFunctionPointer("nvmlDeviceGetSupportedMemoryClocks") ret = fn(handle, byref(c_count), None)
if (ret == NVML_SUCCESS): # special case, no clocks return [] elif (ret == NVML_ERROR_INSUFFICIENT_SIZE): # typical case clocks_array = c_uint * c_count.value c_clocks = clocks_array()
# make the call again ret = fn(handle, byref(c_count), c_clocks) _nvmlCheckReturn(ret)
procs = [] for i in range(c_count.value): procs.append(c_clocks[i])
return procs else: # error case raise NVMLError(ret)
