    c_vgpuPgpuMetadata = c_nvmlVgpuPgpuMetadata_t()
    c_bufferSize = c_uint(0)
    # Make the first NVML API call to get the c_bufferSize value.
    # We have already allocated required buffer above.
    ret = fn(handle, byref(c_vgpuPgpuMetadata), byref(c_bufferSize))
    if (ret == NVML_ERROR_INSUFFICIENT_SIZE):
        ret = fn(handle, byref(c_vgpuPgpuMetadata), byref(c_bufferSize))
        _nvmlCheckReturn(ret)
    else:
        raise NVMLError(ret)
    return c_vgpuPgpuMetadata

def to_json(cls, config, compact=False, indent=2, level=0):
    """Convert HOCON input into a JSON output

    :return: JSON string representation
    :type return: basestring
    """
    lines = ""
    if isinstance(config, ConfigTree):
        if len(config) == 0:
            lines += '{}'
        else:
            lines += '{\n'
            bet_lines = []
            for key, item in config.items():
                bet_lines.append('{indent}"{key}": {value}'.format(
                    indent=''.rjust((level + 1) * indent, ' '),
                    key=key.strip('"'),  # for dotted keys enclosed with "" to not be interpreted as nested key
                    value=cls.to_json(item, compact, indent, level + 1))
                )
            lines += ',\n'.join(bet_lines)
            lines += '\n{indent}}}'.format(indent=''.rjust(level * indent, ' '))
    elif isinstance(config, list):
        if len(config) == 0:
            lines += '[]'
        else:
            lines += '[\n'
            bet_lines = []
            for item in config:
                bet_lines.append('{indent}{value}'.format(
                    indent=''.rjust((level + 1) * indent, ' '),
                    value=cls.to_json(item, compact, indent, level + 1))
                )
            lines += ',\n'.join(bet_lines)
            lines += '\n{indent}]'.format(indent=''.rjust(level * indent, ' '))
    elif isinstance(config, basestring):
        lines = json.dumps(config)
    elif config is None or isinstance(config, NoneValue):
        lines = 'null'
    elif config is True:
        lines = 'true'
    elif config is False:
        lines = 'false'
    else:
        lines = str(config)
    return lines

@classmethod
def to_yaml(cls, config, compact=False, indent=2, level=0):
    """Convert HOCON input into a YAML output

    :return: YAML string representation
    :type return: basestring
    """
    lines = ""
    if isinstance(config, ConfigTree):
        if len(config) > 0:
            if level > 0:
                lines += '\n'
            bet_lines = []
            for key, item in config.items():
                bet_lines.append('{indent}{key}: {value}'.format(
                    indent=''.rjust(level * indent, ' '),
                    key=key.strip('"'),  # for dotted keys enclosed with "" to not be interpreted as nested key,
                    value=cls.to_yaml(item, compact, indent, level + 1))
                )
            lines += '\n'.join(bet_lines)
    elif isinstance(config, list):
        config_list = [line for line in config if line is not None]
        if len(config_list) == 0:
            lines += '[]'
        else:
            lines += '\n'
            bet_lines = []
            for item in config_list:
                bet_lines.append('{indent}- {value}'.format(indent=''.rjust(level * indent, ' '),
                                                            value=cls.to_yaml(item, compact, indent, level + 1)))
            lines += '\n'.join(bet_lines)
    elif isinstance(config, basestring):
        # if it contains a \n then it's multiline
        lines = config.split('\n')
        if len(lines) == 1:
            lines = config
        else:
            lines = '|\n' + '\n'.join([line.rjust(level * indent, ' ') for line in lines])
    elif config is None or isinstance(config, NoneValue):
        lines = 'null'
    elif config is True:
        lines = 'true'
    elif config is False:
        lines = 'false'
    else:
        lines = str(config)
    return lines

@contextmanager
def draw_figure(self, fig, props):
    if hasattr(self, "_current_fig") and self._current_fig is not None:
        warnings.warn("figure embedded in figure: something is wrong")
    self._current_fig = fig
    self._fig_props = props
    self.open_figure(fig=fig, props=props)
    yield
    self.close_figure(fig=fig)
    self._current_fig = None

@contextmanager
def draw_axes(self, ax, props):
    if hasattr(self, "_current_ax") and self._current_ax is not None:
        warnings.warn("axes embedded in axes: something is wrong")
    self._current_ax = ax
    self._ax_props = props
    self.open_axes(ax=ax, props=props)
    yield
    self.close_axes(ax=ax)
    self._current_ax = None

def draw_line(self, data, coordinates, style, label, mplobj=None):
    if coordinates != 'data':
        warnings.warn("Only data coordinates supported. Skipping this")
    dataname = "table{0:03d}".format(len(self.data) + 1)

    # TODO: respect the other style settings
    self.data.append({'name': dataname,
                      'values': [dict(x=d[0], y=d[1]) for d in data]})
    self.marks.append({'type': 'line',
                       'from': {'data': dataname},
                       'properties': {
                           "enter": {
                               "interpolate": {"value": "monotone"},
                               "x": {"scale": "x", "field": "data.x"},
                               "y": {"scale": "y", "field": "data.y"},
                               "stroke": {"value": style['color']},
                               "strokeOpacity": {"value": style['alpha']},
                               "strokeWidth": {"value": style['linewidth']},
                           }
                       }
                       })

def draw_markers(self, data, coordinates, style, label, mplobj=None):
    if coordinates != 'data':
        warnings.warn("Only data coordinates supported. Skipping this")
    dataname = "table{0:03d}".format(len(self.data) + 1)

    # TODO: respect the other style settings
    self.data.append({'name': dataname,
                      'values': [dict(x=d[0], y=d[1]) for d in data]})
    self.marks.append({'type': 'symbol',
                       'from': {'data': dataname},
                       'properties': {
                           "enter": {
                               "interpolate": {"value": "monotone"},
                               "x": {"scale": "x", "field": "data.x"},
                               "y": {"scale": "y", "field": "data.y"},
                               "fill": {"value": style['facecolor']},
                               "fillOpacity": {"value": style['alpha']},
                               "stroke": {"value": style['edgecolor']},
                               "strokeOpacity": {"value": style['alpha']},
                               "strokeWidth": {"value": style['edgewidth']},
                           }
                       }
                       })

def draw_line(self, data, coordinates, style, label, mplobj=None):
    import vincent  # only import if VincentRenderer is used
    if coordinates != 'data':
        warnings.warn("Only data coordinates supported. Skipping this")
    linedata = {'x': data[:, 0],
                'y': data[:, 1]}
    line = vincent.Line(linedata, iter_idx='x',
                        width=self.figwidth, height=self.figheight)

    # TODO: respect the other style settings
    line.scales['color'].range = [style['color']]

    if self.chart is None:
        self.chart = line
    else:
        warnings.warn("Multiple plot elements not yet supported")

def draw_markers(self, data, coordinates, style, label, mplobj=None):
    import vincent  # only import if VincentRenderer is used
    if coordinates != 'data':
        warnings.warn("Only data coordinates supported. Skipping this")
    markerdata = {'x': data[:, 0],
                  'y': data[:, 1]}
    markers = vincent.Scatter(markerdata, iter_idx='x',
                              width=self.figwidth, height=self.figheight)

    # TODO: respect the other style settings
    markers.scales['color'].range = [style['facecolor']]

    if self.chart is None:
        self.chart = markers
    else:
        warnings.warn("Multiple plot elements not yet supported")

def main(): # Connecting ClearML with the current process, # from here on everything is logged automatically task = Task.init(project_name='examples', task_name='pytorch mnist train')
# Training settings parser = argparse.ArgumentParser(description='PyTorch MNIST Example') parser.add_argument('--batch-size', type=int, default=64, metavar='N', help='input batch size for training (default: 64)') parser.add_argument('--test-batch-size', type=int, default=1000, metavar='N', help='input batch size for testing (default: 1000)') parser.add_argument('--epochs', type=int, default=10, metavar='N', help='number of epochs to train (default: 10)') parser.add_argument('--lr', type=float, default=0.01, metavar='LR', help='learning rate (default: 0.01)') parser.add_argument('--momentum', type=float, default=0.5, metavar='M', help='SGD momentum (default: 0.5)') parser.add_argument('--no-cuda', action='store_true', default=False, help='disables CUDA training') parser.add_argument('--seed', type=int, default=1, metavar='S', help='random seed (default: 1)') parser.add_argument('--log-interval', type=int, default=10, metavar='N', help='how many batches to wait before logging training status')
parser.add_argument('--save-model', action='store_true', default=True, help='For Saving the current Model') args = parser.parse_args() use_cuda = not args.no_cuda and torch.cuda.is_available()
torch.manual_seed(args.seed)
device = torch.device("cuda" if use_cuda else "cpu")
kwargs = {'num_workers': 4, 'pin_memory': True} if use_cuda else {} train_loader = torch.utils.data.DataLoader( datasets.MNIST(os.path.join('..', 'data'), train=True, download=True, transform=transforms.Compose([ transforms.ToTensor(), transforms.Normalize((0.1307,), (0.3081,)) ])), batch_size=args.batch_size, shuffle=True, **kwargs) test_loader = torch.utils.data.DataLoader( datasets.MNIST(os.path.join('..', 'data'), train=False, transform=transforms.Compose([ transforms.ToTensor(), transforms.Normalize((0.1307,), (0.3081,)) ])), batch_size=args.test_batch_size, shuffle=True, **kwargs)
model = Net().to(device) optimizer = optim.SGD(model.parameters(), lr=args.lr, momentum=args.momentum)
for epoch in range(1, args.epochs + 1): train(args, model, device, train_loader, optimizer, epoch) test(args, model, device, test_loader, epoch)
if (args.save_model): torch.save(model.state_dict(), os.path.join(gettempdir(), "mnist_cnn.pt"))

def run(epochs, lr, momentum, log_interval): device = "cuda" if torch.cuda.is_available() else "cpu" net = Net().to(device) criterion = nn.CrossEntropyLoss() optimizer = optim.SGD(net.parameters(), lr=lr, momentum=momentum)
trainer = create_supervised_trainer(net, optimizer, criterion, device=device) trainer.logger = setup_logger("trainer")
val_metrics = {"accuracy": Accuracy(),"loss": Loss(criterion), "recall": Recall()} evaluator = create_supervised_evaluator(net, metrics=val_metrics, device=device) evaluator.logger = setup_logger("evaluator")
# Attach handler to plot trainer's loss every 100 iterations tb_logger.attach_output_handler( trainer, event_name=Events.ITERATION_COMPLETED(every=params.get('loss_report')), tag="training", output_transform=lambda loss: {"loss": loss}, )
# Attach handler to dump evaluator's metrics every epoch completed for tag, evaluator in [("training", trainer), ("validation", evaluator)]: tb_logger.attach_output_handler( evaluator, event_name=Events.EPOCH_COMPLETED, tag=tag, metric_names="all", global_step_transform=global_step_from_engine(trainer), )
# Attach function to build debug images and report every epoch end tb_logger.attach( evaluator, log_handler=predictions_gt_images_handler, event_name=Events.EPOCH_COMPLETED(once=1), );
desc = "ITERATION - loss: {:.2f}" pbar = tqdm(initial=0, leave=False, total=len(trainloader), desc=desc.format(0))
@trainer.on(Events.ITERATION_COMPLETED(every=log_interval)) def log_training_loss(engine): pbar.desc = desc.format(engine.state.output) pbar.update(log_interval)
@trainer.on(Events.EPOCH_COMPLETED) def log_training_results(engine): pbar.refresh() evaluator.run(trainloader) metrics = evaluator.state.metrics avg_accuracy = metrics["accuracy"] avg_nll = metrics["loss"] tqdm.write( "Training Results - Epoch: {} Avg accuracy: {:.2f} Avg loss: {:.2f}".format( engine.state.epoch, avg_accuracy, avg_nll ) )
@trainer.on(Events.EPOCH_COMPLETED) def log_validation_results(engine): evaluator.run(testloader) metrics = evaluator.state.metrics avg_accuracy = metrics["accuracy"] avg_nll = metrics["loss"] tqdm.write( "Validation Results - Epoch: {} Avg accuracy: {:.2f} Avg loss: {:.2f}".format( engine.state.epoch, avg_accuracy, avg_nll ) )
pbar.n = pbar.last_print_n = 0
@trainer.on(Events.EPOCH_COMPLETED | Events.COMPLETED) def log_time(): tqdm.write( "{} took {} seconds".format(trainer.last_event_name.name, trainer.state.times[trainer.last_event_name.name]) )
trainer.run(trainloader, max_epochs=epochs) pbar.close()
PATH = './cifar_net.pth' torch.save(net.state_dict(), PATH)
print('Finished Training') print('Task ID number is: {}'.format(task.id))

