super(PCA, self).__init__(contamination=contamination) self.n_components = n_components self.n_selected_components = n_selected_components self.copy = copy self.whiten = whiten self.svd_solver = svd_solver self.tol = tol self.iterated_power = iterated_power self.random_state = random_state self.weighted = weighted self.standardization = standardization
