                def custom_forward(*inputs):
                    return module(*inputs)

                return custom_forward

            hidden_states = torch.utils.checkpoint.checkpoint(
                create_custom_forward(conv_layer),
                hidden_states,
            )
        else:
            hidden_states = conv_layer(hidden_states)

    return hidden_states

def forward(self, input_values):
    hidden_states = input_values[:, None]

    # make sure hidden_states require grad for gradient_checkpointing
    if self._requires_grad and self.training:
        hidden_states.requires_grad = True

    for conv_layer in self.conv_layers:
        if self._requires_grad and self.gradient_checkpointing and self.training:

            def create_custom_forward(module):
                def custom_forward(*inputs):
                    return module(*inputs)

                return custom_forward

            hidden_states = torch.utils.checkpoint.checkpoint(
                create_custom_forward(conv_layer),
                hidden_states,
            )
        else:
            hidden_states = conv_layer(hidden_states)

    return hidden_states

def __init__(self, config):
    super().__init__()
    self.intermediate_dropout = nn.Dropout(config.activation_dropout)

    self.intermediate_dense = nn.Linear(config.hidden_size, config.intermediate_size)
    if isinstance(config.hidden_act, str):
        self.intermediate_act_fn = ACT2FN[config.hidden_act]
    else:
        self.intermediate_act_fn = config.hidden_act

    self.output_dense = nn.Linear(config.intermediate_size, config.hidden_size)
    self.output_dropout = nn.Dropout(config.hidden_dropout)

def __init__(self, config):
    super().__init__()
    self.intermediate_dropout = nn.Dropout(config.activation_dropout)

    self.intermediate_dense = nn.Linear(config.hidden_size, config.intermediate_size)
    if isinstance(config.hidden_act, str):
        self.intermediate_act_fn = ACT2FN[config.hidden_act]
    else:
        self.intermediate_act_fn = config.hidden_act

    self.output_dense = nn.Linear(config.intermediate_size, config.hidden_size)
    self.output_dropout = nn.Dropout(config.hidden_dropout)

    def forward(
        self,
        hidden_states,
        attention_mask=None,
        output_attentions=False,
        output_hidden_states=False,
        return_dict=True,
    ):
        all_hidden_states = () if output_hidden_states else None
        all_self_attentions = () if output_attentions else None

        if attention_mask is not None:
            # make sure padded tokens output 0
            hidden_states[~attention_mask] = 0.0

            # extend attention_mask
            attention_mask = (1.0 - attention_mask[:, None, None, :].to(dtype=hidden_states.dtype)) * -10000.0
            attention_mask = attention_mask.expand(
                attention_mask.shape[0], 1, attention_mask.shape[-1], attention_mask.shape[-1]
            )

        position_embeddings = self.pos_conv_embed(hidden_states)
        hidden_states = hidden_states + position_embeddings
        hidden_states = self.layer_norm(hidden_states)
        hidden_states = self.dropout(hidden_states)

        deepspeed_zero3_is_enabled = is_deepspeed_zero3_enabled()

        for layer in self.layers:
            if output_hidden_states:
                all_hidden_states = all_hidden_states + (hidden_states,)

            # add LayerDrop (see https://arxiv.org/abs/1909.11556 for description)
            dropout_probability = np.random.uniform(0, 1)

            skip_the_layer = True if self.training and (dropout_probability < self.config.layerdrop) else False
            if not skip_the_layer or deepspeed_zero3_is_enabled:
                # under deepspeed zero3 all gpus must run in sync
                if self.gradient_checkpointing and self.training:
                    # create gradient checkpointing function
                    def create_custom_forward(module):
                        def custom_forward(*inputs):
                            return module(*inputs, output_attentions)

                        return custom_forward

                    layer_outputs = torch.utils.checkpoint.checkpoint(
                        create_custom_forward(layer),
                        hidden_states,
                        attention_mask,
                    )
                else:
                    layer_outputs = layer(
                        hidden_states, attention_mask=attention_mask, output_attentions=output_attentions
                    )
                hidden_states = layer_outputs[0]

            if skip_the_layer:
                layer_outputs = (None, None)

            if output_attentions:
                all_self_attentions = all_self_attentions + (layer_outputs[1],)

        if output_hidden_states:
            all_hidden_states = all_hidden_states + (hidden_states,)

        if not return_dict:
            return tuple(v for v in [hidden_states, all_hidden_states, all_self_attentions] if v is not None)
        return BaseModelOutput(
            last_hidden_state=hidden_states,
            hidden_states=all_hidden_states,
            attentions=all_self_attentions,
        )


# Copied from transformers.models.wav2vec2.modeling_wav2vec2.Wav2Vec2EncoderStableLayerNorm with Wav2Vec2->Hubert

def forward(
    self,
    hidden_states,
    attention_mask=None,
    output_attentions=False,
    output_hidden_states=False,
    return_dict=True,
):
    all_hidden_states = () if output_hidden_states else None
    all_self_attentions = () if output_attentions else None

    if attention_mask is not None:
        # make sure padded tokens are not attended to
        hidden_states[~attention_mask] = 0

        # extend attention_mask
        attention_mask = (1.0 - attention_mask[:, None, None, :].to(dtype=hidden_states.dtype)) * -10000.0
        attention_mask = attention_mask.expand(
            attention_mask.shape[0], 1, attention_mask.shape[-1], attention_mask.shape[-1]
        )

    position_embeddings = self.pos_conv_embed(hidden_states)
    hidden_states = hidden_states + position_embeddings
    hidden_states = self.dropout(hidden_states)

    deepspeed_zero3_is_enabled = is_deepspeed_zero3_enabled()

    for layer in self.layers:
        if output_hidden_states:
            all_hidden_states = all_hidden_states + (hidden_states,)

        # add LayerDrop (see https://arxiv.org/abs/1909.11556 for description)
        dropout_probability = np.random.uniform(0, 1)

        skip_the_layer = True if self.training and (dropout_probability < self.config.layerdrop) else False
        if not skip_the_layer or deepspeed_zero3_is_enabled:
            # under deepspeed zero3 all gpus must run in sync
            # XXX: could optimize this like synced_gpus in generate_utils but not sure if it's worth the code complication
            if self.gradient_checkpointing and self.training:
                # create gradient checkpointing function
                def create_custom_forward(module):
                    def custom_forward(*inputs):
                        return module(*inputs, output_attentions)

                    return custom_forward

                layer_outputs = torch.utils.checkpoint.checkpoint(
                    create_custom_forward(layer),
                    hidden_states,
                    attention_mask,
                )
            else:
                layer_outputs = layer(
                    hidden_states, attention_mask=attention_mask, output_attentions=output_attentions
                )
            hidden_states = layer_outputs[0]

        if skip_the_layer:
            layer_outputs = (None, None)

        if output_attentions:
            all_self_attentions = all_self_attentions + (layer_outputs[1],)

    hidden_states = self.layer_norm(hidden_states)

    if output_hidden_states:
        all_hidden_states = all_hidden_states + (hidden_states,)

    if not return_dict:
        return tuple(v for v in [hidden_states, all_hidden_states, all_self_attentions] if v is not None)
    return BaseModelOutput(
        last_hidden_state=hidden_states,
        hidden_states=all_hidden_states,
        attentions=all_self_attentions,
    )

def remove_ignore_keys_(state_dict):
    ignore_keys = [
        "encoder.version",
        "decoder.version",
        "model.encoder.version",
        "model.decoder.version",
        "_float_tensor",
    ]
    for k in ignore_keys:
        state_dict.pop(k, None)

def __init__(self, feature_extractor, tokenizer):
    if not isinstance(feature_extractor, Speech2TextFeatureExtractor):
        raise ValueError(
            f"`feature_extractor` has to be of type {Speech2TextFeatureExtractor.__class__}, but is {type(feature_extractor)}"
        )
    if not isinstance(tokenizer, Speech2TextTokenizer):
        raise ValueError(
            f"`tokenizer` has to be of type {Speech2TextTokenizer.__class__}, but is {type(tokenizer)}"
        )

    self.feature_extractor = feature_extractor
    self.tokenizer = tokenizer
    self.current_processor = self.feature_extractor

def __init__(self, feature_extractor, tokenizer):
    if not isinstance(feature_extractor, Wav2Vec2FeatureExtractor):
        raise ValueError(
            f"`feature_extractor` has to be of type {Wav2Vec2FeatureExtractor.__class__}, but is {type(feature_extractor)}"
        )
    if not isinstance(tokenizer, (PreTrainedTokenizer, PreTrainedTokenizerFast)):
        raise ValueError(
            f"`tokenizer` has to be of type {PreTrainedTokenizer.__class__}, but is {type(tokenizer)}"
        )

    self.feature_extractor = feature_extractor
    self.tokenizer = tokenizer
    self.current_processor = self.feature_extractor

def __init__(self, feature_extractor, tokenizer):
    if not isinstance(feature_extractor, FeatureExtractionMixin):
        raise ValueError(
            f"`feature_extractor` has to be of type {FeatureExtractionMixin.__class__}, but is {type(feature_extractor)}"
        )
    if not isinstance(
        tokenizer, (RobertaTokenizer, RobertaTokenizerFast, XLMRobertaTokenizer, XLMRobertaTokenizerFast)
    ):
        raise ValueError(
            f"`tokenizer` has to be of type {RobertaTokenizer.__class__} or {RobertaTokenizerFast.__class__} or {XLMRobertaTokenizer.__class__} or {XLMRobertaTokenizerFast.__class__}, but is {type(tokenizer)}"
        )

    self.feature_extractor = feature_extractor
    self.tokenizer = tokenizer
    self.current_processor = self.feature_extractor

def __init__(self, feature_extractor, tokenizer):
    if not isinstance(feature_extractor, SequenceFeatureExtractor):
        raise ValueError(
            f"`feature_extractor` has to be of type {SequenceFeatureExtractor.__class__}, but is {type(feature_extractor)}"
        )
    if not isinstance(tokenizer, Speech2Text2Tokenizer):
        raise ValueError(
            f"`tokenizer` has to be of type {Speech2Text2Tokenizer.__class__}, but is {type(tokenizer)}"
        )

    self.feature_extractor = feature_extractor
    self.tokenizer = tokenizer
    self.current_processor = self.feature_extractor

def __init__(
    self, feature_extractor: FeatureExtractionMixin, tokenizer: Union[PreTrainedTokenizer, PreTrainedTokenizerFast]
):
    if not isinstance(feature_extractor, FeatureExtractionMixin):
        raise ValueError(
            f"`feature_extractor` has to be of type {FeatureExtractionMixin.__class__}, but is {type(feature_extractor)}"
        )
    if not isinstance(tokenizer, (PreTrainedTokenizer, PreTrainedTokenizerFast)):
        raise ValueError(
            f"`tokenizer` has to be of type `PreTrainedTokenizer` or `PreTrainedTokenizerFast`, but is {type(tokenizer)}"
        )

    self.feature_extractor = feature_extractor
    self.tokenizer = tokenizer
    self.current_processor = self.feature_extractor

def _build_conversation_input_ids(self, conversation: "Conversation") -> List[int]:
    inputs = []
    for is_user, text in conversation.iter_texts():
        if is_user:
            # We need to space prefix as it's being done within blenderbot
            inputs.append(" " + text)
        else:
            # Generated responses should contain them already.
            inputs.append(text)

    full_string = "  ".join(inputs)
    input_ids = self.encode(full_string)
    if len(input_ids) > self.model_max_length:
        input_ids = input_ids[-self.model_max_length :]
        logger.warning(f"Trimmed input from conversation as it was longer than {self.model_max_length} tokens.")
    return input_ids

def _build_conversation_input_ids(self, conversation: "Conversation") -> List[int]:
    inputs = []
    for is_user, text in conversation.iter_texts():
        if is_user:
            # We need to space prefix as it's being done within blenderbot
            inputs.append(" " + text)
        else:
            # Generated responses should contain them already.
            inputs.append(text)

    full_string = "  ".join(inputs)
    input_ids = self.encode(full_string)
    if len(input_ids) > self.model_max_length:
        input_ids = input_ids[-self.model_max_length :]
        logger.warning(f"Trimmed input from conversation as it was longer than {self.model_max_length} tokens.")
    return input_ids

def __init__(self, config):
    super().__init__()

    self.cls_token = nn.Parameter(torch.zeros(1, 1, config.hidden_size))
    if config.use_mask_token:
        self.mask_token = nn.Parameter(torch.zeros(1, 1, config.hidden_size))
    else:
        self.mask_token = None
    self.patch_embeddings = PatchEmbeddings(
        image_size=config.image_size,
        patch_size=config.patch_size,
        num_channels=config.num_channels,
        embed_dim=config.hidden_size,
    )
    num_patches = self.patch_embeddings.num_patches
    if config.use_absolute_position_embeddings:
        self.position_embeddings = nn.Parameter(torch.zeros(1, num_patches + 1, config.hidden_size))
    else:
        self.position_embeddings = None
    self.dropout = nn.Dropout(config.hidden_dropout_prob)

def prune_heads(self, heads): if len(heads) == 0: return heads, index = find_pruneable_heads_and_indices( heads, self.self.num_attention_heads, self.self.attention_head_size, self.pruned_heads )
# Prune linear layers self.self.query = prune_linear_layer(self.self.query, index) self.self.key = prune_linear_layer(self.self.key, index) self.self.value = prune_linear_layer(self.self.value, index) self.output.dense = prune_linear_layer(self.output.dense, index, dim=1)
# Update hyper params and store pruned heads self.self.num_attention_heads = self.self.num_attention_heads - len(heads) self.self.all_head_size = self.self.attention_head_size * self.self.num_attention_heads self.pruned_heads = self.pruned_heads.union(heads)

def prune_heads(self, heads): if len(heads) == 0: return heads, index = find_pruneable_heads_and_indices( heads, self.attention.num_attention_heads, self.attention.attention_head_size, self.pruned_heads )
# Prune linear layers self.attention.query = prune_linear_layer(self.attention.query, index) self.attention.key = prune_linear_layer(self.attention.key, index) self.attention.value = prune_linear_layer(self.attention.value, index) self.output.dense = prune_linear_layer(self.output.dense, index, dim=1)
# Update hyper params and store pruned heads self.attention.num_attention_heads = self.attention.num_attention_heads - len(heads) self.attention.all_head_size = self.attention.attention_head_size * self.attention.num_attention_heads self.pruned_heads = self.pruned_heads.union(heads)

