def __init__(self, projection_dim=512, logit_scale_init_value=2.6592, **kwargs):
    super().__init__(**kwargs)

    if "vision_config" not in kwargs:
        raise ValueError("`vision_config` can not be `None`.")

    if "text_config" not in kwargs:
        raise ValueError("`text_config` can not be `None`.")

    vision_config = kwargs.pop("vision_config")
    text_config = kwargs.pop("text_config")

    vision_model_type = vision_config.pop("model_type")
    text_model_type = text_config.pop("model_type")

    if vision_model_type == "clip":
        self.vision_config = AutoConfig.for_model(vision_model_type, **vision_config).vision_config
    elif vision_model_type == "clip_vision_model":
        self.vision_config = CLIPVisionConfig(**vision_config)
    else:
        self.vision_config = AutoConfig.for_model(vision_model_type, **vision_config)

    self.text_config = AutoConfig.for_model(text_model_type, **text_config)

    self.projection_dim = projection_dim
    self.logit_scale_init_value = logit_scale_init_value

def __init__(self, projection_dim=512, **kwargs): super().__init__(**kwargs)
if "text_config" not in kwargs: raise ValueError("`text_config` can not be `None`.")
if "vision_config" not in kwargs: raise ValueError("`vision_config` can not be `None`.")
text_config = kwargs.pop("text_config") vision_config = kwargs.pop("vision_config")
text_model_type = text_config.pop("model_type") vision_model_type = vision_config.pop("model_type")
from transformers import AutoConfig
self.text_config = AutoConfig.for_model(text_model_type, **text_config)
if vision_model_type == "clip": self.vision_config = AutoConfig.for_model(vision_model_type, **vision_config).vision_config elif vision_model_type == "clip_vision_model": from transformers import CLIPVisionConfig
self.vision_config = CLIPVisionConfig(**vision_config) else: self.vision_config = AutoConfig.for_model(vision_model_type, **vision_config)
self.projection_dim = projection_dim self.initializer_factor = 1.0

def get_mask(input, local_context):
    if not isinstance(local_context, DropoutContext):
        dropout = local_context
        mask = None
    else:
        dropout = local_context.dropout
        dropout *= local_context.scale
        mask = local_context.mask if local_context.reuse_mask else None

    if dropout > 0 and mask is None:
        mask = (1 - torch.empty_like(input).bernoulli_(1 - dropout)).bool()

    if isinstance(local_context, DropoutContext):
        if local_context.mask is None:
            local_context.mask = mask

    return mask, dropout


