def _check_hidden_states_for_generate(
    self, batch_size, hidden_states, min_length, max_length, config, use_cache=False, num_beam_groups=1
):
    self.assertIsInstance(hidden_states, tuple)
    self.assertListEqual(
        [isinstance(iter_hidden_states, list) for iter_hidden_states in hidden_states],
        [True] * len(hidden_states),
    )
    self.assertEqual(len(hidden_states), (max_length - min_length) * num_beam_groups)

    for idx, iter_hidden_states in enumerate(hidden_states):
        seq_len = min_length + idx
        seq_len = config.local_attn_chunk_length * (
            seq_len // config.local_attn_chunk_length + (seq_len % config.local_attn_chunk_length != 0)
        )

        if use_cache:
            seq_len = 1

        expected_shape = (batch_size * num_beam_groups, seq_len, config.hidden_size)
        # check hidden size
        self.assertListEqual(
            [layer_hidden_states.shape for layer_hidden_states in iter_hidden_states],
            [expected_shape] * len(iter_hidden_states),
        )

def _check_hidden_states_for_generate(
    self, batch_size, hidden_states, min_length, max_length, config, use_cache=False, num_beam_groups=1
):
    self.assertIsInstance(hidden_states, tuple)
    self.assertListEqual(
        [isinstance(iter_hidden_states, list) for iter_hidden_states in hidden_states],
        [True] * len(hidden_states),
    )
    self.assertEqual(len(hidden_states), (max_length - min_length) * num_beam_groups)

    for idx, iter_hidden_states in enumerate(hidden_states):
        seq_len = min_length + idx if not use_cache else 1
        seq_len = config.lsh_attn_chunk_length * (
            seq_len // config.lsh_attn_chunk_length + (seq_len % config.lsh_attn_chunk_length != 0)
        )

        if use_cache:
            seq_len = 1

        expected_shape = (batch_size * num_beam_groups, seq_len, config.hidden_size)
        # check hidden size
        self.assertListEqual(
            [layer_hidden_states.shape for layer_hidden_states in iter_hidden_states],
            [expected_shape] * len(iter_hidden_states),
        )

def test_prepare_batch(self): tokenizer = self.tokenizer_class.from_pretrained("microsoft/prophetnet-large-uncased")
src_text = ["A long paragraph for summarization.", "Another paragraph for summarization."] expected_src_tokens = [1037, 2146, 20423, 2005, 7680, 7849, 3989, 1012, 102] batch = tokenizer(src_text, padding=True, return_tensors="pt") self.assertIsInstance(batch, BatchEncoding) result = list(batch.input_ids.numpy()[0]) self.assertListEqual(expected_src_tokens, result)
self.assertEqual((2, 9), batch.input_ids.shape) self.assertEqual((2, 9), batch.attention_mask.shape)

def setUp(self): self.tmpdirname = tempfile.mkdtemp()
# DPR tok vocab_tokens = [ "[UNK]", "[CLS]", "[SEP]", "[PAD]", "[MASK]", "want", "##want", "##ed", "wa", "un", "runn", "##ing", ",", "low", "lowest", ] dpr_tokenizer_path = os.path.join(self.tmpdirname, "dpr_tokenizer") os.makedirs(dpr_tokenizer_path, exist_ok=True) self.vocab_file = os.path.join(dpr_tokenizer_path, DPR_VOCAB_FILES_NAMES["vocab_file"]) with open(self.vocab_file, "w", encoding="utf-8") as vocab_writer: vocab_writer.write("".join([x + "\n" for x in vocab_tokens]))
# BART tok vocab = [ "l", "o", "w", "e", "r", "s", "t", "i", "d", "n", "\u0120", "\u0120l", "\u0120n", "\u0120lo", "\u0120low", "er", "\u0120lowest", "\u0120newer", "\u0120wider", "", ] vocab_tokens = dict(zip(vocab, range(len(vocab)))) merges = ["#version: 0.2", "\u0120 l", "\u0120l o", "\u0120lo w", "e r", ""] self.special_tokens_map = {"unk_token": ""}
bart_tokenizer_path = os.path.join(self.tmpdirname, "bart_tokenizer") os.makedirs(bart_tokenizer_path, exist_ok=True) self.vocab_file = os.path.join(bart_tokenizer_path, BART_VOCAB_FILES_NAMES["vocab_file"]) self.merges_file = os.path.join(bart_tokenizer_path, BART_VOCAB_FILES_NAMES["merges_file"]) with open(self.vocab_file, "w", encoding="utf-8") as fp: fp.write(json.dumps(vocab_tokens) + "\n") with open(self.merges_file, "w", encoding="utf-8") as fp: fp.write("\n".join(merges))

def setUp(self): self.tmpdirname = tempfile.mkdtemp()
# DPR tok vocab_tokens = [ "[UNK]", "[CLS]", "[SEP]", "[PAD]", "[MASK]", "want", "##want", "##ed", "wa", "un", "runn", "##ing", ",", "low", "lowest", ] dpr_tokenizer_path = os.path.join(self.tmpdirname, "dpr_tokenizer") os.makedirs(dpr_tokenizer_path, exist_ok=True) self.vocab_file = os.path.join(dpr_tokenizer_path, DPR_VOCAB_FILES_NAMES["vocab_file"]) with open(self.vocab_file, "w", encoding="utf-8") as vocab_writer: vocab_writer.write("".join([x + "\n" for x in vocab_tokens]))
# BART tok vocab = [ "l", "o", "w", "e", "r", "s", "t", "i", "d", "n", "\u0120", "\u0120l", "\u0120n", "\u0120lo", "\u0120low", "er", "\u0120lowest", "\u0120newer", "\u0120wider", "", ] vocab_tokens = dict(zip(vocab, range(len(vocab)))) merges = ["#version: 0.2", "\u0120 l", "\u0120l o", "\u0120lo w", "e r", ""] self.special_tokens_map = {"unk_token": ""}
bart_tokenizer_path = os.path.join(self.tmpdirname, "bart_tokenizer") os.makedirs(bart_tokenizer_path, exist_ok=True) self.vocab_file = os.path.join(bart_tokenizer_path, BART_VOCAB_FILES_NAMES["vocab_file"]) self.merges_file = os.path.join(bart_tokenizer_path, BART_VOCAB_FILES_NAMES["merges_file"]) with open(self.vocab_file, "w", encoding="utf-8") as fp: fp.write(json.dumps(vocab_tokens) + "\n") with open(self.merges_file, "w", encoding="utf-8") as fp: fp.write("\n".join(merges))
t5_tokenizer = T5Tokenizer(T5_SAMPLE_VOCAB) t5_tokenizer_path = os.path.join(self.tmpdirname, "t5_tokenizer") t5_tokenizer.save_pretrained(t5_tokenizer_path)

def test_encode(self):
    config, inputs_dict = self.model_tester.prepare_config_and_inputs_for_common()

    for model_class in self.all_model_classes:
        with self.subTest(model_class.__name__):
            prepared_inputs_dict = self._prepare_for_class(inputs_dict, model_class)
            model = model_class(config)

            @jax.jit
            def encode_jitted(input_ids, attention_mask=None, **kwargs):
                return model.encode(input_ids=input_ids, attention_mask=attention_mask)

            with self.subTest("JIT Enabled"):
                jitted_outputs = encode_jitted(**prepared_inputs_dict).to_tuple()

            with self.subTest("JIT Disabled"):
                with jax.disable_jit():
                    outputs = encode_jitted(**prepared_inputs_dict).to_tuple()

            self.assertEqual(len(outputs), len(jitted_outputs))
            for jitted_output, output in zip(jitted_outputs, outputs):
                self.assertEqual(jitted_output.shape, output.shape)

def test_jit_compilation(self):
    config, inputs_dict = self.model_tester.prepare_config_and_inputs_for_common()

    for model_class in self.all_model_classes:
        with self.subTest(model_class.__name__):
            prepared_inputs_dict = self._prepare_for_class(inputs_dict, model_class)
            model = model_class(config)

            @jax.jit
            def model_jitted(input_ids, attention_mask=None, **kwargs):
                return model(input_ids=input_ids, attention_mask=attention_mask, **kwargs)

            with self.subTest("JIT Enabled"):
                jitted_outputs = model_jitted(**prepared_inputs_dict).to_tuple()

            with self.subTest("JIT Disabled"):
                with jax.disable_jit():
                    outputs = model_jitted(**prepared_inputs_dict).to_tuple()

            self.assertEqual(len(outputs), len(jitted_outputs))
            for jitted_output, output in zip(jitted_outputs, outputs):

                self.assertEqual(jitted_output.shape, output.shape)

