def __call__(self, trainer):
    self._t += 1

    optimizer = self._get_optimizer(trainer)
    value = self._init * (1 + self._gamma * self._t) ** (-self._power)
    if self._target is not None:
        if self._power < 0:
            # almost same as value = min(value, self._target), but this
            # line supports negative values, too
            if value / self._target > 1:
                value = self._target
        else:
            # ditto
            if value / self._target < 1:
                value = self._target
    self._update_value(optimizer, value)

def __call__(self, trainer):
    self._t += 1

    optimizer = self._get_optimizer(trainer)
    value = self._init * (self._rate ** self._t)
    if self._target is not None:
        if self._rate > 1:
            # almost same as value = min(value, self._target), but this
            # line supports negative values, too
            if value / self._target > 1:
                value = self._target
        else:
            # ditto
            if value / self._target < 1:
                value = self._target
    self._update_value(optimizer, value)

def __call__(self, trainer):
    self._t += 1
    optimizer = self._get_optimizer(trainer)
    value = self._init * self._gamma ** numpy.floor(self._t / self._step)
    if self._target is not None:
        if self._gamma > 1:
            # almost same as value = min(value, self._target), but this
            # line supports negative values, too
            if value / self._target > 1:
                value = self._target
        else:
            # ditto
            if value / self._target < 1:
                value = self._target
    self._update_value(optimizer, value)

def _gather(link, target): size, num = size_num_grads(link)
ptrs = numpy.empty(num, dtype=numpy.uint64) dtypes = numpy.empty(num, dtype=numpy.int8) info = numpy.empty(num + 1, dtype=numpy.int32) info[0] = 0 i = 0 for _, param in sorted(link.namedparams()): if param.size == 0: continue ptrs[i] = 0 # NULL pointer d = getattr(param, target) if d is not None: ptrs[i] = d.data.ptr dtypes[i] = 0 # fp32 if param.dtype == numpy.float16: dtypes[i] = 1 # fp16 info[i + 1] = info[i] + param.size i += 1 info[0] = num
ptrs = cuda.to_gpu(ptrs) dtypes = cuda.to_gpu(dtypes) info = cuda.to_gpu(info)
return _memcpy_gather()(ptrs, dtypes, info, size=size)

def _scatter(link, array, target): size, num = size_num_grads(link)
ptrs = numpy.zeros(num, dtype=numpy.uint64) dtypes = numpy.zeros(num, dtype=numpy.int8) info = numpy.zeros(num + 1, dtype=numpy.int32) info[0] = 0 i = 0 for _, param in sorted(link.namedparams()): if param.size == 0: continue ptrs[i] = 0 # NULL pointer d = getattr(param, target) if d is None: d = cuda.cupy.zeros(param.shape, dtype=param.dtype) setattr(param, target, d) ptrs[i] = d.data.ptr dtypes[i] = 0 # fp32 if param.dtype == numpy.float16: dtypes[i] = 1 # fp16 info[i + 1] = info[i] + param.size i += 1 if i != num: raise() info[0] = num
ptrs = cuda.to_gpu(ptrs) dtypes = cuda.to_gpu(dtypes) info = cuda.to_gpu(info)
return _memcpy_scatter()(ptrs, dtypes, info, array, size=size)

# to the static chain.
def _unflatten_args(xs, inds):
    ys = []
    for ind in inds:
        code = ind[0]
        if code == 's':
            return xs[0]
        elif code == 'i':
            i_start, i_end, sub_inds = ind[1:]
            y = _unflatten_args(xs[i_start:i_end], sub_inds)
        else:
            i = ind[1]
            y = xs[i]
        ys.append(y)

def _preprocess(self):
    if self.xp == numpy:
        start = _get_time()
        self._running_stack.append(start)
    else:
        start = cuda.Event()
        stop = cuda.Event()
        start.record()
        self._running_stack.append((start, stop))
    self._depth += 1

self.sampling_ratio = sampling_ratio
def check_type_forward(self, in_types): type_check.expect(in_types.size() == 3)
x_type, roi_type, roi_index_type = in_types type_check.expect( x_type.dtype == numpy.float32, x_type.ndim == 4, roi_type.dtype == numpy.float32, roi_type.ndim == 2, roi_type.shape[1] == 4, roi_index_type.dtype == numpy.int32, roi_index_type.ndim == 1, roi_type.shape[0] == roi_index_type.shape[0],

def check_type_forward(self, in_types):
    type_check.expect(in_types.size() == 1)
    x_type = in_types[0]
    type_check.expect(
        x_type.dtype.kind == 'f',
        x_type.shape[1] % self.groups == 0,
    )
    type_check.expect(
        x_type.ndim >= 2,
    )

def __init__(self, eps=2e-5, mean=None, var=None, decay=0.9, axis=None,
             impl_selector=_impl_selector):
    self.running_mean = mean
    self.running_var = var

    # Note: cuDNN requires that eps be greater than or equals to
    # CUDNN_BN_MIN_EPSILON. Otherwise, an error will occur.
    # See CUDNN_BN_MIN_EPSILON value in cudnn.h to verify minimum allowable
    # value.
    self.eps = eps
    if chainer.should_use_cudnn('>=auto'):
        if eps < libcudnn.CUDNN_BN_MIN_EPSILON:
            raise RuntimeError(
                'cuDNN does not allow an eps value '
                'less than {}.'.format(libcudnn.CUDNN_BN_MIN_EPSILON))
    self.decay = decay
    if isinstance(axis, collections_abc.Sequence):
        for i in range(1, len(axis)):
            if axis[i - 1] >= axis[i]:
                msg = 'numbers in axis must be sorted in ascending order'
                raise RuntimeError(msg)
    elif isinstance(axis, six.integer_types):
        axis = axis,
    elif axis is not None:
        raise RuntimeError('axis must be int, tuple of int or None')
    self.axis = axis

    self._impl_selector = impl_selector

def __init__(self, eps=2e-5, axis=None):
    # Note: cuDNN requires that eps be greater than or equals to
    # CUDNN_BN_MIN_EPSILON. Otherwise, an error will occur.
    # See CUDNN_BN_MIN_EPSILON value in cudnn.h to verify minimum allowable
    # value.
    self.eps = eps
    if chainer.should_use_cudnn('>=auto'):
        if eps < libcudnn.CUDNN_BN_MIN_EPSILON:
            raise RuntimeError(
                'cuDNN does not allow an eps value '
                'less than {}.'.format(libcudnn.CUDNN_BN_MIN_EPSILON))
    if isinstance(axis, collections_abc.Sequence):
        for i in range(1, len(axis)):
            if axis[i - 1] >= axis[i]:
                msg = 'numbers in axis must be sorted in ascending order'
                raise RuntimeError(msg)
    elif isinstance(axis, six.integer_types):
        axis = axis,
    elif axis is not None:
        raise RuntimeError('axis must be int, tuple of int or None')
    self.axis = axis

