def check_type_forward(self, in_types):
    type_check._argname(in_types, ('x',))
    type_check.expect(in_types[0].dtype.kind == 'f')

    if self.axis is not None:
        for axis in self.axis:
            if axis >= 0:
                type_check.expect(
                    axis < in_types[0].ndim,
                )
            else:
                type_check.expect(
                    -axis - 1 < in_types[0].ndim,
                )

