    except KeyError:
        # guess previous_epoch_detail for older version
        self._previous_epoch_detail = self.epoch + \
            (self.current_position - self.batch_size) / len(self.dataset)
        if self.epoch_detail > 0:
            self._previous_epoch_detail = max(
                self._previous_epoch_detail, 0.)
        else:
            self._previous_epoch_detail = -1.

def serialize(self, serializer):
    current_position = serializer('current_position',
                                  self.current_position)
    epoch = serializer('epoch', self.epoch)
    is_new_epoch = serializer('is_new_epoch', self.is_new_epoch)
    order = self._state.order.copy()
    try:
        serializer('order', order)
    except KeyError:
        serializer('_order', order)
    self._reset_state(current_position, epoch, is_new_epoch, order)
    try:
        self._previous_epoch_detail = serializer(
            'previous_epoch_detail', self._previous_epoch_detail)
    except KeyError:
        # guess previous_epoch_detail for older version
        self._previous_epoch_detail = self.epoch + \
            (self.current_position - self.batch_size) / self._epoch_size
        if self.epoch_detail > 0:
            self._previous_epoch_detail = max(
                self._previous_epoch_detail, 0.)
        else:
            self._previous_epoch_detail = -1.

def serialize(self, serializer):
    current_position = serializer('current_position',
                                  self.current_position)
    epoch = serializer('epoch', self.epoch)
    is_new_epoch = serializer('is_new_epoch', self.is_new_epoch)
    order = self._state.order
    if order is not None:
        try:
            serializer('order', order)
        except KeyError:
            serializer('_order', order)
    self._state = _statemachine.IteratorState(
        current_position, epoch, is_new_epoch, order)
    try:
        self._previous_epoch_detail = serializer(
            'previous_epoch_detail', self._previous_epoch_detail)
    except KeyError:
        # guess previous_epoch_detail for older version
        self._previous_epoch_detail = self.epoch + \
            (self.current_position - self.batch_size) / self._epoch_size
        if self.epoch_detail > 0:
            self._previous_epoch_detail = max(
                self._previous_epoch_detail, 0.)
        else:
            self._previous_epoch_detail = -1.

def run_test_backward(self, backend_config):
    # Runs the backward test.

    if self.skip_backward_test:
        raise unittest.SkipTest('skip_backward_test is set')

    # avoid cyclic import
    from chainer import gradient_check

    self.backend_config = backend_config
    self.test_name = 'test_backward'
    self.before_test(self.test_name)

    def f(*args):
        return self._forward(args, backend_config)

    def do_check():
        inputs = self._generate_inputs()
        outputs = self._forward_expected(inputs)
        grad_outputs = self._generate_grad_outputs(outputs)

        inputs = backend_config.get_array(inputs)
        grad_outputs = backend_config.get_array(grad_outputs)
        inputs = self._to_noncontiguous_as_needed(inputs)
        grad_outputs = self._to_noncontiguous_as_needed(grad_outputs)

        with FunctionTestError.raise_if_fail(
                'backward is not implemented correctly'):
            gradient_check.check_backward(
                f, inputs, grad_outputs, dtype=self.numerical_grad_dtype,
                detect_nondifferentiable=self.dodge_nondifferentiable,
                **self.check_backward_options)

    if self.dodge_nondifferentiable:
        while True:
            try:
                do_check()
            except gradient_check.NondifferentiableError:
                continue
            else:
                break
    else:
        do_check()

def run_test_double_backward(self, backend_config): # Runs the double-backward test.
if self.skip_double_backward_test: raise unittest.SkipTest('skip_double_backward_test is set')
# avoid cyclic import from chainer import gradient_check
self.backend_config = backend_config self.test_name = 'test_double_backward' self.before_test(self.test_name)
def f(*args): return self._forward(args, backend_config)
def do_check(): inputs = self._generate_inputs() outputs = self._forward_expected(inputs) grad_outputs = self._generate_grad_outputs(outputs) grad_grad_inputs = self._generate_grad_grad_inputs(inputs)
# Drop ggx corresponding to non-differentiable inputs. # Generated `grad_grad_inputs`, the upstream gradients for the # double backward test, may contain `None` for omitted gradients. # These must be propagated to the gradient check. grad_grad_inputs = [ ggx for ggx in grad_grad_inputs if (ggx is None or ggx.dtype.kind == 'f')]
inputs = backend_config.get_array(inputs) grad_outputs = backend_config.get_array(grad_outputs) grad_grad_inputs = backend_config.get_array(grad_grad_inputs) inputs = self._to_noncontiguous_as_needed(inputs) grad_outputs = self._to_noncontiguous_as_needed(grad_outputs) grad_grad_inputs = ( self._to_noncontiguous_as_needed(grad_grad_inputs))
with backend_config: with FunctionTestError.raise_if_fail( 'double backward is not implemented correctly'): gradient_check.check_double_backward( f, inputs, grad_outputs, grad_grad_inputs, dtype=self.numerical_grad_dtype, detect_nondifferentiable=self.dodge_nondifferentiable, **self.check_double_backward_options)
if self.dodge_nondifferentiable: while True: try: do_check() except gradient_check.NondifferentiableError: continue else: break else: do_check()

