link2 = convolution_nd.ConvolutionND.from_params( link1.W, link1.b, stride=self.stride, pad=self.pad, groups=self.groups) assert link2.W.shape == link1.W.shape assert link2.b.shape == link1.b.shape assert link2.stride == link1.stride assert link2.pad == link1.pad

