def check_type_mismatch(self, x_data, retain):
    xp = backend.get_array_module(x_data)

    class DummyFunction(chainer.Function):
        label = 'dummy_function'

        def forward(self, inputs):
            if not retain:
                self.retain_inputs(())
            return xp.array(1, np.float32),

        def backward(self, inputs, grads):
            return [1]

    x = chainer.Variable(x_data)
    y = DummyFunction()(x)
    with six.assertRaisesRegex(self, TypeError, 'dummy_function'):
        y.backward()

def check_dtype_mismatch(self, x_data, retain):
    xp = backend.get_array_module(x_data)

    class DummyFunction(chainer.Function):
        label = 'dummy_function'

        def forward(self, inputs):
            if not retain:
                self.retain_inputs(())
            return xp.array(1, np.float32),

        def backward(self, inputs, grads):
            return xp.array([1], np.int32),

    x = chainer.Variable(x_data)
    y = DummyFunction()(x)
    with six.assertRaisesRegex(self, TypeError, 'dummy_function'):
        y.backward()

def check_shape_mismatch(self, x_data, retain):
    xp = backend.get_array_module(x_data)

    class DummyFunction(chainer.Function):
        label = 'dummy_function'

        def forward(self, inputs):
            if not retain:
                self.retain_inputs(())
            return xp.array(1, np.float32),

        def backward(self, inputs, grads):
            return xp.array([1, 2], np.float32),

    x = chainer.Variable(x_data)
    y = DummyFunction()(x)
    with six.assertRaisesRegex(self, ValueError, 'dummy_function'):
        y.backward()

def test_no_grads_option(self, backend_config): if backend_config.use_chainerx: raise unittest.SkipTest( 'gradient_check does not support no_grad option for ChainerX') x1 = backend_config.get_array(numpy.array([2], dtype='f')) # grad check for this is skipped x2 = backend_config.get_array(numpy.array([3], dtype='f')) g1 = backend_config.get_array(numpy.array([5], dtype='f'))
def f(x, y): y_array = y.array if (backend_config.xp is chainerx and isinstance(y_array, chainerx.ndarray)): y_array = y_array.as_grad_stopped() s = x + y_array return s,
self.assertRaises( RuntimeError, # backward computes x1.grad gradient_check.check_backward, f, (x1, x2), g1, no_grads=[True, True])

def test_const_input(self, backend_config): x1 = backend_config.get_array(numpy.array([2], dtype='f')) # grad check for this is skipped x2 = backend_config.get_array(numpy.array([3], dtype='f')) g1 = backend_config.get_array(numpy.array([5], dtype='f'))
def f(x, y): y_array = y.array if (backend_config.xp is chainerx and isinstance(y_array, chainerx.ndarray)): y_array = y_array.as_grad_stopped() s = x + y_array return s,
self.assertRaises( AssertionError, # numerical backward to x2 is nonzero gradient_check.check_backward, f, (x1, x2), g1, no_grads=[False, False])

def _broken_func_1(self):
    class Broken(chainer.Function):
        def forward(self, inputs):
            x, = inputs
            return (x * x),

        def backward(self, inputs, grad_outputs):
            x, = inputs
            gy, = grad_outputs
            return 3 * x * gy,

    return Broken()

def _broken_func_2(self):
    class Broken(chainer.FunctionNode):
        def forward(self, inputs):
            x, = inputs
            self.retain_inputs((0,))
            return (x * x),

        def backward(self, indexes, grad_outputs):
            x, = self.get_retained_inputs()
            gy, = grad_outputs
            return 3 * x * gy,

    return Broken()

def _broken_func_3(self):
    class Broken(chainer.FunctionNode):
        def forward(self, inputs):
            x, = inputs
            self.retain_inputs((0,))
            return (x * x),

        def backward(self, indexes, grad_outputs):
            x, = self.get_retained_inputs()
            gy, = grad_outputs
            gx1 = 2 * x * gy
            gx2 = 3 * x * gy
            return (gx1, gx2)

    return Broken()

def test_hooks(self): w_pre = np.copy(self.target.w.data) h_pre = WeightSaveHook() h_post = WeightSaveHook() self.create() self.optimizer.add_hook(h_pre, timing='pre') self.optimizer.add_hook(h_post, name='WeightSaveHookPost', timing='post')
x = chainer.Variable(np.array(5., dtype=np.float32)) self.optimizer.update(self.target, x) w_post = np.copy(self.target.w.data)
self.assertEqual(w_pre, h_pre.value) self.assertEqual(w_post, h_post.value) self.assertNotEqual(h_pre.value, h_post.value)

def test_hooks_auto(self): w_pre = np.copy(self.target.w.data) h_pre = WeightSaveHook() h_pre.timing = 'pre' h_post = WeightSaveHook() h_post.timing = 'post' self.create() self.optimizer.add_hook(h_pre, timing='auto') self.optimizer.add_hook(h_post, name='WeightSaveHookPost', timing='auto')
x = chainer.Variable(np.array(5., dtype=np.float32)) self.optimizer.update(self.target, x) w_post = np.copy(self.target.w.data)
self.assertEqual(w_pre, h_pre.value) self.assertEqual(w_post, h_post.value) self.assertNotEqual(h_pre.value, h_post.value)

def test_new_pickle(self): self.create() pickled_opt = pickle.dumps(self.optimizer)
x = chainer.Variable(np.array(5., dtype=np.float32)) self.optimizer.update(self.target, x) w_post = np.copy(self.target.w.data) # Pickle has saved a copy of the target opt = pickle.loads(pickled_opt) opt.update(opt.target, x) pickled_w_post = np.copy(opt.target.w.data)
self.assertEqual(w_post, pickled_w_post)

def test_from_chx(self, backend_config):
    self.set_count_parameters()
    self.c2.to_device(backend_config.device)
    self.c2.from_chx()

    src_device = backend_config.device
    if src_device.xp is chainerx:
        expected_device = src_device.fallback_device
    else:
        expected_device = src_device
    self.check_expected_device(expected_device)

