self.assertIsInstance(y.creator_node.outputs, tuple)

def test_unchain(self): f, _x1, _y1 = self._get_f() y1, y2 = f.outputs f.unchain()
# As _y1 is alive, this weak ref is also alive y1_ref = y1() self.assertIsNotNone(y1_ref) self.assertIsNone(y1_ref.creator) # This weak ref is dead by unchain y2_ref = y2() self.assertIsNone(y2_ref)
self.assertIsNone(f.inputs)

def test_unchain(self): self.setup_cpu() f, _x1, _y1 = self._get_f() y1, y2 = f.outputs f.unchain()
# As _y1 is alive, this weak ref is also alive y1_ref = y1() self.assertIsNotNone(y1_ref) self.assertIsNone(y1_ref.creator) # This weak ref is dead by unchain y2_ref = y2() self.assertIsNone(y2_ref)
self.assertIsNone(f.inputs)

def test_force_backprop_mode(self): with chainer.no_backprop_mode(): with chainer.force_backprop_mode(): y = self.x + 1 self.assertTrue(y.creator_node is not None)
y = self.x + 1 self.assertTrue(y.creator_node is not None)
with chainer.force_backprop_mode(): y = self.x + 1 self.assertTrue(y.creator_node is not None)

def test_force_backprop_mode(self): with chainer.no_backprop_mode(): with chainer.force_backprop_mode(): y = self.x + 1 self.assertTrue(y.creator_node is not None)
y = self.x + 1 self.assertTrue(y.creator_node is not None)
with chainer.force_backprop_mode(): y = self.x + 1 self.assertTrue(y.creator_node is not None)

def call_func_function(self, backend_config, x1, x2, x3): forward_xp, backward_xp = _get_expected_xp(backend_config, True)
class Func(chainer.Function): def __init__(self): self.array_init = backend_config.device.send( numpy.array([3], numpy.float32))
def forward(self, inputs): # Inputs assert isinstance(inputs, tuple) # x1, x3: float32 # x2: int32 x1, x2, x3 = inputs assert isinstance(x1, forward_xp.ndarray) assert isinstance(x2, forward_xp.ndarray) assert isinstance(x3, forward_xp.ndarray)
# attribute fallback assert isinstance(self.array_init, forward_xp.ndarray) self.array_forward = forward_xp.array([2], numpy.float32) assert isinstance(self.array_forward, forward_xp.ndarray)
y1 = x2 - 1 # int32 y2 = x1 * x3 + x2.astype(x1.dtype) y3 = x1 + x3 self.retain_inputs((0, 2)) self.retain_outputs((0, 1,)) return y1, y2, y3
def backward(self, inputs, grad_outputs):
# Retained inputs assert isinstance(inputs, tuple) x1, x2, x3 = inputs assert isinstance(x1, backward_xp.ndarray) assert x2 is None # not retained assert isinstance(x3, backward_xp.ndarray)
# Output gradients assert isinstance(grad_outputs, tuple) gy1, gy2, gy3 = grad_outputs assert gy1 is None # y1 is int32 # y3 is disconnected # TODO(niboshi): Expression after "or" is workaround for # chainerx. ChainerX backward should return None for # disconnected output and this workaround should be removed. assert (gy3 is None or (float(gy3.max()) == 0 and float((-gy3).max()) == 0))
# Retained outputs output_data = self.output_data assert isinstance(output_data, tuple) y1, y2, y3 = output_data assert isinstance(y1, backward_xp.ndarray) assert isinstance(y2, backward_xp.ndarray) assert y3 is None
# attribute fallback assert isinstance(self.array_init, backward_xp.ndarray) assert isinstance(self.array_forward, backward_xp.ndarray) self.array_backward = backward_xp.array([4], numpy.float32) assert isinstance(self.array_backward, backward_xp.ndarray)
gx1 = x3 * gy2 # + gy3 gx2 = None gx3 = x1 * gy2 # + gy3 return gx1, gx2, gx3
return Func()(x1, x2, x3)

def call_func_function_node(self, backend_config, x1, x2, x3): forward_xp, backward_xp = _get_expected_xp(backend_config, False)
class Func(chainer.FunctionNode): def __init__(self): self.array_init = backend_config.device.send( numpy.array([3], numpy.float32))
def forward(self, inputs):
# Inputs # x1, x3: float32 # x2: int32 x1, x2, x3 = inputs assert isinstance(x1, forward_xp.ndarray) assert isinstance(x2, forward_xp.ndarray) assert isinstance(x3, forward_xp.ndarray)
# attribute fallback assert isinstance(self.array_init, forward_xp.ndarray) self.array_forward = forward_xp.array([2], numpy.float32) assert isinstance(self.array_forward, forward_xp.ndarray)
y1 = x2 - 1 # int32 y2 = x1 * x3 + x2.astype(x1.dtype) y3 = x1 + x3 self.retain_inputs((0, 2)) self.retain_outputs((0, 1,)) return y1, y2, y3
def backward(self, input_indexes, grad_outputs):
# Input indexes assert isinstance(input_indexes, tuple) assert input_indexes == (0, 2)
# Retained inputs retained_inputs = self.get_retained_inputs() assert isinstance(retained_inputs, tuple) x1, x3 = retained_inputs assert isinstance(x1.array, backward_xp.ndarray) assert isinstance(x3.array, backward_xp.ndarray)
# Output gradients assert isinstance(grad_outputs, tuple) gy1, gy2, gy3 = grad_outputs assert gy1 is None # y1 is int32 assert isinstance(gy2.array, backward_xp.ndarray) # y3 is disconnected # TODO(niboshi): Expression after "or" is workaround for # chainerx. ChainerX backward should return None for # disconnected output and this workaround should be removed. assert (gy3 is None or (float(gy3.array.max()) == 0 and float((-gy3.array).max()) == 0))
# Retained outputs retained_outputs = self.get_retained_outputs() assert isinstance(retained_outputs, tuple) y1, y2, = retained_outputs assert isinstance(y1.array, backward_xp.ndarray) assert isinstance(y2.array, backward_xp.ndarray)
# attribute fallback assert isinstance(self.array_init, backward_xp.ndarray) assert isinstance(self.array_forward, backward_xp.ndarray) self.array_backward = backward_xp.array([4], numpy.float32) assert isinstance(self.array_backward, backward_xp.ndarray)
gx1 = x3 * gy2 # + gy3 gx2 = None gx3 = x1 * gy2 # + gy3 return gx1, gx2, gx3
return Func().apply((x1, x2, x3))

def test_numpy_scalar(self):
    dtype = self.dtype
    if dtype is numpy.bool_:
        x = dtype(True)
    elif issubclass(dtype, numpy.complex_):
        x = dtype(3.2 - 2.4j)
    elif issubclass(dtype, numpy.integer):
        x = dtype(3)
    elif issubclass(dtype, numpy.floating):
        x = dtype(3.2)
    else:
        assert False

    y = cuda.to_cpu(x)
    assert isinstance(y, numpy.ndarray)
    assert y.shape == ()
    assert y.dtype == dtype
    assert y == x

def test_numpy_scalar(self):
    dtype = self.dtype
    if dtype is numpy.bool_:
        x = dtype(True)
    elif issubclass(dtype, numpy.complex_):
        x = dtype(3.2 - 2.4j)
    elif issubclass(dtype, numpy.integer):
        x = dtype(3)
    elif issubclass(dtype, numpy.floating):
        x = dtype(3.2)
    else:
        assert False

    y = cuda.to_gpu(x)
    assert isinstance(y, cuda.ndarray)
    assert y.shape == ()
    assert y.dtype == dtype
    assert y == x

