def test_add_two_extensions_default_priority(self):
    self.called_order = []

    @training.make_extension(trigger=(1, 'epoch'))
    def dummy_extension_1(trainer):
        self.called_order.append(1)

    @training.make_extension(trigger=(1, 'epoch'))
    def dummy_extension_2(trainer):
        self.called_order.append(2)

    self.trainer.extend(dummy_extension_1)
    self.trainer.extend(dummy_extension_2)
    self.trainer.run()
    self.assertEqual(self.called_order, [1, 2])

def test_add_two_extensions_specific_priority(self):
    self.called_order = []

    @training.make_extension(trigger=(1, 'epoch'), priority=50)
    def dummy_extension_1(trainer):
        self.called_order.append(1)

    @training.make_extension(trigger=(1, 'epoch'), priority=100)
    def dummy_extension_2(trainer):
        self.called_order.append(2)

    self.trainer.extend(dummy_extension_1)
    self.trainer.extend(dummy_extension_2)
    self.trainer.run()
    self.assertEqual(self.called_order, [2, 1])

def test_exception_handler(self):
ext = ErrorHandlingExtension() self.trainer.extend(ext, trigger=(1, 'iteration'), priority=1) self.assertFalse(ext.is_error_handled)
d = {}
def exception_handler(trainer, exp, tb): d['called'] = True
@training.make_extension(trigger=(1, 'iteration'), priority=100, on_error=exception_handler) def exception_raiser(trainer): raise TheOnlyError() self.trainer.extend(exception_raiser)
dummy_extension = DummyExtension(self) self.trainer.extend(dummy_extension)
with self.assertRaises(TheOnlyError): self.trainer.run()
self.assertTrue(d['called']) self.assertTrue(ext.is_error_handled) self.assertTrue(dummy_extension.is_finalized)

def test_exception_in_exception_handler(self):
ext = ErrorHandlingExtension() self.trainer.extend(ext, trigger=(1, 'iteration'), priority=1) self.assertFalse(ext.is_error_handled)
def exception_handler(trainer, exp, tb): raise ValueError('hogehoge from exception handler')
@training.make_extension(trigger=(1, 'iteration'), priority=100, on_error=exception_handler) def exception_raiser(trainer): raise TheOnlyError() self.trainer.extend(exception_raiser)
dummy_extension = DummyExtension(self) self.trainer.extend(dummy_extension)
with self.assertRaises(TheOnlyError): self.trainer.run()
self.assertTrue(ext.is_error_handled) self.assertTrue(dummy_extension.is_finalized)

