def generate_grad_outputs(self, outputs_template):
    grad_out = []
    c = outputs_template[0]
    h = outputs_template[1]

    c_shape = c.shape
    h_shape = h.shape
    if self.grad_outputs[0] is True:
        grad_out.append(_shaped_random(c_shape, c.dtype))
    else:
        grad_out.append(None)

    if self.grad_outputs[1] is True:
        grad_out.append(_shaped_random(h_shape, h.dtype))
    else:
        grad_out.append(None)
    return tuple(grad_out)

def generate_grad_grad_inputs(self, inputs_template):
    grad_grad_in = []
    c = inputs_template[0]
    x = inputs_template[1]

    c_shape = c.shape
    x_shape = x.shape
    if self.grad_grad_inputs[0] is True:
        grad_grad_in.append(_shaped_random(c_shape, c.dtype))
    else:
        grad_grad_in.append(None)

    if self.grad_grad_inputs[1] is True:
        grad_grad_in.append(_shaped_random(x_shape, x.dtype))
    else:
        grad_grad_in.append(None)
    return tuple(grad_grad_in)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = numpy.float32

    h = array(h_shape, dtype)
    c = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = []
    for b in range(len(self.batches)):
        xs.append(array((self.batches[b], in_size), dtype))

    def w_in(i, j):
        return in_size if i == 0 and j < 4 else out_size

    inputs = []
    inputs.append(h)
    inputs.append(c)
    for i in range(len(self.batches)):
        inputs.append(xs[i])
    for n in range(self.n_layers):
        for i in range(8):
            inputs.append(array((out_size, w_in(n, i)), dtype))
        for i in range(8):
            inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = self.dtype

    h = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        return in_size if i == 0 and j < 1 else out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])
    for n in range(self.n_layers):
        for i in range(2):
            inputs.append(array((out_size, w_in(n, i)), dtype))
        for i in range(2):
            inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = numpy.float32

    h = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        return in_size if i == 0 and j < 3 else out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])
    for n in range(self.n_layers):
        for i in range(6):
            inputs.append(array((out_size, w_in(n, i)), dtype))
        for i in range(6):
            inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers * 2, self.batches[0], self.hidden_size)
    dtype = numpy.float32

    h = array(h_shape, dtype)
    c = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = []
    for b in range(len(self.batches)):
        xs.append(array((self.batches[b], in_size), dtype))

    def w_in(i, j):
        if i == 0 and j < 4:
            return in_size
        elif i > 0 and j < 4:
            return out_size * 2
        else:
            return out_size

    inputs = []
    inputs.append(h)
    inputs.append(c)
    for i in range(len(self.batches)):
        inputs.append(xs[i])
    for n in range(self.n_layers):
        for direction in (0, 1):
            for i in range(8):
                inputs.append(array((out_size, w_in(n, i)), dtype))
            for i in range(8):
                inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = self.in_dtypes[0]

    h = array_utils.uniform(h_shape, dtype)

    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array_utils.uniform((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        return in_size if i == 0 and j < 1 else out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for i in range(2):
            inputs.append(array_utils.uniform(
                (out_size, w_in(n, i)), dtype))
        for i in range(2):
            inputs.append(array_utils.uniform((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = self.in_dtypes[0]

    h = array_utils.uniform(h_shape, dtype)

    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array_utils.uniform((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        return in_size if i == 0 and j < 3 else out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for i in range(6):
            inputs.append(array_utils.uniform(
                (out_size, w_in(n, i)), dtype))
        for i in range(6):
            inputs.append(array_utils.uniform((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers * 2, self.batches[0], self.hidden_size)
    dtype = self.in_dtypes[0]

    h = array_utils.uniform(h_shape, dtype)
    c = array_utils.uniform(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array_utils.uniform((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        if i == 0 and j < 4:
            return in_size
        elif i > 0 and j < 4:
            return out_size * 2
        else:
            return out_size

    inputs = []
    inputs.append(h)
    inputs.append(c)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for direction in (0, 1):
            for i in range(8):
                inputs.append(array_utils.uniform(
                    (out_size, w_in(n, i)), dtype))
            for i in range(8):
                inputs.append(array_utils.uniform((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers * 2, self.batches[0], self.hidden_size)
    dtype = self.dtype

    h = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        if i == 0 and j < 1:
            return in_size
        elif i > 0 and j < 1:
            return out_size * 2
        else:
            return out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for direction in (0, 1):
            for i in range(2):
                inputs.append(array((out_size, w_in(n, i)), dtype))
            for i in range(2):
                inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers * 2, self.batches[0], self.hidden_size)
    dtype = numpy.float32

    h = array(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        if i == 0 and j < 3:
            return in_size
        elif i > 0 and j < 3:
            return out_size * 2
        else:
            return out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for direction in (0, 1):
            for i in range(6):
                inputs.append(array((out_size, w_in(n, i)), dtype))
            for i in range(6):
                inputs.append(array((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers, self.batches[0], self.hidden_size)
    dtype = self.in_dtypes[0]

    h = array_utils.uniform(h_shape, dtype)
    c = array_utils.uniform(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array_utils.uniform((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        return in_size if i == 0 and j < 4 else out_size

    inputs = []
    inputs.append(h)
    inputs.append(c)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for i in range(8):
            inputs.append(array_utils.uniform(
                (out_size, w_in(n, i)), dtype))
        for i in range(8):
            inputs.append(array_utils.uniform((out_size,), dtype))
    return tuple(inputs)

def generate_inputs(self):
    h_shape = (self.n_layers * 2, self.batches[0], self.hidden_size)
    dtype = self.in_dtypes[0]

    h = array_utils.uniform(h_shape, dtype)
    in_size = self.input_size
    out_size = self.hidden_size
    xs = [array_utils.uniform((self.batches[b], in_size), dtype)
          for b in range(len(self.batches))]

    def w_in(i, j):
        if i == 0 and j < 3:
            return in_size
        elif i > 0 and j < 3:
            return out_size * 2
        else:
            return out_size

    inputs = []
    inputs.append(h)
    for i in range(len(self.batches)):
        inputs.append(xs[i])

    for n in range(self.n_layers):
        for direction in (0, 1):
            for i in range(6):
                inputs.append(array_utils.uniform(
                    (out_size, w_in(n, i)), dtype))
            for i in range(6):
                inputs.append(array_utils.uniform((out_size,), dtype))
    return tuple(inputs)

def forward(self, inputs, device):
    h, c, ws, bs, xs = self.process_inputs(inputs)
    if h.array.dtype == numpy.float64:
        with chainer.using_config('use_cudnn', 'never'):
            out = F.n_step_lstm(self.n_layers, 0.0, h, c, ws, bs, xs)
    else:
        out = F.n_step_lstm(self.n_layers, 0.0, h, c, ws, bs, xs)
    rets = []
    rets.append(out[0])
    rets.append(out[1])
    for i in range(len(out[2])):
        rets.append(out[2][i])
    return tuple(rets)

def forward(self, inputs, device):
    h, c, ws, bs, xs = self.process_inputs(inputs)
    if h.array.dtype == numpy.float64:
        with chainer.using_config('use_cudnn', 'never'):
            out = F.n_step_bilstm(self.n_layers, 0.0, h, c, ws, bs, xs)
    else:
        out = F.n_step_bilstm(self.n_layers, 0.0, h, c, ws, bs, xs)

    rets = []
    rets.append(out[0])
    rets.append(out[1])
    for i in range(len(out[2])):
        rets.append(out[2][i])
    return tuple(rets)

def forward(self, inputs, device):
    h, ws, bs, xs = self.process_inputs(inputs)
    if h.array.dtype == numpy.float64:
        with chainer.using_config('use_cudnn', 'never'):
            out = F.n_step_gru(self.n_layers, 0.0, h, ws, bs, xs)
    else:
        out = F.n_step_gru(self.n_layers, 0.0, h, ws, bs, xs)

    rets = []
    rets.append(out[0])
    for i in range(len(out[1])):
        rets.append(out[1][i])
    return tuple(rets)

def forward(self, inputs, device):
    h, ws, bs, xs = self.process_inputs(inputs)
    if h.array.dtype == numpy.float64:
        with chainer.using_config('use_cudnn', 'never'):
            out = F.n_step_bigru(self.n_layers, 0.0, h, ws, bs, xs)
    else:
        out = F.n_step_bigru(self.n_layers, 0.0, h, ws, bs, xs)
    rets = []
    rets.append(out[0])
    for i in range(len(out[1])):
        rets.append(out[1][i])
    return tuple(rets)

def check_backward(self, inputs, grad_outputs, backend_config): inputs = backend_config.get_array(inputs) grad_outputs = backend_config.get_array(grad_outputs) if not self.c_contiguous: with backend_config: inputs = _as_noncontiguous_array(inputs) grad_outputs = _as_noncontiguous_array(grad_outputs)
def f(*inputs): y = functions.batch_normalization( *inputs, **self.bn_options) return y,
with backend_config: gradient_check.check_backward( f, inputs, grad_outputs, **self.check_backward_options)

def check_backward(self, inputs, grad_outputs, backend_config): inputs = backend_config.get_array(inputs) grad_outputs = backend_config.get_array(grad_outputs) if not self.c_contiguous: with backend_config: inputs = _as_noncontiguous_array(inputs) grad_outputs = _as_noncontiguous_array(grad_outputs)
def f(*inputs): y = functions.fixed_batch_normalization(*inputs, eps=self.eps) return y,
with backend_config: gradient_check.check_backward( f, inputs, grad_outputs, **self.check_backward_options)

def check_double_backward( self, inputs, grad_outputs, grad_grad_inputs, backend_config): inputs = backend_config.get_array(inputs) grad_outputs = backend_config.get_array(grad_outputs) grad_grad_inputs = backend_config.get_array(grad_grad_inputs) if not self.c_contiguous: with backend_config: inputs = _as_noncontiguous_array(inputs) grad_outputs = _as_noncontiguous_array(grad_outputs) grad_grad_inputs = _as_noncontiguous_array(grad_grad_inputs)
def f(*inputs): return functions.batch_normalization( *inputs, **self.bn_options)
with backend_config: gradient_check.check_double_backward( f, inputs, grad_outputs, grad_grad_inputs, **self.check_double_backward_options)

def check_double_backward( self, inputs, grad_outputs, grad_grad_inputs, backend_config): inputs = backend_config.get_array(inputs) grad_outputs = backend_config.get_array(grad_outputs) grad_grad_inputs = backend_config.get_array(grad_grad_inputs) if not self.c_contiguous: with backend_config: inputs = _as_noncontiguous_array(inputs) grad_outputs = _as_noncontiguous_array(grad_outputs) grad_grad_inputs = _as_noncontiguous_array(grad_grad_inputs)
def f(*inputs): return functions.fixed_batch_normalization(*inputs, eps=self.eps)
with backend_config: gradient_check.check_double_backward( f, inputs, grad_outputs, grad_grad_inputs, **self.check_double_backward_options)

def check_SPDN_backward(self, a_data, b_data, c_grad, atol, rtol):
    sp_a = utils.to_coo(a_data)
    func = F.math.sparse_matmul.CooMatMul(
        sp_a.row, sp_a.col, sp_a.shape, sp_a.order,
        transa=self.transa, transb=self.transb, transc=False)

    def op(a, b):
        return func.apply((a, b))[0]
    gradient_check.check_backward(
        op, (sp_a.data.data, b_data), c_grad, atol=atol, rtol=rtol,
        dtype=numpy.float32)

def check_DNSP_backward(self, a_data, b_data, c_grad, atol, rtol):
    sp_b = utils.to_coo(b_data)
    func = F.math.sparse_matmul.CooMatMul(
        sp_b.row, sp_b.col, sp_b.shape, sp_b.order,
        transa=not self.transb, transb=not self.transa, transc=True)

    def op(b, a):
        return func.apply((b, a))[0]
    gradient_check.check_backward(
        op, (sp_b.data.data, a_data), c_grad, atol=atol, rtol=rtol,
        dtype=numpy.float32)

