def check_backward(self, xp):
    x = xp.asarray(self.x)
    t = xp.asarray(self.t)
    gy = xp.asarray(self.gy)
    if self.class_weight is not None:
        class_weight = xp.asarray(self.class_weight)
    else:
        class_weight = None

    def f(x_, t_):
        return functions.softmax_cross_entropy(
            x_, t_, class_weight=class_weight, reduce=self.reduce,
            ignore_label=self.ignore_label,
            enable_double_backprop=self.enable_double_backprop)

    gradient_check.check_backward(f, (x, t), gy)

def check_double_backward(self, xp):
    x = xp.asarray(self.x)
    t = xp.asarray(self.t)
    gy = xp.asarray(self.gy)
    ggx = xp.asarray(self.ggx)
    if self.class_weight is not None:
        class_weight = xp.asarray(self.class_weight)
    else:
        class_weight = None

    def f(x_):
        return functions.softmax_cross_entropy(
            x_, t, class_weight=class_weight, reduce=self.reduce,
            ignore_label=self.ignore_label,
            enable_double_backprop=True)

    gradient_check.check_double_backward(f, x, gy, ggx)

def test_backward(self, backend_config): sampler = make_sampler(backend_config, self.label_size) x_data = backend_config.get_array(self.x) t_data = backend_config.get_array(self.t) w_data = backend_config.get_array(self.w) y_grad = backend_config.get_array(self.gy)
def f(x, w): return functions.negative_sampling( x, t_data, w, sampler, self.sample_size, reduce=self.reduce)
with backend_config: gradient_check.check_backward( f, (x_data, w_data), y_grad, **self.check_backward_options)

def test_double_backward(self, backend_config): sampler = make_sampler(backend_config, self.label_size) x_data = backend_config.get_array(self.x) t_data = backend_config.get_array(self.t) w_data = backend_config.get_array(self.w) y_grad = backend_config.get_array(self.gy) x_grad_grad = backend_config.get_array(self.ggx) w_grad_grad = backend_config.get_array(self.ggw)
def f(x, w): return functions.negative_sampling( x, t_data, w, sampler, self.sample_size, reduce=self.reduce)
with backend_config: gradient_check.check_double_backward( f, (x_data, w_data), y_grad, (x_grad_grad, w_grad_grad), **self.check_double_backward_options)

def forward_expected(self, inputs):
    """
    Current forward_expected implementation depends on
    F.convolution_2d itself and thus it's only capable
    of checking consistency between backends, not absolute
    correctness of computations
    """
    if self.nobias:
        x, W = inputs
        b = None
    else:
        x, W, b = inputs
    with chainer.using_config('use_ideep', 'never'):
        y_expected = F.convolution_2d(
            x, W, b, stride=self.stride, pad=self.pad,
            cover_all=self.cover_all, dilate=self.dilate,
            groups=self.groups)
    if self.old_numpy_fp16:
        return y_expected.array*0,
    return y_expected.array,

def forward(self, inputs, device):
    if self.nobias:
        x, W = inputs
        b = None
    else:
        x, W, b = inputs
    out = F.convolution_2d(
        x, W, b, stride=self.stride, pad=self.pad,
        cover_all=self.cover_all, dilate=self.dilate,
        groups=self.groups)
    if self.old_numpy_fp16:
        return out*0,
    return out,

def forward(self, inputs, device):
    if self.nobias:
        x, W = inputs
        b = None
    else:
        x, W, b = inputs
    y = F.convolution_nd(
        x, W, b, stride=self.stride, pad=self.pad,
        cover_all=self.cover_all, dilate=self.dilate,
        groups=self.groups)
    return y,

def forward(self, inputs, device):
    if self.nobias:
        x, W = inputs
        b = None
    else:
        x, W, b = inputs
    y = F.deconvolution_nd(
        x, W, b, stride=self.stride, pad=self.pad,
        outsize=self.outsize, dilate=self.dilate,
        groups=self.groups)
    return y,

def generate_inputs(self):
    W = numpy.random.normal(
        0, self.W_scale, self.W_shape).astype(self.W_dtype)
    x = numpy.random.uniform(-1, 1, self.x_shape).astype(self.x_dtype)
    if self.nobias:
        return x, W
    else:
        b = numpy.random.uniform(
            -1, 1, self.out_channels).astype(self.x_dtype)
        return x, W, b

def generate_inputs(self):
    W = numpy.random.normal(
