def check_in_recomputing(self, backend_config): layer, hook = self.layer, self.hook layer.add_hook(hook) layer.to_device(backend_config.device) x = backend_config.get_array(self.x)
y1 = layer(x).array u1 = getattr(layer, hook.vector_name).copy() v1 = hook.v.copy() with chainer.using_config('in_recomputing', True): y2 = layer(x).array u2 = getattr(layer, hook.vector_name) v2 = hook.v
u1, u2 = _cpu._to_cpu(u1), _cpu._to_cpu(u2) v1, v2 = _cpu._to_cpu(v1), _cpu._to_cpu(v2) numpy.testing.assert_array_equal(u1, u2) numpy.testing.assert_array_equal(v1, v2) testing.assert_allclose(y1, y2)

def check_u_not_updated_in_test(self, backend_config): layer, hook = self.layer, self.hook layer.add_hook(hook) layer.to_device(backend_config.device) x = backend_config.get_array(self.x)
with chainer.using_config('train', False): y1 = layer(x).array u1 = getattr(layer, hook.vector_name).copy() v1 = hook.v.copy() y2 = layer(x).array u2 = getattr(layer, hook.vector_name) v2 = hook.v.copy()
u1, u2 = _cpu._to_cpu(u1), _cpu._to_cpu(u2) v1, v2 = _cpu._to_cpu(v1), _cpu._to_cpu(v2) numpy.testing.assert_array_equal(u1, u2) numpy.testing.assert_array_equal(v1, v2) testing.assert_allclose(y1, y2)

def test_iterator_list_type(self):
    dataset = [[i, numpy.zeros((10,)) + i] for i in range(6)]
    it = iterators.MultiprocessIterator(dataset, 2, **self.options)
    for i in range(3):
        self.assertEqual(it.epoch, i)
        self.assertAlmostEqual(it.epoch_detail, i)
        if i == 0:
            self.assertIsNone(it.previous_epoch_detail)
        else:
            self.assertAlmostEqual(it.previous_epoch_detail, i - 2 / 6)
        batches = {}
        for j in range(3):
            batch = it.next()
            self.assertEqual(len(batch), 2)
            if j != 2:
                self.assertFalse(it.is_new_epoch)
            else:
                self.assertTrue(it.is_new_epoch)
            self.assertAlmostEqual(
                it.epoch_detail, (3 * i + j + 1) * 2 / 6)
            self.assertAlmostEqual(
                it.previous_epoch_detail, (3 * i + j) * 2 / 6)
            for x in batch:
                self.assertIsInstance(x, list)
                self.assertIsInstance(x[1], numpy.ndarray)
                batches[x[0]] = x[1]

        self.assertEqual(len(batches), len(dataset))
        for k, v in six.iteritems(batches):
            numpy.testing.assert_allclose(dataset[k][1], v)

def test_iterator_tuple_type(self):
    dataset = [(i, numpy.zeros((10,)) + i) for i in range(6)]
    it = iterators.MultiprocessIterator(dataset, 2, **self.options)
    for i in range(3):
        self.assertEqual(it.epoch, i)
        self.assertAlmostEqual(it.epoch_detail, i)
        if i == 0:
            self.assertIsNone(it.previous_epoch_detail)
        else:
            self.assertAlmostEqual(it.previous_epoch_detail, i - 2 / 6)
        batches = {}
        for j in range(3):
            batch = it.next()
            self.assertEqual(len(batch), 2)
            if j != 2:
                self.assertFalse(it.is_new_epoch)
            else:
                self.assertTrue(it.is_new_epoch)
            self.assertAlmostEqual(
                it.epoch_detail, (3 * i + j + 1) * 2 / 6)
            self.assertAlmostEqual(
                it.previous_epoch_detail, (3 * i + j) * 2 / 6)
            for x in batch:
                self.assertIsInstance(x, tuple)
                self.assertIsInstance(x[1], numpy.ndarray)
                batches[x[0]] = x[1]

        self.assertEqual(len(batches), len(dataset))
        for k, v in six.iteritems(batches):
            numpy.testing.assert_allclose(dataset[k][1], v)

def test_iterator_dict_type(self):
    dataset = [{i: numpy.zeros((10,)) + i} for i in range(6)]
    it = iterators.MultiprocessIterator(dataset, 2, **self.options)
    for i in range(3):
        self.assertEqual(it.epoch, i)
        self.assertAlmostEqual(it.epoch_detail, i)
        if i == 0:
            self.assertIsNone(it.previous_epoch_detail)
        else:
            self.assertAlmostEqual(it.previous_epoch_detail, i - 2 / 6)
        batches = {}
        for j in range(3):
            batch = it.next()
            self.assertEqual(len(batch), 2)
            if j != 2:
                self.assertFalse(it.is_new_epoch)
            else:
                self.assertTrue(it.is_new_epoch)
            self.assertAlmostEqual(
                it.epoch_detail, (3 * i + j + 1) * 2 / 6)
            self.assertAlmostEqual(
                it.previous_epoch_detail, (3 * i + j) * 2 / 6)
            for x in batch:
                self.assertIsInstance(x, dict)
                k = tuple(x)[0]
                v = x[k]
                self.assertIsInstance(v, numpy.ndarray)
                batches[k] = v

        self.assertEqual(len(batches), len(dataset))
        for k, v in six.iteritems(batches):
            x = dataset[k][tuple(dataset[k])[0]]
            numpy.testing.assert_allclose(x, v)

