self.assertEqual(len(batch3), 1) self.assertEqual(sorted(batch1 + batch2 + batch3), dataset)

def test_iterator_not_repeat_not_even(self): dataset = [1, 2, 3, 4, 5] it = iterators.SerialIterator(dataset, 2, repeat=False, shuffle=self.shuffle, order_sampler=self.order_sampler)
self.assertAlmostEqual(it.epoch_detail, 0 / 5) self.assertIsNone(it.previous_epoch_detail) batch1 = it.next() self.assertAlmostEqual(it.epoch_detail, 2 / 5) self.assertAlmostEqual(it.previous_epoch_detail, 0 / 5) batch2 = it.next() self.assertAlmostEqual(it.epoch_detail, 4 / 5) self.assertAlmostEqual(it.previous_epoch_detail, 2 / 5) batch3 = it.next() self.assertAlmostEqual(it.epoch_detail, 5 / 5) self.assertAlmostEqual(it.previous_epoch_detail, 4 / 5) self.assertRaises(StopIteration, it.next)
self.assertEqual(len(batch3), 1) self.assertEqual(sorted(batch1 + batch2 + batch3), dataset)

def test_reset(self):
    dataset = [1, 2, 3, 4, 5]
    it = iterators.MultiprocessIterator(
        dataset, 2, repeat=False, **self.options)

    for trial in range(4):
        batches = sum([it.next() for _ in range(3)], [])
        self.assertEqual(sorted(batches), dataset)
        for _ in range(2):
            self.assertRaises(StopIteration, it.next)
        it.reset()

def test_reset(self):
    dataset = [1, 2, 3, 4, 5]
    it = iterators.MultithreadIterator(
        dataset, 2, repeat=False, **self.options)

    for trial in range(4):
        batches = sum([it.next() for _ in range(3)], [])
        self.assertEqual(sorted(batches), dataset)
        for _ in range(2):
            self.assertRaises(StopIteration, it.next)
        it.reset()

def check_crossing_model(gpu, param): communicator, rank_next, rank_prev = create_communicator(gpu)
n, d = 100, 10 X = np.random.randn(n, d).astype(param.dtype) Y = (np.random.rand(n) * 2).astype(np.int32)
with chainer.using_config('dtype', param.dtype): if communicator.rank == 0: model = L.Classifier(Cross0( d, communicator, rank_next, rank_prev)) else: model = L.Classifier(Cross1( d, communicator, rank_next, rank_prev))
if gpu: model.to_device(cupy.cuda.Device()) X = chainer.backends.cuda.to_gpu(X) Y = chainer.backends.cuda.to_gpu(Y)
for i in range(n): err = model(X[i:i + 1], Y[i:i + 1]) err.backward()

def check_twisting_model(gpu, param): communicator, rank_next, rank_prev = create_communicator(gpu)
n, d = 100, 10 X = np.random.randn(n, d).astype(param.dtype) Y = (np.random.rand(n) * 2).astype(np.int32)
with chainer.using_config('dtype', param.dtype): if communicator.rank == 0: model = L.Classifier( TwistFirst(d, communicator, rank_next)) elif communicator.rank == communicator.size - 1: model = L.Classifier( TwistLast(d, communicator, rank_prev)) else: model = L.Classifier(Twist( d, communicator, rank_prev, rank_next))
if gpu: model.to_device(cupy.cuda.Device()) X = chainer.backends.cuda.to_gpu(X) Y = chainer.backends.cuda.to_gpu(Y)
for i in range(n): err = model(X[i:i + 1], Y[i:i + 1]) err.backward()

def check_homogeneous_rnn(gpu, dtype): communicator, rank_prev, rank_next = setup_communicator(gpu=gpu)
n, n_vocab, l = 100, 8, 10 # Number of model parameters are same among processes. n_hid = 2 with chainer.using_config('dtype', dtype): X = [np.random.randint( 0, n_vocab, size=np.random.randint(l // 2, l + 1), dtype=np.int32) for _ in range(n)] Y = (np.random.rand(n) * 2).astype(dtype) model = Model( n_vocab, n_hid, communicator, rank_next, rank_prev)
if gpu: model.to_device(cupy.cuda.Device()) X = [chainer.backends.cuda.to_gpu(x) for x in X] Y = chainer.backends.cuda.to_gpu(Y)
for i in range(n): err = model(X[i:i + 1], Y[i:i + 1]) err.backward()
# Check if backprop finishes without deadlock. assert True

def check_heterogeneous_rnn(gpu, dtype): communicator, rank_prev, rank_next = setup_communicator(gpu)
with chainer.using_config('dtype', dtype): n, n_vocab, l = 100, 8, 10 # Number of model parameters are different among processes. n_hid = (communicator.rank + 1) * 10
X = [np.random.randint( 0, n_vocab, size=np.random.randint(l // 2, l + 1), dtype=np.int32) for _ in range(n)] Y = (np.random.rand(n) * 2).astype(dtype) model = Model( n_vocab, n_hid, communicator, rank_next, rank_prev)
if gpu: model.to_device(cupy.cuda.Device()) X = [chainer.backends.cuda.to_gpu(x) for x in X] Y = chainer.backends.cuda.to_gpu(Y)
for i in range(n): err = model(X[i:i + 1], Y[i:i + 1]) err.backward()
# Check if backprop finishes without deadlock. assert True

def create_communicator(gpu, param):
    if gpu:
        communicator = chainermn.create_communicator('flat')
        device = communicator.intra_rank
        chainer.cuda.get_device_from_id(device).use()
    else:
        communicator = chainermn.create_communicator('naive')

    if communicator.size < 2:
        pytest.skip('This test is for multinode')

    return communicator

def get_communicator(gpu):
    numpy.random.seed(42)

    if gpu:
        communicator = chainermn.create_communicator('flat')
        device = communicator.intra_rank
        chainer.cuda.get_device_from_id(device).use()
    else:
        communicator = chainermn.create_communicator('naive')

    if communicator.size < 2:
        pytest.skip('This test is for multinode')

    return communicator

def test_sync(self):
    # test the case when datasize is a multiple of batchsize
    iterator = chainermn.iterators.create_synchronized_iterator(
        chainer.iterators.SerialIterator(
            self.dataset, batch_size=4, shuffle=True),
        self.communicator)

    for e in range(3):
        self.assertEqual(e, iterator.epoch)

        while True:
            batch = np.array(iterator.next(), dtype=np.float32)
            if self.communicator.rank == 0:
                for rank_from in range(1, self.communicator.size):
                    _batch = self.communicator.recv(rank_from, tag=0)
                    chainer.testing.assert_allclose(batch, _batch)
            else:
                self.communicator.send(batch, dest=0, tag=0)

            if iterator.is_new_epoch:
                break

def test_sync_frag(self):
    # test the case when datasize is not a multiple of batchsize
    iterator = chainermn.iterators.create_synchronized_iterator(
        chainer.iterators.SerialIterator(
            self.dataset, batch_size=7, shuffle=True),
        self.communicator)

    for e in range(3):
        self.assertEqual(e, iterator.epoch)

        while True:
            batch = np.array(iterator.next(), dtype=np.float32)
            if self.communicator.rank == 0:
                for rank_from in range(1, self.communicator.size):
                    _batch = self.communicator.recv(rank_from, tag=0)
                    chainer.testing.assert_allclose(batch, _batch)
            else:
                self.communicator.send(batch, dest=0, tag=0)

            if iterator.is_new_epoch:
                break

def test_sync_no_repeat(self):
    iterator = chainermn.iterators.create_synchronized_iterator(
        chainer.iterators.SerialIterator(
            self.dataset, batch_size=4, shuffle=True, repeat=False),
        self.communicator)

    for e in range(3):
        try:
            while True:
                batch = np.array(iterator.next(), dtype=np.float32)
                if self.communicator.rank == 0:
                    for rank_from in range(1, self.communicator.size):
                        _batch = self.communicator.recv(rank_from, tag=0)
                        chainer.testing.assert_allclose(batch, _batch)
                else:
                    self.communicator.send(batch, dest=0, tag=0)
        except StopIteration:
            iterator.reset()

