def test_mn_iterator(self):
    # Datasize is a multiple of batchsize.
    bs = 4
    iterator = chainermn.iterators.create_multi_node_iterator(
        self.iterator_class(
            self.dataset, batch_size=bs, shuffle=True),
        self.communicator)

    for e in range(3):
        for i in range(100):
            batch = iterator.next()
            if self.communicator.rank == 0:
                for rank_from in range(1, self.communicator.size):
                    _batch = self.communicator.mpi_comm.recv(
                        source=rank_from)
                    self.assertEqual(batch, _batch)
            else:
                self.communicator.mpi_comm.ssend(batch, dest=0)

def test_mn_iterator_no_repeat(self):
    # Do not repeat iterator to test if we can catch StopIteration.
    bs = 4
    iterator = chainermn.iterators.create_multi_node_iterator(
        self.iterator_class(
            self.dataset, batch_size=bs, shuffle=True, repeat=False),
        self.communicator)

    for e in range(3):
        try:
            while True:
                batch = iterator.next()
                if self.communicator.rank == 0:
                    for rank_from in range(1, self.communicator.size):
                        _batch = self.communicator.mpi_comm.recv(
                            source=rank_from)
                        self.assertEqual(batch, _batch)
                else:
                    self.communicator.mpi_comm.ssend(batch, dest=0)
        except StopIteration:
            continue

def test_mn_iterator_frag(self):
    # Batasize is not a multiple of batchsize.
    bs = 7
    iterator = chainermn.iterators.create_multi_node_iterator(
        self.iterator_class(
            self.dataset, batch_size=bs, shuffle=True),
        self.communicator)

    for e in range(3):
        for i in range(100):
            batch = iterator.next()
            if self.communicator.rank == 0:
                for rank_from in range(1, self.communicator.size):
                    _batch = self.communicator.mpi_comm.recv(
                        source=rank_from)
                    self.assertEqual(batch, _batch)
            else:
                self.communicator.mpi_comm.ssend(batch, dest=0)

def test_cupy_to_chainerx_delete_cupy_first(): dtype = numpy.float32 a_cupy = cupy.arange(6, dtype=dtype).reshape((2, 3)) a_chx = _fromrawpointer( a_cupy.data.mem.ptr, a_cupy.shape, a_cupy.dtype, a_cupy.strides, 'cuda:0', 0, a_cupy)
del a_cupy
a_chx += 1 chainerx.testing.assert_array_equal_ex( a_chx, numpy.array([[1, 2, 3], [4, 5, 6]], dtype))

def test_cupy_to_chainerx_delete_chainerx_first(): dtype = numpy.float32 a_cupy = cupy.arange(6, dtype=dtype).reshape((2, 3)) a_chx = _fromrawpointer( a_cupy.data.mem.ptr, a_cupy.shape, a_cupy.dtype, a_cupy.strides, 'cuda:0', 0, a_cupy)
del a_chx
a_cupy += 1 chainerx.testing.assert_array_equal_ex( a_cupy.get(), numpy.array([[1, 2, 3], [4, 5, 6]], dtype))

def test_array_from_numpy_array(xp, shape, dtype, device): a_np = array_utils.create_dummy_ndarray(numpy, shape, dtype) a_xp = xp.array(a_np)
if xp is chainerx: _check_array_from_numpy_array(a_xp, a_np, device)
# test possibly freed memory a_np_copy = a_np.copy() del a_np chainerx.testing.assert_array_equal_ex( a_xp, a_np_copy, strides_check=False)
return a_xp

def test_array_from_numpy_non_contiguous_array(xp, shape, dtype, device): a_np = array_utils.create_dummy_ndarray(numpy, shape, dtype, padding=True) a_xp = xp.array(a_np)
if xp is chainerx: _check_array_from_numpy_array(a_xp, a_np, device)
# test possibly freed memory a_np_copy = a_np.copy() del a_np chainerx.testing.assert_array_equal_ex( a_xp, a_np_copy, strides_check=False)
return a_xp

def test_array_from_chainerx_array_with_device( src_dtype, dst_dtype, copy, device, dst_device_spec): t = array_utils.create_dummy_ndarray( chainerx, (2,), src_dtype, device=device) a = chainerx.array(t, dtype=dst_dtype, copy=copy, device=dst_device_spec)
dst_device = chainerx.get_device(dst_device_spec)
if (not copy and (dst_dtype is None or src_dtype == dst_dtype) and (dst_device_spec is None or device is dst_device)): assert t is a else: assert t is not a if dst_dtype is None: dst_dtype = t.dtype chainerx.testing.assert_array_equal_ex( a, t.to_device(dst_device).astype(dst_dtype)) assert a.dtype == chainerx.dtype(dst_dtype) assert a.device is dst_device

def test_asarray_from_chainerx_array_with_device( src_dtype, dst_dtype, device, dst_device_spec): t = array_utils.create_dummy_ndarray( chainerx, (2,), src_dtype, device=device) a = chainerx.asarray(t, dtype=dst_dtype, device=dst_device_spec)
dst_device = chainerx.get_device(dst_device_spec)
if ((dst_dtype is None or src_dtype == dst_dtype) and (dst_device_spec is None or device is dst_device)): assert t is a else: assert t is not a if dst_dtype is None: dst_dtype = t.dtype chainerx.testing.assert_array_equal_ex( a, t.to_device(dst_device).astype(dst_dtype)) assert a.dtype == chainerx.dtype(dst_dtype) assert a.device is dst_device

def test_asarray_from_chainerx_array(dtype):
    obj = array_utils.create_dummy_ndarray(chainerx, (2, 3), 'int32')
    a = chainerx.asarray(obj, dtype=dtype)
    if a.dtype == obj.dtype:
        assert a is obj
    else:
        assert a is not obj
    e = chainerx.array(obj, dtype=dtype, copy=False)
    chainerx.testing.assert_array_equal_ex(e, a)
    assert e.device is a.device

def test_asanyarray_from_chainerx_array(dtype):
    obj = array_utils.create_dummy_ndarray(chainerx, (2, 3), 'int32')
    a = chainerx.asanyarray(obj, dtype=dtype)
    if a.dtype == obj.dtype:
        assert a is obj
    else:
        assert a is not obj
    e = chainerx.array(obj, dtype=dtype, copy=False)
    chainerx.testing.assert_array_equal_ex(e, a)
    assert e.device is a.device

def test_empty(xp, shape_as_sequence_or_int, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name
    if dtype_spec is Unspecified:
        a = xp.empty(shape_as_sequence_or_int)
    else:
        a = xp.empty(shape_as_sequence_or_int, dtype_spec)
    a.fill(0)
    if dtype_spec in (None, Unspecified):
        a = dtype_utils.cast_if_numpy_array(xp, a, 'float32')
    return a

def test_ones(xp, shape_as_sequence_or_int, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name
    if dtype_spec is Unspecified:
        out = xp.ones(shape_as_sequence_or_int)
    else:
        out = xp.ones(shape_as_sequence_or_int, dtype_spec)
    if dtype_spec in (None, Unspecified):
        out = dtype_utils.cast_if_numpy_array(xp, out, 'float32')
    return out

def test_zeros(xp, shape_as_sequence_or_int, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name
    if dtype_spec is Unspecified:
        out = xp.zeros(shape_as_sequence_or_int)
    else:
        out = xp.zeros(shape_as_sequence_or_int, dtype_spec)
    if dtype_spec in (None, Unspecified):
        out = dtype_utils.cast_if_numpy_array(xp, out, 'float32')
    return out

def test_arange_start_stop(xp, start, stop, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name
    # Checked in test_invalid_arange_too_long_bool
    if _is_bool_spec(dtype_spec) and abs(stop - start) > 2:
        return chainerx.testing.ignore()
    if ((isinstance(start, bool)
            or isinstance(stop, bool))
            and dtype_spec is None):
        # TODO(niboshi): This pattern needs dtype promotion.
        return chainerx.testing.ignore()
    out = xp.arange(start, stop, dtype=dtype_spec)
    if dtype_spec in (None, Unspecified):
        expected_dtype = _get_default_dtype(stop)
        out = dtype_utils.cast_if_numpy_array(xp, out, expected_dtype)
    return out

def test_arange_start_stop_step(xp, start, stop, step, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name
    # Checked in test_invalid_arange_too_long_bool
    if _is_bool_spec(dtype_spec) and abs((stop - start) / step) > 2:
        return chainerx.testing.ignore()
    if ((isinstance(start, bool)
            or isinstance(stop, bool)
            or isinstance(step, bool))
            and dtype_spec is None):
        # TODO(niboshi): This pattern needs dtype promotion.
        return chainerx.testing.ignore()
    out = xp.arange(start, stop, step, dtype=dtype_spec)
    if dtype_spec in (None, Unspecified):
        expected_dtype = _get_default_dtype(step)
        out = dtype_utils.cast_if_numpy_array(xp, out, expected_dtype)
    return out

def test_eye_with_default(xp, N, M, k, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name

    if M is None and k is None:
        return xp.eye(N, dtype=dtype_spec)
    elif M is None:
        return xp.eye(N, k=k, dtype=dtype_spec)
    elif k is None:
        return xp.eye(N, M=M, dtype=dtype_spec)
    assert False

def test_tri_with_default(xp, N, M, k, dtype_spec, device):
    if xp is numpy and isinstance(dtype_spec, chainerx.dtype):
        dtype_spec = dtype_spec.name

    if M is None and k is None:
        return xp.tri(N, dtype=dtype_spec)
    elif M is None:
        return xp.tri(N, k=k, dtype=dtype_spec)
    elif k is None:
        return xp.tri(N, M=M, dtype=dtype_spec)
    assert False

def generate_inputs(self):
    x_shape = self.x_shape
    w_shape = self.w_shape
    b_shape = self.b_shape
    if len(self.in_dtypes) == 3:
        x_dtype, w_dtype, b_dtype = self.in_dtypes
    else:
        (x_dtype, w_dtype), b_dtype = self.in_dtypes, None
    x = array_utils.uniform(x_shape, x_dtype)
    w = array_utils.uniform(w_shape, w_dtype)

    if b_shape is None:
        return x, w
    else:
        b = array_utils.uniform(b_shape, b_dtype)
        return x, w, b

def generate_inputs(self):
    x_shape = self.x_shape
    w_shape = self.w_shape
    b_shape = self.b_shape
    if len(self.in_dtypes) == 3:
        x_dtype, w_dtype, b_dtype = self.in_dtypes
    else:
        (x_dtype, w_dtype), b_dtype = self.in_dtypes, None
    x = array_utils.uniform(x_shape, x_dtype)
    w = array_utils.uniform(w_shape, w_dtype)

    if b_shape is None:
        return x, w
    else:
        b = array_utils.uniform(b_shape, b_dtype)
        return x, w, b

def generate_inputs(self):
    x_shape = self.x_shape
    w_shape = self.w_shape
    b_shape = self.b_shape
    if len(self.in_dtypes) == 3:
        x_dtype, w_dtype, b_dtype = self.in_dtypes
    else:
        (x_dtype, w_dtype), b_dtype = self.in_dtypes, None
    x = array_utils.uniform(x_shape, x_dtype)
    w = array_utils.uniform(w_shape, w_dtype)
    if b_shape in (None, Unspecified):
        return x, w
    else:
        b = array_utils.uniform(b_shape, b_dtype)
        return x, w, b

def forward_chainer(self, inputs):
    if len(inputs) == 2:
        (x, w), b = inputs, None
    else:
        x, w, b = inputs
    if x.dtype.kind != 'f':
        x = F.cast(x, 'float64')
    if w.dtype.kind != 'f':
        w = F.cast(w, 'float64')
    if b is not None and b.dtype.kind != 'f':
        b = F.cast(b, 'float64')
    y = F.convolution_nd(
        x, w, b, self.stride, self.pad, self.cover_all)
    y = F.cast(y, self.out_dtype)
    return y,

def forward_chainer(self, inputs):
    if len(inputs) == 3:
        x, w, b = inputs
    else:
        (x, w), b = inputs, None
    if x.dtype.kind != 'f':
        x = F.cast(x, 'float64')
    if w.dtype.kind != 'f':
        w = F.cast(w, 'float64')
    if b is not None and b.dtype.kind != 'f':
        b = F.cast(b, 'float64')
    y = chainer.functions.deconvolution_nd(
        x, w, b, self.stride, self.pad, self.outsize)
    y = F.cast(y, self.out_dtype)
    return y,

def func_scalar(self, xp, a, scalar):
    if self.is_module:
        if self.is_scalar_rhs:
            return a & scalar
        else:
            return scalar & a
    else:
        if self.is_scalar_rhs:
            return xp.bitwise_and(a, scalar)
        else:
            return xp.bitwise_and(scalar, a)

def func_scalar(self, xp, a, scalar):
    if self.is_module:
        if self.is_scalar_rhs:
            return a ^ scalar
        else:
            return scalar ^ a
    else:
        if self.is_scalar_rhs:
            return xp.bitwise_xor(a, scalar)
        else:
            return xp.bitwise_xor(scalar, a)

def func_scalar(self, xp, a, scalar):
    if self.is_module:
        if self.is_scalar_rhs:
            return a | scalar
        else:
            return scalar | a
    else:
        if self.is_scalar_rhs:
            return xp.bitwise_or(a, scalar)
        else:
            return xp.bitwise_or(scalar, a)

def func_scalar(self, xp, a, scalar):
    if self.is_module:
        if self.is_scalar_rhs:
            return a + scalar
        else:
            return scalar + a
    else:
        if self.is_scalar_rhs:
            return xp.add(a, scalar)
        else:
            return xp.add(scalar, a)

def func_scalar(self, xp, a, scalar):
    if self.is_module:
        if self.is_scalar_rhs:
            return a % scalar
        else:
            return scalar % a
    else:
        if self.is_scalar_rhs:
            return xp.remainder(a, scalar)
        else:
            return xp.remainder(scalar, a)

