def call(self, inputs): """Invoked when __call__ method of the layer is used.""" maxpool1 = self.max_pool1(inputs) conv1 = inputs for layer in self.conv_block1: conv1 = layer(conv1)
conv2 = inputs for layer in self.conv_block2: conv2 = layer(conv2)
output = self.concat_layer([maxpool1, conv1, conv2]) output = self.activation_layer(output) return output

def test_fit_variables(): """Test training a subset of the variables in a model."""
class VarModel(tf.keras.Model):
def __init__(self, **kwargs): super(VarModel, self).__init__(**kwargs) self.var1 = tf.Variable([0.5]) self.var2 = tf.Variable([0.5])
def call(self, inputs, training=False): return [self.var1, self.var2]
def loss(outputs, labels, weights): return (outputs[0] * outputs[1] - labels[0])**2
keras_model = VarModel() model = dc.models.KerasModel(keras_model, loss, learning_rate=0.01) x = np.ones((1, 1)) vars = model.predict_on_batch(x) assert np.allclose(vars[0], 0.5) assert np.allclose(vars[1], 0.5) model.fit_generator([(x, x, x)] * 300) vars = model.predict_on_batch(x) assert np.allclose(vars[0], 1.0) assert np.allclose(vars[1], 1.0) model.fit_generator([(x, 2 * x, x)] * 300, variables=[keras_model.var1]) vars = model.predict_on_batch(x) assert np.allclose(vars[0], 2.0) assert np.allclose(vars[1], 1.0) model.fit_generator([(x, x, x)] * 300, variables=[keras_model.var2]) vars = model.predict_on_batch(x) assert np.allclose(vars[0], 2.0) assert np.allclose(vars[1], 0.5)

