class VarModel(torch.nn.Module):
def __init__(self, **kwargs): super(VarModel, self).__init__(**kwargs) self.var1 = torch.nn.Parameter(torch.Tensor([0.5])) self.var2 = torch.nn.Parameter(torch.Tensor([0.5]))
