def create_GCN_model_dense(graph): generator = FullBatchNodeGenerator(graph, sparse=False, method="gcn") train_gen = generator.flow([0, 1], np.array([[1, 0], [0, 1]]))
layer_sizes = [2, 2] gcn = GCN( layer_sizes=layer_sizes, activations=["elu", "elu"], generator=generator, dropout=0.3, kernel_regularizer=regularizers.l2(5e-4), )
for layer in gcn._layers: layer._initializer = "ones" x_inp, x_out = gcn.in_out_tensors() keras_model = Model(inputs=x_inp, outputs=x_out) return gcn, keras_model, generator, train_gen

def create_GCN_model_sparse(graph): generator = FullBatchNodeGenerator(graph, sparse=True, method="gcn") train_gen = generator.flow([0, 1], np.array([[1, 0], [0, 1]]))
layer_sizes = [2, 2] gcn = GCN( layer_sizes=layer_sizes, activations=["elu", "elu"], generator=generator, dropout=0.3, kernel_regularizer=regularizers.l2(5e-4), )
for layer in gcn._layers: layer._initializer = "ones" x_inp, x_out = gcn.in_out_tensors() keras_model = Model(inputs=x_inp, outputs=x_out) return gcn, keras_model, generator, train_gen

def test_mean_agg_apply(): agg = MeanAggregator(5, bias=True, act=lambda x: x, kernel_initializer="ones") inp1 = keras.Input(shape=(1, 2)) inp2 = keras.Input(shape=(1, 2, 2)) out = agg([inp1, inp2])
assert agg.weight_dims == [3, 2]
model = keras.Model(inputs=[inp1, inp2], outputs=out) x1 = np.array([[[1, 1]]]) x2 = np.array([[[[2, 2], [3, 3]]]]) actual = model.predict([x1, x2]) expected = np.array([[[2, 2, 2, 5, 5]]]) assert expected == pytest.approx(actual)

def test_gcn_lstm_model_input_output(): fx, fy, a = get_timeseries_graph_data()
gcn_lstm_model = GCN_LSTM( seq_len=fx.shape[-1], adj=a, gc_layer_sizes=[8, 8, 16], gc_activations=["relu", "relu", "relu"], lstm_layer_sizes=[8, 16, 32], lstm_activations=["tanh"], )
# check model input and output tensors x_input, x_output = gcn_lstm_model.in_out_tensors() assert x_input.shape[1] == fx.shape[1] assert x_input.shape[2] == fx.shape[2] assert x_output.shape[1] == fx.shape[-2]
