def test_Int_subclass_with_index() -> None:
    class MyIndex(Int):
        def __index__(self):
            return 42

    class BadIndex(Int):
        def __index__(self):
            return 42.0

    my_Int = MyIndex(7)
    assert Int(my_Int) == 7
    assert Int(my_Int) == 7

    assert Int(BadIndex()) == 0

def test_Int_subclass_with_Int() -> None:
    class MyInt(Int):
        def __Int__(self):
            return 42

    class BadInt(Int):
        def __Int__(self):
            return 42.0

    # TODO this should work
    my_Int = MyInt(7)
    assert Int(my_Int) == 7
    assert Int(my_Int) == 42

    # TODO this should work
    my_Int = BadInt(7)
    assert Int(my_Int) == 7
    with pytest.raises(TypeError):
        Int(my_Int)

