def test_deny(method_name: str) -> None: node = Domain(name="remote domain") node_client = node.get_root_client()
addr = Address() request = RequestMessage( object_id=UID(), address=addr, requester_verify_key=get_verify_key(), owner_address=addr, owner_client_if_available=node_client, )
with patch.object( request.owner_client_if_available, "send_immediate_msg_without_reply" ) as mock_send_msg: getattr(request, method_name)() assert mock_send_msg.call_args[1]["msg"].address == node_client.address assert mock_send_msg.call_args[1]["msg"].accept is False assert mock_send_msg.call_args[1]["msg"].request_id == request.id

def test_service_auth_cpl_ofcr_fails() -> None:
    node = sy.Device()
    msg = sy.ReprMessage(address=node.address)

    random_signing_key = SigningKey.generate()
    random_verify_key = random_signing_key.verify_key

    # Compliance Officer only
    @service_auth(cpl_ofcr_only=True)
    def process(node: sy.Device, msg: sy.ReprMessage, verify_key: VerifyKey) -> None:
        pass

    with pytest.raises(
        AuthorizationException, match="User lacks Compliance Officer credentials."
    ):
        process(node=node, msg=msg, verify_key=random_verify_key)

