def create_GCN_model_dense(graph): generator = FullBatchNodeGenerator(graph, sparse=False, method="gcn") train_gen = generator.flow([0, 1], np.array([[1, 0], [0, 1]]))
layer_sizes = [2, 2] gcn = GCN( layer_sizes=layer_sizes, activations=["elu", "elu"], generator=generator, dropout=0.3, kernel_regularizer=regularizers.l2(5e-4), )
for layer in gcn._layers: layer._initializer = "ones" x_inp, x_out = gcn.in_out_tensors() keras_model = Model(inputs=x_inp, outputs=x_out) return gcn, keras_model, generator, train_gen

def create_GCN_model_sparse(graph): generator = FullBatchNodeGenerator(graph, sparse=True, method="gcn") train_gen = generator.flow([0, 1], np.array([[1, 0], [0, 1]]))
layer_sizes = [2, 2] gcn = GCN( layer_sizes=layer_sizes, activations=["elu", "elu"], generator=generator, dropout=0.3, kernel_regularizer=regularizers.l2(5e-4), )
for layer in gcn._layers: layer._initializer = "ones" x_inp, x_out = gcn.in_out_tensors() keras_model = Model(inputs=x_inp, outputs=x_out) return gcn, keras_model, generator, train_gen
