class.source:
def post(self, request: Request, organization) -> Response:
    """
    Create a saved query
    """
    if not self.has_feature(organization, request):
        return self.respond(status=404)

    try:
        params = self.get_filter_params(
            request, organization, project_ids=request.data.get("projects")
        )
    except NoProjects:
        raise ParseError(detail="No Projects found, join a Team")

    serializer = DiscoverSavedQuerySerializer(
        data=request.data,
        context={"params": params},
    )

    if not serializer.is_valid():
        return Response(serializer.errors, status=400)

    data = serializer.validated_data

    model = DiscoverSavedQuery.objects.create(
        organization=organization,
        name=data["name"],
        query=data["query"],
        version=data["version"],
        created_by=request.user if request.user.is_authenticated else None,
    )

    model.set_projects(data["project_ids"])

    return Response(serialize(model), status=201)

def put(self, request: Request, organization, query_id) -> Response:
    """
    Modify a saved query
    """
    if not self.has_feature(organization, request):
        return self.respond(status=404)

    try:
        model = DiscoverSavedQuery.objects.get(id=query_id, organization=organization)
    except DiscoverSavedQuery.DoesNotExist:
        raise ResourceDoesNotExist

    try:
        params = self.get_filter_params(
            request, organization, project_ids=request.data.get("projects")
        )
    except NoProjects:
        raise ParseError(detail="No Projects found, join a Team")

    serializer = DiscoverSavedQuerySerializer(
        data=request.data,
        context={"params": params},
    )
    if not serializer.is_valid():
        return Response(serializer.errors, status=400)

    data = serializer.validated_data
    model.update(
        organization=organization,
        name=data["name"],
        query=data["query"],
        version=data["version"],
    )

    model.set_projects(data["project_ids"])

    return Response(serialize(model), status=200)

def validate(self, password):
    if len(password) < self.min_length:
        raise ValidationError(
            ungettext(
                "This password is too short. It must contain at least %(min_length)d character.",
                "This password is too short. It must contain at least %(min_length)d characters.",
                self.min_length,
            ),
            code="password_too_short",
            params={"min_length": self.min_length},
        )

def validate(self, password):
    if len(password) > self.max_length:
        raise ValidationError(
            ungettext(
                "This password is too long. It must contain no more than %(max_length)d character.",
                "This password is too long. It must contain no more than %(max_length)d characters.",
                self.max_length,
            ),
            code="password_too_long",
            params={"max_length": self.max_length},
        )

def _make_request(self):
    action_to_past_tense = {"create": "created", "link": "linked"}

    try:
        req = send_and_save_sentry_app_request(
            self._build_url(),
            self.sentry_app,
            self.install.organization_id,
            f"external_issue.{action_to_past_tense[self.action]}",
            headers=self._build_headers(),
            method="POST",
            data=self.body,
        )
        body = safe_urlread(req)
        response = json.loads(body)
    except Exception as e:
        logger.info(
            "issue-link-requester.error",
            extra={
                "sentry_app": self.sentry_app.slug,
                "install": self.install.uuid,
                "project": self.group.project.slug,
                "group": self.group.id,
                "uri": self.uri,
                "error_message": str(e),
            },
        )
        response = {}

    if not self._validate_response(response):
        raise APIError()

    return response

def _make_request(self):
    try:
        body = safe_urlread(
            send_and_save_sentry_app_request(
                self._build_url(),
                self.sentry_app,
                self.install.organization_id,
                "select_options.requested",
                headers=self._build_headers(),
            )
        )

        response = json.loads(body)
    except Exception as e:
        logger.info(
            "select-requester.error",
            extra={
                "sentry_app_slug": self.sentry_app.slug,
                "install_uuid": self.install.uuid,
                "project_slug": self.project and self.project.slug,
                "uri": self.uri,
                "error_message": str(e),
            },
        )
        response = {}

    if not self._validate_response(response):
        raise APIError()

    return self._format_response(response)

def send_and_save_webhook_request(sentry_app, app_platform_event, url=None): """ Notify a SentryApp's webhook about an incident and log response on redis.
:param sentry_app: The SentryApp to notify via a webhook. :param app_platform_event: Incident data. See AppPlatformEvent. :param url: The URL to hit for this webhook if it is different from `sentry_app.webhook_url`. :return: Webhook response """ buffer = SentryAppWebhookRequestsBuffer(sentry_app)
org_id = app_platform_event.install.organization_id event = f"{app_platform_event.resource}.{app_platform_event.action}" slug = sentry_app.slug_for_metrics url = url or sentry_app.webhook_url try: resp = safe_urlopen( url=url, data=app_platform_event.body, headers=app_platform_event.headers, timeout=5 ) except (Timeout, ConnectionError) as e: error_type = e.__class__.__name__.lower() logger.info( "send_and_save_webhook_request.timeout", extra={ "error_type": error_type, "organization_id": org_id, "integration_slug": sentry_app.slug, }, ) track_response_code(error_type, slug, event) # Response code of 0 represents timeout buffer.add_request(response_code=0, org_id=org_id, event=event, url=url) # Re-raise the exception because some of these tasks might retry on the exception raise
else: track_response_code(resp.status_code, slug, event) buffer.add_request( response_code=resp.status_code, org_id=org_id, event=event, url=url, error_id=resp.headers.get("Sentry-Hook-Error"), project_id=resp.headers.get("Sentry-Hook-Project"), )
if resp.status_code == 503: raise ApiHostError.from_request(resp.request)
elif resp.status_code == 504: raise ApiTimeoutError.from_request(resp.request)
if 400 <= resp.status_code < 500: raise ClientError(resp.status_code, url, response=resp)
resp.raise_for_status() return resp

def audit(self):
    from sentry.utils.audit import create_audit_entry

    if self.request:
        create_audit_entry(
            request=self.request,
            organization=self.install.organization,
            target_object=self.install.organization.id,
            event=AuditLogEntryEvent.SENTRY_APP_INSTALL,
            data={"sentry_app": self.sentry_app.name},
        )

def audit(self):
    from sentry.utils.audit import create_audit_entry

    if self.request:
        create_audit_entry(
            request=self.request,
            organization=self.organization,
            target_object=self.organization.id,
            event=AuditLogEntryEvent.SENTRY_APP_ADD,
            data={"sentry_app": self.sentry_app.name},
        )

