self.sentry_app = SentryApp.objects.create( application=self.application, name="NullDB", proxy_user=self.proxy, owner=self.org, scope_list=("project:read",), webhook_url="http://example.com", )
self.install = SentryAppInstallation(sentry_app=self.sentry_app, organization=self.org)

def test_keep_disjoint_difs(self): file = self.create_file( name="crash.dsym", checksum="dc1e3f3e411979d336c3057cce64294f3420f93a" ) dif1, created1 = self.create_dif(file=file, data={"features": ["unwind"]})
file = self.create_file( name="crash.dsym", checksum="2b92c5472f4442a27da02509951ea2e0f529511c" ) dif2, created2 = self.create_dif(file=file, data={"features": ["debug"]})
assert created1 and created2 assert ProjectDebugFile.objects.filter(id=dif1.id).exists() assert ProjectDebugFile.objects.filter(id=dif2.id).exists()

def test_remove_redundant_dif(self): file = self.create_file( name="crash.dsym", checksum="dc1e3f3e411979d336c3057cce64294f3420f93a" ) dif1, created1 = self.create_dif(file=file, data={"features": ["debug"]})
file = self.create_file( name="crash.dsym", checksum="2b92c5472f4442a27da02509951ea2e0f529511c" ) dif2, created2 = self.create_dif(file=file, data={"features": ["debug"]})
assert created1 and created2 assert not ProjectDebugFile.objects.filter(id=dif1.id).exists() assert ProjectDebugFile.objects.filter(id=dif2.id).exists()

def test_keep_overlapping_difs(self): file = self.create_file( name="crash.dsym", checksum="dc1e3f3e411979d336c3057cce64294f3420f93a" ) dif1, created1 = self.create_dif(file=file, data={"features": ["symtab", "unwind"]})
file = self.create_file( name="crash.dsym", checksum="2b92c5472f4442a27da02509951ea2e0f529511c" ) dif2, created2 = self.create_dif(file=file, data={"features": ["symtab", "debug"]})
assert created1 and created2 assert ProjectDebugFile.objects.filter(id=dif1.id).exists() assert ProjectDebugFile.objects.filter(id=dif2.id).exists()

def test_simple(self, mock_fetch_commit):
    refs = [
        {
            "repository": "test/repo",
            "previousCommit": "previous-commit-id",
            "commit": "current-commit-id",
        },
        {
            "repository": "test/repo",
            "previousCommit": "previous-commit-id-2",
            "commit": "current-commit-id-2",
        },
    ]

    self.release.set_refs(refs, self.user, True)

    commits = Commit.objects.all().order_by("id")
    self.assert_commit(commits[0], refs[0]["commit"])
    self.assert_commit(commits[1], refs[1]["commit"])

    head_commits = ReleaseHeadCommit.objects.all()
    self.assert_head_commit(head_commits[0], refs[1]["commit"])

    self.assert_fetch_commits(mock_fetch_commit, None, self.release.id, refs)

def test_fetch_false(self, mock_fetch_commit):
    refs = [
        {
            "repository": "test/repo",
            "previousCommit": "previous-commit-id",
            "commit": "current-commit-id",
        },
        {
            "repository": "test/repo",
            "previousCommit": "previous-commit-id-2",
            "commit": "current-commit-id-2",
        },
    ]

    self.release.set_refs(refs, self.user, False)

    commits = Commit.objects.all().order_by("id")
    self.assert_commit(commits[0], refs[0]["commit"])
    self.assert_commit(commits[1], refs[1]["commit"])

    head_commits = ReleaseHeadCommit.objects.all()
    self.assert_head_commit(head_commits[0], refs[1]["commit"])

    assert len(mock_fetch_commit.method_calls) == 0

def test_follows_semver_user_accidentally_stopped_using_semver_a_few_times(self): """ Test that ensures that when a user accidentally stops using semver versioning for a few times but there exists at least one semver compliant release in the last 3 releases and at least 3 releases that are semver compliant in the last 10 then we still consider project to be following semantic versioning """ proj = self.create_project(organization=self.org)
for i in range(2): self.create_release(version=f"{self.fake_package}{proj.id}@1.{i}", project=proj) for i in range(7): self.create_release(version=f"foo release {i}", project=proj) self.create_release(version=f"{self.fake_package}{proj.id}@1.9", project=proj)
assert ( follows_semver_versioning_scheme( org_id=self.org.id, project_id=proj.id, ) is True )

def test_follows_semver_user_accidentally_uses_semver_a_few_times(self): """ Test that ensures that if user accidentally uses semver compliant versions for a few times then the project will not be considered to be using semver """ proj = self.create_project(organization=self.org)
for i in range(8): self.create_release(version=f"foo release {i}", project=proj) for i in range(2): self.create_release(version=f"{self.fake_package}{proj.id}@1.{i}", project=proj)
assert ( follows_semver_versioning_scheme( org_id=self.org.id, project_id=proj.id, ) is False )

def test_follows_semver_user_stops_using_semver(self): """ Test that ensures that if a user stops using semver and so the last 3 releases in the last 10 releases are all non-semver releases, then the project does not follow semver anymore since 1st condition of at least one semver release in the last 3 has to be a semver release is not satisfied """ proj = self.create_project(organization=self.org)
for i in range(7): self.create_release(version=f"{self.fake_package}{proj.id}@1.{i}", project=proj) for i in range(3): self.create_release(version=f"helloworld {i}", project=proj)
assert ( follows_semver_versioning_scheme( org_id=self.org.id, project_id=proj.id, ) is False )

def test_follows_semver_user_starts_using_semver(self): """ Test that ensures if a user starts using semver by having at least the last 3 releases using semver then we consider the project to be using semver """ proj = self.create_project(organization=self.org)
for i in range(7): self.create_release(version=f"foo release {i}", project=proj) for i in range(3): self.create_release(version=f"{self.fake_package}{proj.id}@1.{i}", project=proj)
assert ( follows_semver_versioning_scheme( org_id=self.org.id, project_id=proj.id, ) is True )

def test_follows_semver_user_starts_using_semver_with_less_than_10_recent_releases(self): """ Test that ensures that a project with only 5 (<10) releases and at least one semver release in the most recent releases is considered to be following semver """ proj = self.create_project(organization=self.org)
for i in range(4): self.create_release(version=f"helloworld {i}", project=proj) self.create_release(version=f"{self.fake_package}{proj.id}@1.0", project=proj)
assert ( follows_semver_versioning_scheme( org_id=self.org.id, project_id=proj.id, ) is True )

def test_when_current_release_version_is_set_with_new_release(self):
    for grp_res_type in [GroupResolution.Type.in_release, GroupResolution.Type.in_next_release]:
        grp_resolution = GroupResolution.objects.create(
            release=self.old_release,
            current_release_version=self.old_release.version,
            group=self.group,
            type=grp_res_type,
        )
        assert not GroupResolution.has_resolution(self.group, self.new_release)

        grp_resolution.delete()

def test_for_semver_when_current_release_version_is_set_with_same_release(self):
    for grp_res_type in [GroupResolution.Type.in_release, GroupResolution.Type.in_next_release]:
        grp_resolution = GroupResolution.objects.create(
            release=self.old_semver_release,
            current_release_version=self.old_semver_release.version,
            group=self.group,
            type=grp_res_type,
        )
        assert GroupResolution.has_resolution(self.group, self.old_semver_release)

        grp_resolution.delete()

def test_for_semver_when_current_release_version_is_set_with_old_semver_release(self):
    for grp_res_type in [GroupResolution.Type.in_release, GroupResolution.Type.in_next_release]:
        grp_resolution = GroupResolution.objects.create(
            release=self.new_semver_release,
            current_release_version=self.new_semver_release.version,
            group=self.group,
            type=grp_res_type,
        )
        assert GroupResolution.has_resolution(self.group, self.old_semver_release)
        grp_resolution.delete()

def test_when_current_release_version_is_set_with_same_release(self):
    for grp_res_type in [GroupResolution.Type.in_release, GroupResolution.Type.in_next_release]:
        grp_resolution = GroupResolution.objects.create(
            release=self.old_release,
            current_release_version=self.old_release.version,
            group=self.group,
            type=grp_res_type,
        )
        assert GroupResolution.has_resolution(self.group, self.old_release)

        grp_resolution.delete()

def test_when_current_release_version_is_set_with_old_release(self):
    for grp_res_type in [GroupResolution.Type.in_release, GroupResolution.Type.in_next_release]:
        grp_resolution = GroupResolution.objects.create(
            release=self.new_release,
            current_release_version=self.new_release.version,
            group=self.group,
            type=grp_res_type,
        )
        assert GroupResolution.has_resolution(self.group, self.old_release)

        grp_resolution.delete()

def test_build_hash(self):
    cases = [
        (
            {
                "ident": "ident",
                "username": "username",
                "email": "email",
                "ip_address": "127.0.0.1",
            },
            "67217d8b401cf5e72bbf5103d60f3e97",
        ),
        (
            {"username": "username", "email": "email", "ip_address": "127.0.0.1"},
            "14c4b06b824ec593239362517f538b29",
        ),
        ({"email": "email", "ip_address": "127.0.0.1"}, "0c83f57c786a0b4a39efab23731c7ebc"),
        ({"ip_address": "127.0.0.1"}, "f528764d624db129b32c21fbca0cb8d6"),
        ({}, None),
    ]
    for kw, value in cases:
        assert EventUser(**kw).build_hash() == value

def test_tag_value(self):
    cases = [
        (
            {
                "ident": "ident",
                "username": "username",
                "email": "email",
                "ip_address": "127.0.0.1",
            },
            "id:ident",
        ),
        (
            {"username": "username", "email": "email", "ip_address": "127.0.0.1"},
            "username:username",
        ),
        ({"email": "email", "ip_address": "127.0.0.1"}, "email:email"),
        ({"ip_address": "127.0.0.1"}, "ip:127.0.0.1"),
        ({}, None),
    ]
    for kw, value in cases:
        assert EventUser(**kw).tag_value == value

def test_notify_after_resolve_in_commit(self, delay):
    repo = self.create_repo(project=self.project)
    commit = self.create_commit(repo=repo)

    self.update_issue(
        {"statusDetails": {"inCommit": {"repository": repo.name, "commit": commit.key}}}
    )

    assert faux(delay).called_with(
        installation_id=self.install.id,
        issue_id=self.issue.id,
        type="resolved",
        user_id=self.user.id,
        data={"resolution_type": "in_commit"},
    )

def test_notify_after_resolve_in_specific_release(self, delay): release = self.create_release(project=self.project)
self.update_issue({"statusDetails": {"inRelease": release.version}})
assert faux(delay).called_with( installation_id=self.install.id, issue_id=self.issue.id, type="resolved", user_id=self.user.id, data={"resolution_type": "in_release"}, )

def test_notify_after_resolve_in_next_release(self, delay): self.create_release(project=self.project)
self.update_issue({"statusDetails": {"inNextRelease": True}})
assert faux(delay).called_with( installation_id=self.install.id, issue_id=self.issue.id, type="resolved", user_id=self.user.id, data={"resolution_type": "in_next_release"}, )

def test_notify_after_resolve_in_latest_release(self, delay): self.create_release(project=self.project)
self.update_issue({"statusDetails": {"inRelease": "latest"}})
assert faux(delay).called_with( installation_id=self.install.id, issue_id=self.issue.id, type="resolved", user_id=self.user.id, data={"resolution_type": "in_release"}, )

def test_source_maps(self):
    event = self.store_event(
        data={
            "platform": "javascript",
            "exception": {
                "values": [
                    {
                        "stacktrace": {
                            "frames": [
                                {
                                    "data": {
                                        "sourcemap": "https://media.sentry.io/_static/29e365f8b0d923bc123e8afa38d890c3/sentry/dist/vendor.js.map"
                                    }
                                }
                            ]
                        },
                        "type": "TypeError",
                    }
                ]
            },
        },
        project_id=self.project.id,
    )
    event_processed.send(project=self.project, event=event, sender=type(self.project))

    source_maps = FeatureAdoption.objects.get_by_slug(
        organization=self.organization, slug="source_maps"
    )
    assert source_maps

