                        "type": "http",
                        "data": {
                            "url": "/api/path/here",
                            "status_code": "500",
                            "method": "POST",
                        },
                    }
                ]
            }
        },
        project_id=self.project.id,
    )
    event_processed.send(project=self.project, event=event, sender=type(self.project))

    breadcrumbs = FeatureAdoption.objects.get_by_slug(
        organization=self.organization, slug="breadcrumbs"
    )
    assert breadcrumbs

def test_cocoa_culprit():
    culprit = get_culprit(
        {
            "platform": "cocoa",
            "exception": {
                "type": "Crash",
                "stacktrace": {
                    "frames": [
                        {
                            "filename": "foo/baz.c",
                            "package": "/foo/bar/baz.dylib",
                            "lineno": 1,
                            "in_app": True,
                            "function": "-[CRLCrashAsyncSafeThread crash]",
                        }
                    ]
                },
            },
        }
    )
    assert culprit == "-[CRLCrashAsyncSafeThread crash]"

def test_culprit_for_javascript_event():
    culprit = get_culprit(
        {
            "platform": "javascript",
            "exception": {
                "type": "Error",
                "value": "I fail hard",
                "stacktrace": {
                    "frames": [
                        {
                            "filename": "foo/baz.js",
                            "package": "node_modules/blah/foo/bar.js",
                            "lineno": 42,
                            "in_app": True,
                            "function": "fooBar",
                        }
                    ]
                },
            },
        }
    )
    assert culprit == "fooBar(foo/baz.js)"

def test_emoji_culprit():
    culprit = get_culprit(
        {
            "platform": "native",
            "exception": {
                "type": "Crash",
                "stacktrace": {
                    "frames": [
                        {
                            "filename": "foo/baz.c",
                            "package": "/foo/bar/baz.dylib",
                            "module": "\U0001f62d",
                            "lineno": 1,
                            "in_app": True,
                            "function": "\U0001f60d",
                        }
                    ]
                },
            },
        }
    )
    assert culprit == "\U0001f60d"

def test_culprit_for_synthetic_event():
    # Synthetic events do not generate a culprit
    culprit = get_culprit(
        {
            "platform": "javascript",
            "exception": {
                "type": "Error",
                "value": "I threw up stringly",
                "mechanism": {"type": "string-error", "synthetic": True},
                "stacktrace": {
                    "frames": [
                        {
                            "filename": "foo/baz.js",
                            "package": "node_modules/blah/foo/bar.js",
                            "lineno": 42,
                            "in_app": True,
                            "function": "fooBar",
                        }
                    ]
                },
            },
        }
    )
    assert culprit == ""

def test_culprit_for_python_event():
    culprit = get_culprit(
        {
            "platform": "python",
            "exception": {
                "type": "ZeroDivisionError",
                "value": "integer division or modulo by zero",
                "stacktrace": {
                    "frames": [
                        {
                            "filename": "foo/baz.py",
                            "module": "foo.baz",
                            "package": "foo/baz.py",
                            "lineno": 23,
                            "in_app": True,
                            "function": "it_failed",
                        }
                    ]
                },
            },
        }
    )
    assert culprit == "foo.baz in it_failed"

def test_dif(self): sym_file = self.load_fixture("crash.sym") blob1 = FileBlob.from_file(ContentFile(sym_file)) total_checksum = sha1(sym_file).hexdigest()
assemble_dif( project_id=self.project.id, name="crash.sym", checksum=total_checksum, chunks=[blob1.checksum], )
status, _ = get_assemble_status(AssembleTask.DIF, self.project.id, total_checksum) assert status == ChunkFileState.OK
dif = ProjectDebugFile.objects.filter( project_id=self.project.id, checksum=total_checksum ).get()
assert dif.file.headers == {"Content-Type": "text/x-breakpad"}

