def test_process_event_unprocessed( default_project, mock_event_processing_store, mock_save_event, register_plugin ): register_plugin(globals(), BasicPreprocessorPlugin)
data = { "project": default_project.id, "platform": "holdmeclose", "logentry": {"formatted": "test"}, "event_id": EVENT_ID, "extra": {"foo": "bar"}, }
mock_event_processing_store.get.return_value = data mock_event_processing_store.store.return_value = "e:1"
process_event(cache_key="e:1", start_time=1)
((_, (event,), _),) = mock_event_processing_store.store.mock_calls assert event["unprocessed"] is True
mock_save_event.delay.assert_called_once_with( cache_key="e:1", data=None, start_time=1, event_id=EVENT_ID, project_id=default_project.id )

