def test_future_set_callback_success(): future_set = FutureSet([Future() for i in range(3)])
callback = mock.Mock() future_set.add_done_callback(callback)
for i, future in enumerate(list(future_set)): assert callback.call_count == 0 future.set_result(True)
assert callback.call_count == 1 assert callback.call_args == mock.call(future_set)
other_callback = mock.Mock() future_set.add_done_callback(other_callback)
assert other_callback.call_count == 1 assert other_callback.call_args == mock.call(future_set)

def test_future_set_callback_error(): future_set = FutureSet([Future() for i in range(3)])
callback = mock.Mock() future_set.add_done_callback(callback)
for i, future in enumerate(list(future_set)): assert callback.call_count == 0 future.set_exception(Exception)
assert callback.call_count == 1 assert callback.call_args == mock.call(future_set)
other_callback = mock.Mock() future_set.add_done_callback(other_callback)
assert other_callback.call_count == 1 assert other_callback.call_args == mock.call(future_set)

def test_audit_entry_org_delete_log(self): entry = create_audit_entry( request=self.req, organization=self.org, target_object=self.org.id, event=AuditLogEntryEvent.ORG_REMOVE, data=self.org.get_audit_log_data(), )
assert entry.actor == self.user assert entry.actor_label == username[:64] # needs trimming assert entry.target_object == self.org.id assert entry.event == AuditLogEntryEvent.ORG_REMOVE
deleted_org = DeletedOrganization.objects.get(slug=self.org.slug) self.assert_valid_deleted_log(deleted_org, self.org)

