def test_raw_content(self): msg = MessageBuilder( subject="Test", body="hello world", html_body="hello world", headers={"X-Test": "foo"}, ) msg.send(["foo@example.com"])
assert len(mail.outbox) == 1
out = mail.outbox[0] assert out.to == ["foo@example.com"] assert out.subject == "Test" assert out.extra_headers["X-Test"] == "foo" assert out.body == "hello world" assert len(out.alternatives) == 1 assert out.alternatives[0] == ( "\nhello world", "text/html", )

def test_explicit_reply_to(self): msg = MessageBuilder( subject="Test", body="hello world", html_body="hello world", headers={"X-Sentry-Reply-To": "bar@example.com"}, ) msg.send(["foo@example.com"])
assert len(mail.outbox) == 1
out = mail.outbox[0] assert out.to == ["foo@example.com"] assert out.subject == "Test" assert out.extra_headers["Reply-To"] == "bar@example.com" assert out.body == "hello world" assert len(out.alternatives) == 1 assert out.alternatives[0] == ( "\nhello world", "text/html", )

def test_by_api_token(self):
    token = ApiToken(id=1)
    for n in range(5):
        assert not ratelimits.for_organization_member_invite(
            Organization(id=randint(0, 100000)),
            f"{randint(0, 1000000)}@example.com",
            auth=token,
            config=RELAXED_CONFIG,
        )

    assert ratelimits.for_organization_member_invite(
        Organization(id=1), "anything@example.com", auth=token, config=RELAXED_CONFIG
    )

def test_by_user(self):
    user = User(email="biz@example.com")
    for n in range(5):
        assert not ratelimits.for_organization_member_invite(
            Organization(id=randint(0, 100000)),
            f"{randint(0, 1000000)}@example.com",
            user=user,
            config=RELAXED_CONFIG,
        )

    assert ratelimits.for_organization_member_invite(
        Organization(id=1), "anything@example.com", user=user, config=RELAXED_CONFIG
    )

def test_get_kafka_producer_cluster_options():
    cluster_options = get_kafka_producer_cluster_options("default")
    assert (
        cluster_options["bootstrap.servers"]
        == settings.KAFKA_CLUSTERS["default"]["common"]["bootstrap.servers"]
    )

    with override_settings(
        KAFKA_CLUSTERS={"default": {"producers": {"bootstrap.servers": "my.server:9092"}}}
    ):
        cluster_options = get_kafka_producer_cluster_options("default")
        assert cluster_options["bootstrap.servers"] == "my.server:9092"

    with override_settings(
        KAFKA_CLUSTERS={
            "default": {
                "producers": {"bootstrap.servers": "my.server:9092"},
                "bootstrap.servers": "my.legacy.server:9092",
                "security.protocol": "plain",  # legacy config
            }
        }
    ):
        cluster_options = get_kafka_producer_cluster_options("default")
        assert cluster_options["bootstrap.servers"] == "my.legacy.server:9092"
        assert cluster_options["security.protocol"] == "plain"

def test_get_kafka_consumer_cluster_options():
    cluster_options = get_kafka_consumer_cluster_options("default")
    assert (
        cluster_options["bootstrap.servers"]
        == settings.KAFKA_CLUSTERS["default"]["common"]["bootstrap.servers"]
    )

    with override_settings(
        KAFKA_CLUSTERS={"default": {"consumers": {"bootstrap.servers": "my.other.server:9092"}}}
    ):
        cluster_options = get_kafka_consumer_cluster_options("default")
        assert cluster_options["bootstrap.servers"] == "my.other.server:9092"

    with override_settings(
        KAFKA_CLUSTERS={
            "default": {
                "consumers": {"bootstrap.servers": "my.other.server:9092"},
                # legacy config:
                "security.protocol": "plain",
                "bootstrap.servers": "my.legacy.server:9092",
            }
        }
    ):
        cluster_options = get_kafka_consumer_cluster_options("default")
        assert cluster_options["bootstrap.servers"] == "my.legacy.server:9092"
        assert "security.protocol" not in cluster_options

def test_filter_status(self): self.create_team(organization=self.organization, members=[self.user]) incident = self.create_incident() closed_incident = self.create_incident(status=IncidentStatus.CLOSED.value) self.login_as(self.user)
with self.feature(["organizations:incidents", "organizations:performance-view"]): resp_closed = self.get_valid_response(self.organization.slug, status="closed") resp_open = self.get_valid_response(self.organization.slug, status="open")
assert len(resp_closed.data) == 1 assert len(resp_open.data) == 1 assert resp_closed.data == serialize([closed_incident]) assert resp_open.data == serialize([incident])

def test_filter_name(self): self.create_team(organization=self.organization, members=[self.user]) incident = self.create_incident(title="yet another alert rule") self.login_as(self.user)
with self.feature(["organizations:incidents", "organizations:performance-view"]): results = self.get_valid_response(self.organization.slug, title="yet") no_results = self.get_valid_response(self.organization.slug, title="no results")
assert len(results.data) == 1 assert len(no_results.data) == 0 assert results.data == serialize([incident])

def test_no_label(self):
    rule_one_trigger_no_label = {
        "aggregate": "count()",
        "query": "",
        "timeWindow": "300",
        "projects": [self.project.slug],
        "name": "OneTriggerOnlyCritical",
        "owner": self.user.id,
        "resolveThreshold": 100,
        "thresholdType": 1,
        "triggers": [
            {
                "alertThreshold": 200,
                "actions": [
                    {"type": "email", "targetType": "team", "targetIdentifier": self.team.id}
                ],
            }
        ],
    }

    with self.feature("organizations:incidents"):
        self.get_valid_response(
            self.organization.slug, status_code=400, **rule_one_trigger_no_label
        )

def test_only_critical_trigger(self):
    rule_one_trigger_only_critical = {
        "aggregate": "count()",
        "query": "",
        "timeWindow": "300",
        "projects": [self.project.slug],
        "name": "OneTriggerOnlyCritical",
        "owner": self.user.id,
        "resolveThreshold": 200,
        "thresholdType": 1,
        "triggers": [
            {
                "label": "critical",
                "alertThreshold": 100,
                "actions": [
                    {"type": "email", "targetType": "team", "targetIdentifier": self.team.id}
                ],
            }
        ],
    }
    with self.feature("organizations:incidents"):
        resp = self.get_valid_response(
            self.organization.slug, status_code=201, **rule_one_trigger_only_critical
        )
    assert "id" in resp.data
    alert_rule = AlertRule.objects.get(id=resp.data["id"])
    assert resp.data == serialize(alert_rule, self.user)

def test_no_critical_trigger(self):
    rule_one_trigger_only_warning = {
        "aggregate": "count()",
        "query": "",
        "timeWindow": "300",
        "projects": [self.project.slug],
        "name": "JustATestRule",
        "owner": self.user.id,
        "resolveThreshold": 100,
        "thresholdType": 1,
        "triggers": [
            {
                "label": "warning",
                "alertThreshold": 200,
                "actions": [
                    {"type": "email", "targetType": "team", "targetIdentifier": self.team.id}
                ],
            }
        ],
    }

    with self.feature("organizations:incidents"):
        resp = self.get_valid_response(
            self.organization.slug, status_code=400, **rule_one_trigger_only_warning
        )
        assert resp.data == {"nonFieldErrors": ['Trigger 1 must be labeled "critical"']}

def test_superuser_can_edit(self): self.user.is_superuser = True self.user.save()
edited_comment = "this comment has been edited"
with self.feature("organizations:incidents"): self.get_valid_response( self.organization.slug, self.incident.identifier, self.user2_activity.id, comment=edited_comment, status_code=200, ) activity = IncidentActivity.objects.get(id=self.user2_activity.id) assert activity.user != self.user assert activity.comment == edited_comment

def test_email_subject(self):
    event1 = self.store_event(
        data={
            "event_id": "a" * 32,
            "message": "Foo bar",
            "level": "info",
            "fingerprint": ["group-1"],
        },
        project_id=self.project.id,
    )
    event2 = self.store_event(
        data={
            "event_id": "b" * 32,
            "message": "Foo bar",
            "level": "error",
            "fingerprint": ["group-1"],
        },
        project_id=self.project.id,
    )

    group = event1.group

    group.level = 30

    assert event1.get_email_subject() == "BAR-1 - Foo bar"
    assert event2.get_email_subject() == "BAR-1 - Foo bar"

def create_issues(self):
    event_a = self.store_event(
        data={
            "event_id": "a" * 32,
            "message": "oh no",
            "timestamp": iso_format(event_time),
            "fingerprint": ["group-1"],
        },
        project_id=self.project.id,
    )
    add_group_to_inbox(event_a.group, GroupInboxReason.NEW)
    event_b = self.store_event(
        data={
            "event_id": "b" * 32,
            "message": "oh snap",
            "timestamp": iso_format(event_time),
            "fingerprint": ["group-2"],
        },
        project_id=self.project.id,
    )
    add_group_to_inbox(event_b.group, GroupInboxReason.NEW)

def create_issues(self):
    self.issue_1 = self.store_event(
        data={
            "event_id": "a" * 32,
            "message": "oh no",
            "timestamp": iso_format(event_time),
            "fingerprint": ["group-1"],
        },
        project_id=self.project_1.id,
    )
    self.issue_2 = self.store_event(
        data={
            "event_id": "b" * 32,
            "message": "oh snap",
            "timestamp": iso_format(event_time),
            "fingerprint": ["group-2"],
            "environment": "prod",
        },
        project_id=self.project_2.id,
    )

