updated = appconnect.AppStoreConnectConfig( type=config.type, id=uuid.uuid4().hex, name=config.name, appconnectIssuer=config.appconnectIssuer, appconnectKey=config.appconnectKey, appconnectPrivateKey="A NEW KEY", appName=config.appName, appId=config.appId, bundleId=config.bundleId, )
with pytest.raises(ValueError): updated.update_project_symbol_source(default_project, allow_multiple=False)

