def test_simple(self): file = self.create_file( name="baz.dSYM", size=42, headers={"Content-Type": "application/x-mach-binary"}, checksum="dc1e3f3e411979d336c3057cce64294f3420f93a", )
dif = self.create_dif_file( debug_id="dfb8e43a-f242-3d73-a453-aeb6a777ef75", code_id="DFB8E43AF2423D73A453AEB6A777EF75", object_name="baz.dSYM", cpu_name="x86_64", file=file, data={"features": ["debug"]}, )
result = serialize(dif) result.pop("id") result.pop("dateCreated")
assert result == { "uuid": "dfb8e43a-f242-3d73-a453-aeb6a777ef75", "debugId": "dfb8e43a-f242-3d73-a453-aeb6a777ef75", "codeId": "DFB8E43AF2423D73A453AEB6A777EF75", "cpuName": "x86_64", "objectName": "baz.dSYM", "symbolType": "macho", "size": 42, "sha1": "dc1e3f3e411979d336c3057cce64294f3420f93a", "headers": {"Content-Type": "application/x-mach-binary"}, "data": {"features": ["debug"]}, }

